INSERT INTO soterms (id, name, definition) VALUES ('000000', 'Sequence_Ontology', '');
INSERT INTO soterms (id, name, definition) VALUES ('000001', 'region', 'A sequence_feature with an extent greater than zero. A nucleotide region is composed of bases and a polypeptide region is composed of amino acids." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000002', 'sequence_secondary_structure', 'A folded sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000003', 'G_quartet', 'G-quartets are unusual nucleic acid structures consisting of a planar arrangement where each guanine is hydrogen bonded by hoogsteen pairing to another guanine in the quartet." [http://www.ncbi.nlm.nih.gov/pubmed/7919797?dopt=Abstract]');
INSERT INTO soterms (id, name, definition) VALUES ('000004', 'interior_coding_exon', 'G-quartets are unusual nucleic acid structures consisting of a planar arrangement where each guanine is hydrogen bonded by hoogsteen pairing to another guanine in the quartet." [http://www.ncbi.nlm.nih.gov/pubmed/7919797?dopt=Abstract]');
INSERT INTO soterms (id, name, definition) VALUES ('000005', 'satellite_DNA', 'The many tandem repeats (identical or related) of a short basic repeating unit; many have a base composition or other property different from the genome average that allows them to be separated from the bulk (main band) genomic DNA." [http://www.ebi.ac.uk/embl/Documentation/FT_definitions/feature_table.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000006', 'PCR_product', 'A region amplified by a PCR reaction." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000007', 'read_pair', 'One of a pair of sequencing reads in which the two members of the pair are related by originating at either end of a clone insert." [SO:ls]');
INSERT INTO soterms (id, name, definition) VALUES ('000008', 'gene_sensu_your_favorite_organism', 'One of a pair of sequencing reads in which the two members of the pair are related by originating at either end of a clone insert." [SO:ls]');
INSERT INTO soterms (id, name, definition) VALUES ('000009', 'gene_class', 'One of a pair of sequencing reads in which the two members of the pair are related by originating at either end of a clone insert." [SO:ls]');
INSERT INTO soterms (id, name, definition) VALUES ('000010', 'protein_coding', 'One of a pair of sequencing reads in which the two members of the pair are related by originating at either end of a clone insert." [SO:ls]');
INSERT INTO soterms (id, name, definition) VALUES ('000011', 'non_protein_coding', 'One of a pair of sequencing reads in which the two members of the pair are related by originating at either end of a clone insert." [SO:ls]');
INSERT INTO soterms (id, name, definition) VALUES ('000012', 'scRNA_primary_transcript', 'The primary transcript of any one of several small cytoplasmic RNA molecules present in the cytoplasm and sometimes nucleus of a Eukaryote." [http://www.ebi.ac.uk/embl/WebFeat/align/scRNA_s.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000013', 'scRNA', 'A small non coding RNA sequence, present in the cytoplasm." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000014', 'INR_motif', 'A sequence element characteristic of some RNA polymerase II promoters required for the correct positioning of the polymerase for the start of transcription. Overlaps the TSS. The mammalian consensus sequence is YYAN(T|A)YY; the Drosophila consensus sequence is TCA(G|T)t(T|C). In each the A is at position +1 with respect to the TSS. Functionally similar to the TATA box element." [PMID:12651739, PMID:16858867]');
INSERT INTO soterms (id, name, definition) VALUES ('000015', 'DPE_motif', 'A sequence element characteristic of some RNA polymerase II promoters; Positioned from +28 to +32 with respect to the TSS (+1). Experimental results suggest that the DPE acts in conjunction with the INR_motif to provide a binding site for TFIID in the absence of a TATA box to mediate transcription of TATA-less promoters. Consensus sequence (A|G)G(A|T)(C|T)(G|A|C)." [PMID:12651739\:12537576, PMID:16858867]');
INSERT INTO soterms (id, name, definition) VALUES ('000016', 'BREu_motif', 'A sequence element characteristic of some RNA polymerase II promoters, located immediately upstream of some TATA box elements at -37 to -32 with respect to the TSS (+1). Consensus sequence is (G|C)(G|C)(G|A)CGCC. Binds TFIIB." [PMID:12651739, PMID:16858867]');
INSERT INTO soterms (id, name, definition) VALUES ('000017', 'PSE_motif', 'A sequence element characteristic of the promoters of snRNA genes transcribed by RNA polymerase II or by RNA polymerase III. Located between -45 and -60 relative to the TSS. The human PSE_motif consensus sequence is TCACCNTNA(C|G)TNAAAAG(T|G)." [PMID:12651739]');
INSERT INTO soterms (id, name, definition) VALUES ('000018', 'linkage_group', 'A group of loci that can be grouped in a linear order representing the different degrees of linkage among the genes concerned." [ISBN:038752046]');
INSERT INTO soterms (id, name, definition) VALUES ('000020', 'RNA_internal_loop', 'A region of double stranded RNA where the bases do not conform to WC base pairing. The loop is closed on both sides by canonical base pairing. If the interruption to base pairing occurs on one strand only, it is known as a bulge." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000021', 'asymmetric_RNA_internal_loop', 'An internal RNA loop where one of the strands includes more bases than the corresponding region on the other strand." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000022', 'A_minor_RNA_motif', 'A region forming a motif, composed of adenines, where the minor groove edges are inserted into the minor groove of another helix." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000023', 'K_turn_RNA_motif', 'The kink turn (K-turn) is an RNA structural motif that creates a sharp (~120 degree) bend between two continuous helices." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000024', 'sarcin_like_RNA_motif', 'A loop in ribosomal RNA containing the sites of attack for ricin and sarcin." [http://www.ncbi.nlm.nih.gov/pubmed/7897662]');
INSERT INTO soterms (id, name, definition) VALUES ('000025', 'symmetric_RNA_internal_loop', 'An internal RNA loop where the extent of the loop on both stands is the same size." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000026', 'RNA_junction_loop', 'An internal RNA loop where the extent of the loop on both stands is the same size." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000027', 'RNA_hook_turn', 'An internal RNA loop where the extent of the loop on both stands is the same size." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000028', 'base_pair', 'An internal RNA loop where the extent of the loop on both stands is the same size." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000029', 'WC_base_pair', 'The canonical base pair, where two bases interact via WC edges, with glycosidic bonds oriented cis relative to the axis of orientation." [PMID:12177293]');
INSERT INTO soterms (id, name, definition) VALUES ('000030', 'sugar_edge_base_pair', 'A type of non-canonical base-pairing." [PMID:12177293]');
INSERT INTO soterms (id, name, definition) VALUES ('000031', 'aptamer', 'DNA or RNA molecules that have been selected from random pools based on their ability to bind other molecules." [http://aptamer.icmb.utexas.edu]');
INSERT INTO soterms (id, name, definition) VALUES ('000032', 'DNA_aptamer', 'DNA molecules that have been selected from random pools based on their ability to bind other molecules." [http:aptamer.icmb.utexas.edu]');
INSERT INTO soterms (id, name, definition) VALUES ('000033', 'RNA_aptamer', 'RNA molecules that have been selected from random pools based on their ability to bind other molecules." [http://aptamer.icmb.utexas.edu]');
INSERT INTO soterms (id, name, definition) VALUES ('000034', 'morpholino_oligo', 'Morpholino oligos are synthesized from four different Morpholino subunits, each of which contains one of the four genetic bases (A, C, G, T) linked to a 6-membered morpholine ring. Eighteen to 25 subunits of these four subunit types are joined in a specific order by non-ionic phosphorodiamidate intersubunit linkages to give a Morpholino." [http://www.gene-tools.com/]');
INSERT INTO soterms (id, name, definition) VALUES ('000035', 'riboswitch', 'A riboswitch is a part of an mRNA that can act as a direct sensor of small molecules to control their own expression. A riboswitch is a cis element in the 5 end of an mRNA, that acts as a direct sensor of metabolites." [PMID:2820954]');
INSERT INTO soterms (id, name, definition) VALUES ('000036', 'matrix_attachment_site', 'A DNA region that is required for the binding of chromatin to the nuclear matrix." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000037', 'locus_control_region', 'A DNA region that includes DNAse hypersensitive sites located 5 to a gene that confers the high-level, position-independent, and copy number-dependent expression to that gene." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000038', 'match_set', 'A collection of match parts." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000039', 'match_part', 'A part of a match, for example an hsp from blast is a match_part." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000040', 'genomic_clone', 'A clone of a DNA region of a genome." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000041', 'sequence_operation', 'An operation that can be applied to a sequence, that results in a change." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000042', 'pseudogene_attribute', 'An attribute of a pseudogene (SO:0000336)." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000043', 'processed_pseudogene', 'A pseudogene created via retrotranposition of the mRNA of a functional protein-coding parent gene followed by accumulation of deleterious mutations lacking introns and promoters, often including a polyA tail." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000044', 'pseudogene_by_unequal_crossing_over', 'A pseudogene caused by unequal crossing over at recombination." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000045', 'delete', 'To remove a subsection of sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000046', 'insert', 'To insert a subsection of sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000047', 'invert', 'To invert a subsection of sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000048', 'substitute', 'To substitute a subsection of sequence for another." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000049', 'translocate', 'To translocate a subsection of sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000050', 'gene_part', 'A part of a gene, that has no other route in the ontology back to region. This concept is necessary for logical inference as these parts must have the properties of region. It also allows us to associate all the parts of genes with a gene." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000051', 'probe', 'A DNA sequence used experimentally to detect the presence or absence of a complementary nucleic acid." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000052', 'assortment_derived_deficiency', 'A DNA sequence used experimentally to detect the presence or absence of a complementary nucleic acid." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000053', 'sequence_variant_affecting_regulatory_region', 'A sequence_variant_effect which changes the regulatory region of a gene." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000054', 'aneuploid', 'A kind of chromosome variation where the chromosome complement is not an exact multiple of the haploid number." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000055', 'hyperploid', 'A kind of chromosome variation where the chromosome complement is not an exact multiple of the haploid number as extra chromosomes are present." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000056', 'hypoploid', 'A kind of chromosome variation where the chromosome complement is not an exact multiple of the haploid number as some chromosomes are missing." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000057', 'operator', 'A regulatory element of an operon to which activators or repressors bind thereby effecting translation of genes in that operon." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000058', 'assortment_derived_aneuploid', 'A regulatory element of an operon to which activators or repressors bind thereby effecting translation of genes in that operon." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000059', 'nuclease_binding_site', 'A binding site that, of a nucleotide molecule, that interacts selectively and non-covalently with polypeptide residues of a nuclease." [SO:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('000060', 'compound_chromosome_arm', 'A binding site that, of a nucleotide molecule, that interacts selectively and non-covalently with polypeptide residues of a nuclease." [SO:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('000061', 'restriction_enzyme_binding_site', 'A binding site that, in the nucleotide molecule, interacts selectively and non-covalently with polypeptide residues of a restriction enzyme." [SO:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('000062', 'deficient_intrachromosomal_transposition', 'An intrachromosomal transposition whereby a translocation in which one of the four broken ends loses a segment before re-joining." [FB:reference_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('000063', 'deficient_interchromosomal_transposition', 'An interchromosomal transposition whereby a translocation in which one of the four broken ends loses a segment before re-joining." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000064', 'gene_by_transcript_attribute', 'An interchromosomal transposition whereby a translocation in which one of the four broken ends loses a segment before re-joining." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000065', 'free_chromosome_arm', 'A chromosome structure variation whereby an arm exists as an individual chromosome element." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000066', 'gene_by_polyadenylation_attribute', 'A chromosome structure variation whereby an arm exists as an individual chromosome element." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000067', 'gene_to_gene_feature', 'A chromosome structure variation whereby an arm exists as an individual chromosome element." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000068', 'overlapping', 'An attribute describing a gene that has a sequence that overlaps the sequence of another gene." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000069', 'inside_intron', 'An attribute to describe a gene when it is located within the intron of another gene." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000070', 'inside_intron_antiparallel', 'An attribute to describe a gene when it is located within the intron of another gene and on the opposite strand." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000071', 'inside_intron_parallel', 'An attribute to describe a gene when it is located within the intron of another gene and on the same strand." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000072', 'end_overlapping_gene', 'An attribute to describe a gene when it is located within the intron of another gene and on the same strand." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000073', 'five_prime_three_prime_overlap', 'An attribute to describe a gene when the five prime region overlaps with another genes 3 region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000074', 'five_prime_five_prime_overlap', 'An attribute to describe a gene when the five prime region overlaps with another genes five prime region." [SO:ke]');
