INSERT INTO soterms (id, name, definition) VALUES ('000088', 'mt_gene', 'A gene located in mitochondrial sequence." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000089', 'kinetoplast_gene', 'A gene located in kinetoplast sequence." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000090', 'plastid_gene', 'A gene from plastid sequence." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000091', 'apicoplast_gene', 'A gene from apicoplast sequence." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000092', 'ct_gene', 'A gene from chloroplast sequence." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000093', 'chromoplast_gene', 'A gene from chromoplast_sequence." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000094', 'cyanelle_gene', 'A gene from cyanelle sequence." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000095', 'leucoplast_gene', 'A plastid gene from leucoplast sequence." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000096', 'proplastid_gene', 'A gene from proplastid sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000097', 'nucleomorph_gene', 'A gene from nucleomorph sequence." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000098', 'plasmid_gene', 'A gene from plasmid sequence." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000099', 'proviral_gene', 'A gene from proviral sequence." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000100', 'endogenous_retroviral_gene', 'A proviral gene with origin endogenous retrovirus." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000101', 'transposable_element', 'A transposon or insertion sequence. An element that can insert in a variety of DNA sequences." [http://www.sci.sdsu.edu/~smaloy/Glossary/T.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000102', 'expressed_sequence_match', 'A match to an EST or cDNA sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000103', 'clone_insert_end', 'The end of the clone insert." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000104', 'polypeptide', 'A sequence of amino acids linked by peptide bonds which may lack appreciable tertiary structure and may not be liable to irreversible denaturation." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000105', 'chromosome_arm', 'A region of the chromosome between the centromere and the telomere. Human chromosomes have two arms, the p arm (short) and the q arm (long) which are separated from each other by the centromere." [http://www.medterms.com/script/main/art.asp?articlekey=5152]');
INSERT INTO soterms (id, name, definition) VALUES ('000106', 'non_capped_primary_transcript', 'A region of the chromosome between the centromere and the telomere. Human chromosomes have two arms, the p arm (short) and the q arm (long) which are separated from each other by the centromere." [http://www.medterms.com/script/main/art.asp?articlekey=5152]');
INSERT INTO soterms (id, name, definition) VALUES ('000107', 'sequencing_primer', 'A region of the chromosome between the centromere and the telomere. Human chromosomes have two arms, the p arm (short) and the q arm (long) which are separated from each other by the centromere." [http://www.medterms.com/script/main/art.asp?articlekey=5152]');
INSERT INTO soterms (id, name, definition) VALUES ('000108', 'mRNA_with_frameshift', 'An mRNA with a frameshift." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000109', 'sequence_variant_obs', 'A sequence_variant is a non exact copy of a sequence_feature or genome exhibiting one or more sequence_alteration." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000110', 'sequence_feature', 'Any extent of continuous biological sequence." [LAMHDI:mb, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000111', 'transposable_element_gene', 'A gene encoded within a transposable element. For example gag, int, env and pol are the transposable element genes of the TY element in yeast." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000112', 'primer', 'An oligo to which new deoxyribonucleotides can be added by DNA polymerase." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000113', 'proviral_region', 'A viral sequence which has integrated into a host genome." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000114', 'methylated_cytosine', 'A methylated deoxy-cytosine." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000115', 'transcript_feature', 'A methylated deoxy-cytosine." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000116', 'edited', 'An attribute describing a sequence that is modified by editing." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000117', 'transcript_with_readthrough_stop_codon', 'An attribute describing a sequence that is modified by editing." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000118', 'transcript_with_translational_frameshift', 'A transcript with a translational frameshift." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000119', 'regulated', 'An attribute to describe a sequence that is regulated." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000120', 'protein_coding_primary_transcript', 'A primary transcript that, at least in part, encodes one or more proteins." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000121', 'forward_primer', 'A single stranded oligo used for polymerase chain reaction." [http://mged.sourceforge.net/ontologies/MGEDontology.php]');
INSERT INTO soterms (id, name, definition) VALUES ('000122', 'RNA_sequence_secondary_structure', 'A folded RNA sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000123', 'transcriptionally_regulated', 'An attribute describing a gene that is regulated at transcription." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000124', 'transcriptionally_constitutive', 'Expressed in relatively constant amounts without regard to cellular environmental conditions such as the concentration of a particular substrate." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000125', 'transcriptionally_induced', 'An inducer molecule is required for transcription to occur." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000126', 'transcriptionally_repressed', 'A repressor molecule is required for transcription to stop." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000127', 'silenced_gene', 'A gene that is silenced." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000128', 'gene_silenced_by_DNA_modification', 'A gene that is silenced by DNA modification." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000129', 'gene_silenced_by_DNA_methylation', 'A gene that is silenced by DNA methylation." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000130', 'post_translationally_regulated', 'An attribute describing a gene that is regulated after it has been translated." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000131', 'translationally_regulated', 'An attribute describing a gene that is regulated as it is translated." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000132', 'reverse_primer', 'A single stranded oligo used for polymerase chain reaction." [http://mged.sourceforge.net/ontologies/MGEDontology.php]');
INSERT INTO soterms (id, name, definition) VALUES ('000133', 'epigenetically_modified', 'This attribute describes a gene where heritable changes other than those in the DNA sequence occur. These changes include: modification to the DNA (such as DNA methylation, the covalent modification of cytosine), and post-translational modification of histones." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000134', 'genomically_imprinted', 'Imprinted genes are epigenetically modified genes that are expressed monoallelically according to their parent of origin." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000135', 'maternally_imprinted', 'The maternal copy of the gene is modified, rendering it transcriptionally silent." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000136', 'paternally_imprinted', 'The paternal copy of the gene is modified, rendering it transcriptionally silent." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000137', 'allelically_excluded', 'Allelic exclusion is a process occurring in diploid organisms, where a gene is inactivated and not expressed in that cell." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000138', 'gene_rearranged_at_DNA_level', 'An epigenetically modified gene, rearranged at the DNA level." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000139', 'ribosome_entry_site', 'Region in mRNA where ribosome assembles." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000140', 'attenuator', 'A sequence segment located within the five prime end of an mRNA that causes premature termination of translation." [SO:as]');
INSERT INTO soterms (id, name, definition) VALUES ('000141', 'terminator', 'The sequence of DNA located either at the end of the transcript that causes RNA polymerase to terminate transcription." [http://www.ebi.ac.uk/embl/Documentation/FT_definitions/feature_table.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000142', 'DNA_sequence_secondary_structure', 'A folded DNA sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000143', 'assembly_component', 'A region of known length which may be used to manufacture a longer region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000144', 'primary_transcript_attribute', 'A region of known length which may be used to manufacture a longer region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000145', 'recoded_codon', 'A codon that has been redefined at translation. The redefinition may be as a result of translational bypass, translational frameshifting or stop codon readthrough." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000146', 'capped', 'An attribute describing when a sequence, usually an mRNA is capped by the addition of a modified guanine nucleotide at the 5 end." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000147', 'exon', 'A region of the transcript sequence within a gene which is not removed from the primary RNA transcript by RNA splicing." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000148', 'supercontig', 'One or more contigs that have been ordered and oriented using end-read information. Contains gaps that are filled with Ns." [SO:ls]');
INSERT INTO soterms (id, name, definition) VALUES ('000149', 'contig', 'A contiguous sequence derived from sequence assembly. Has no gaps, but may contain Ns from unavailable bases." [SO:ls]');
INSERT INTO soterms (id, name, definition) VALUES ('000150', 'read', 'A sequence obtained from a single sequencing experiment. Typically a read is produced when a base calling program interprets information from a chromatogram trace file produced from a sequencing machine." [SO:rd]');
INSERT INTO soterms (id, name, definition) VALUES ('000151', 'clone', 'A piece of DNA that has been inserted in a vector so that it can be propagated in a host bacterium or some other organism." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000152', 'YAC', 'Yeast Artificial Chromosome, a vector constructed from the telomeric, centromeric, and replication origin sequences needed for replication in yeast cells." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000153', 'BAC', 'Bacterial Artificial Chromosome, a cloning vector that can be propagated as mini-chromosomes in a bacterial host." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000154', 'PAC', 'The P1-derived artificial chromosome are DNA constructs that are derived from the DNA of P1 bacteriophage. They can carry large amounts (about 100-300 kilobases) of other sequences for a variety of bioengineering purposes. It is one type of vector used to clone DNA fragments (100- to 300-kb insert size; average, 150 kb) in Escherichia coli cells." [http://en.wikipedia.org/wiki/P1-derived_artificial_chromosome]');
INSERT INTO soterms (id, name, definition) VALUES ('000155', 'plasmid', 'A self replicating, using the hosts cellular machinery, often circular nucleic acid molecule that is distinct from a chromosome in the organism." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000156', 'cosmid', 'A cloning vector that is a hybrid of lambda phages and a plasmid that can be propagated as a plasmid or packaged as a phage,since they retain the lambda cos sites." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000157', 'phagemid', 'A plasmid which carries within its sequence a bacteriophage replication origin. When the host bacterium is infected with \"helper\" phage, a phagemid is replicated along with the phage DNA and packaged into phage capsids." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000158', 'fosmid', 'A cloning vector that utilizes the E. coli F factor." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000159', 'deletion', 'The point at which one or more contiguous nucleotides were excised." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000160', 'lambda_clone', 'A linear clone derived from lambda bacteriophage. The genes involved in the lysogenic pathway are removed from the from the viral DNA. Up to 25 kb of foreign DNA can then be inserted into the lambda genome." [ISBN:0-1767-2380-8]');
INSERT INTO soterms (id, name, definition) VALUES ('000161', 'methylated_adenine', 'A modified  base in which adenine has been methylated." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000162', 'splice_site', 'Consensus region of primary transcript bordering junction of splicing. A region that overlaps exactly 2 base and adjacent_to splice_junction." [SO:cjm, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000163', 'five_prime_cis_splice_site', 'Intronic 2 bp region bordering the exon, at the 5 edge of the intron. A splice_site that is downstream_adjacent_to exon and starts intron." [http://www.ucl.ac.uk/~ucbhjow/b241/glossary.html, SO:cjm, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000164', 'three_prime_cis_splice_site', 'Intronic 2 bp region bordering the exon, at the 3 edge of the intron. A splice_site that is upstream_adjacent_to exon and finishes intron." [http://www.ucl.ac.uk/~ucbhjow/b241/glossary.html, SO:cjm, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000165', 'enhancer', 'A cis-acting sequence that increases the utilization of (some) eukaryotic promoters, and can function in either orientation and in any location (upstream or downstream) relative to the promoter." [http://www.ebi.ac.uk/embl/Documentation/FT_definitions/feature_table.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000166', 'enhancer_bound_by_factor', 'An enhancer bound by a factor." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000167', 'promoter', 'A regulatory_region composed of the TSS(s) and binding sites for TF_complexes of the basal transcription machinery." [SO:regcreative]');
INSERT INTO soterms (id, name, definition) VALUES ('000168', 'restriction_enzyme_cut_site', 'A specific nucleotide sequence of DNA at or near which a particular restriction enzyme cuts the DNA." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000169', 'RNApol_I_promoter', 'A DNA sequence in eukaryotic DNA to which RNA polymerase I binds, to begin transcription." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000170', 'RNApol_II_promoter', 'A DNA sequence in eukaryotic DNA to which RNA polymerase II binds, to begin transcription." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000171', 'RNApol_III_promoter', 'A DNA sequence in eukaryotic DNA to which RNA polymerase III binds, to begin transcription." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000172', 'CAAT_signal', 'Part of a conserved sequence located about 75-bp upstream of the start point of eukaryotic transcription units which may be involved in RNA polymerase binding; consensus=GG(C|T)CAATCT." [http://www.ebi.ac.uk/embl/Documentation/FT_definitions/feature_table.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000173', 'GC_rich_promoter_region', 'A conserved GC-rich region located upstream of the start point of eukaryotic transcription units which may occur in multiple copies or in either orientation; consensus=GGGCGG." [http://www.ebi.ac.uk/embl/Documentation/FT_definitions/feature_table.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000174', 'TATA_box', 'A conserved AT-rich septamer found about 25-bp before the start point of many eukaryotic RNA polymerase II transcript units; may be involved in positioning the enzyme for correct initiation; consensus=TATA(A|T)A(A|T)." [http://www.ebi.ac.uk/embl/Documentation/FT_definitions/feature_table.html, PMID:16858867]');
INSERT INTO soterms (id, name, definition) VALUES ('000175', 'minus_10_signal', 'A conserved region about 10-bp upstream of the start point of bacterial transcription units which may be involved in binding RNA polymerase; consensus=TAtAaT. This region is associated with sigma factor 70." [http://www.ebi.ac.uk/embl/Documentation/FT_definitions/feature_table.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000176', 'minus_35_signal', 'A conserved hexamer about 35-bp upstream of the start point of bacterial transcription units; consensus=TTGACa or TGTTGACA. This region is associated with sigma factor 70." [http://www.ebi.ac.uk/embl/Documentation/FT_definitions/feature_table.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000177', 'cross_genome_match', 'A nucleotide match against a sequence from another organism." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000178', 'operon', 'A group of contiguous genes transcribed as a single (polycistronic) mRNA from a single regulatory region." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000179', 'clone_insert_start', 'The start of the clone insert." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000180', 'retrotransposon', 'A transposable element that is incorporated into a chromosome by a mechanism that requires reverse transcriptase." [http://www.dddmag.com/Glossary.aspx#r]');
INSERT INTO soterms (id, name, definition) VALUES ('000181', 'translated_nucleotide_match', 'A match against a translated sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000182', 'DNA_transposon', 'A transposon where the mechanism of transposition is via a DNA intermediate." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000183', 'non_transcribed_region', 'A region of the gene which is not transcribed." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000184', 'U2_intron', 'A major type of spliceosomal intron spliced by the U2 spliceosome, that includes U1, U2, U4/U6 and U5 snRNAs." [PMID:9428511]');
INSERT INTO soterms (id, name, definition) VALUES ('000185', 'primary_transcript', 'A transcript that in its initial state requires modification to be functional." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000186', 'LTR_retrotransposon', 'A retrotransposon flanked by long terminal repeat sequences." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000187', 'repeat_family', 'A group of characterized repeat sequences." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000188', 'intron', 'A region of a primary transcript that is transcribed, but removed from within the transcript by splicing together the sequences (exons) on either side of it." [http://www.ebi.ac.uk/embl/Documentation/FT_definitions/feature_table.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000189', 'non_LTR_retrotransposon', 'A retrotransposon without long terminal repeat sequences." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000190', 'five_prime_intron', 'A retrotransposon without long terminal repeat sequences." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000191', 'interior_intron', 'A retrotransposon without long terminal repeat sequences." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000192', 'three_prime_intron', 'A retrotransposon without long terminal repeat sequences." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000193', 'RFLP_fragment', 'A DNA fragment used as a reagent to detect the polymorphic genomic loci by hybridizing against the genomic DNA digested with a given restriction enzyme." [GOC:pj]');
INSERT INTO soterms (id, name, definition) VALUES ('000194', 'LINE_element', 'A dispersed repeat family with many copies, each from 1 to 6 kb long. New elements are generated by retroposition of a transcribed copy. Typically the LINE contains 2 ORFs one of which is reverse transcriptase, and 3and 5 direct repeats." [http://www.ucl.ac.uk/~ucbhjow/b241/glossary.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000195', 'coding_exon', 'An exon whereby at least one base is part of a codon (here, codon is inclusive of the stop_codon)." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000196', 'five_prime_coding_exon_coding_region', 'The sequence of the five_prime_coding_exon that codes for protein." [SO:cjm]');
INSERT INTO soterms (id, name, definition) VALUES ('000197', 'three_prime_coding_exon_coding_region', 'The sequence of the three_prime_coding_exon that codes for protein." [SO:cjm]');
INSERT INTO soterms (id, name, definition) VALUES ('000198', 'noncoding_exon', 'An exon that does not contain any codons." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000199', 'translocation', 'A region of nucleotide sequence that has translocated to a new position. The observed adjacency of two previously separated regions." [NCBI:th, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000200', 'five_prime_coding_exon', 'The 5 most coding exon." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000201', 'interior_exon', 'An exon that is bounded by 5 and 3 splice sites." [PMID:10373547]');
INSERT INTO soterms (id, name, definition) VALUES ('000202', 'three_prime_coding_exon', 'The coding exon that is most 3-prime on a given transcript." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000203', 'UTR', 'Messenger RNA sequences that are untranslated and lie five prime or three prime to sequences which are translated." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000204', 'five_prime_UTR', 'A region at the 5 end of a mature transcript (preceding the initiation codon) that is not translated into a protein." [http://www.ebi.ac.uk/embl/Documentation/FT_definitions/feature_table.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000205', 'three_prime_UTR', 'A region at the 3 end of a mature transcript (following the stop codon) that is not translated into a protein." [http://www.ebi.ac.uk/embl/Documentation/FT_definitions/feature_table.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000206', 'SINE_element', 'A repetitive element, a few hundred base pairs long, that is dispersed throughout the genome. A common human SINE is the Alu element." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000207', 'simple_sequence_length_variation', 'SSLP are a kind of sequence alteration where the number of repeated sequences in intergenic regions may differ." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000208', 'terminal_inverted_repeat_element', 'A DNA transposable element defined as having termini with perfect, or nearly perfect short inverted repeats, generally 10 - 40 nucleotides long." [http://www.genetics.org/cgi/reprint/156/4/1983.pdf]');
INSERT INTO soterms (id, name, definition) VALUES ('000209', 'rRNA_primary_transcript', 'A primary transcript encoding a ribosomal RNA." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000210', 'tRNA_primary_transcript', 'A primary transcript encoding a transfer RNA (SO:0000253)." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000211', 'alanine_tRNA_primary_transcript', 'A primary transcript encoding alanyl tRNA." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000212', 'arginine_tRNA_primary_transcript', 'A primary transcript encoding arginyl tRNA (SO:0000255)." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000213', 'asparagine_tRNA_primary_transcript', 'A primary transcript encoding asparaginyl tRNA (SO:0000256)." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000214', 'aspartic_acid_tRNA_primary_transcript', 'A primary transcript encoding aspartyl tRNA (SO:0000257)." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000215', 'cysteine_tRNA_primary_transcript', 'A primary transcript encoding cysteinyl tRNA (SO:0000258)." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000216', 'glutamic_acid_tRNA_primary_transcript', 'A primary transcript encoding glutaminyl tRNA (SO:0000260)." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000217', 'glutamine_tRNA_primary_transcript', 'A primary transcript encoding glutamyl tRNA (SO:0000260)." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000218', 'glycine_tRNA_primary_transcript', 'A primary transcript encoding glycyl tRNA (SO:0000263)." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000219', 'histidine_tRNA_primary_transcript', 'A primary transcript encoding histidyl tRNA (SO:0000262)." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000220', 'isoleucine_tRNA_primary_transcript', 'A primary transcript encoding isoleucyl tRNA (SO:0000263)." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000221', 'leucine_tRNA_primary_transcript', 'A primary transcript encoding leucyl tRNA (SO:0000264)." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000222', 'lysine_tRNA_primary_transcript', 'A primary transcript encoding lysyl tRNA (SO:0000265)." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000223', 'methionine_tRNA_primary_transcript', 'A primary transcript encoding methionyl tRNA (SO:0000266)." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000224', 'phenylalanine_tRNA_primary_transcript', 'A primary transcript encoding phenylalanyl tRNA (SO:0000267)." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000225', 'proline_tRNA_primary_transcript', 'A primary transcript encoding prolyl tRNA (SO:0000268)." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000226', 'serine_tRNA_primary_transcript', 'A primary transcript encoding seryl tRNA (SO:000269)." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000227', 'threonine_tRNA_primary_transcript', 'A primary transcript encoding threonyl tRNA (SO:000270)." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000228', 'tryptophan_tRNA_primary_transcript', 'A primary transcript encoding tryptophanyl tRNA (SO:000271)." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000229', 'tyrosine_tRNA_primary_transcript', 'A primary transcript encoding tyrosyl tRNA (SO:000272)." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000230', 'valine_tRNA_primary_transcript', 'A primary transcript encoding valyl tRNA (SO:000273)." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000231', 'snRNA_primary_transcript', 'A primary transcript encoding a small nuclear RNA (SO:0000274)." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000232', 'snoRNA_primary_transcript', 'A primary transcript encoding a small nucleolar mRNA (SO:0000275)." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000233', 'mature_transcript', 'A transcript which has undergone the necessary modifications, if any, for its function. In eukaryotes this includes, for example, processing of introns, cleavage, base modification, and modifications to the 5 and/or the 3 ends, other than addition of bases. In bacteria functional mRNAs are usually not modified." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000234', 'mRNA', 'Messenger RNA is the intermediate molecule between DNA and protein. It includes UTR and coding sequences. It does not contain introns." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000235', 'TF_binding_site', 'A region of a nucleotide molecule that binds a Transcription Factor or Transcription Factor complex [GO:0005667]." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000236', 'ORF', 'The in-frame interval between the stop codons of a reading frame which when read as sequential triplets, has the potential of encoding a sequential string of amino acids. TER(NNN)nTER." [SGD:rb, SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000237', 'transcript_attribute', 'The in-frame interval between the stop codons of a reading frame which when read as sequential triplets, has the potential of encoding a sequential string of amino acids. TER(NNN)nTER." [SGD:rb, SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000238', 'foldback_element', 'A transposable element with extensive secondary structure, characterized by large modular imperfect long inverted repeats." [http://www.genetics.org/cgi/reprint/156/4/1983.pdf]');
INSERT INTO soterms (id, name, definition) VALUES ('000239', 'flanking_region', 'The sequences extending on either side of a specific region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000240', 'chromosome_variation', 'The sequences extending on either side of a specific region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000241', 'internal_UTR', 'A UTR bordered by the terminal and initial codons of two CDSs in a polycistronic transcript. Every UTR is either 5, 3 or internal." [SO:cjm]');
INSERT INTO soterms (id, name, definition) VALUES ('000242', 'untranslated_region_polycistronic_mRNA', 'The untranslated sequence separating the cistrons of multicistronic mRNA." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000243', 'internal_ribosome_entry_site', 'Sequence element that recruits a ribosomal subunit to internal mRNA for translation initiation." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000244', 'four_cutter_restriction_site', 'Sequence element that recruits a ribosomal subunit to internal mRNA for translation initiation." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000245', 'mRNA_by_polyadenylation_status', 'Sequence element that recruits a ribosomal subunit to internal mRNA for translation initiation." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000246', 'polyadenylated', 'A attribute describing the addition of a poly A tail to the 3 end of a mRNA molecule." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000247', 'mRNA_not_polyadenylated', 'A attribute describing the addition of a poly A tail to the 3 end of a mRNA molecule." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000248', 'sequence_length_alteration', 'A kind of kind of sequence alteration where the copies of a region present varies across a population." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000249', 'six_cutter_restriction_site', 'A kind of kind of sequence alteration where the copies of a region present varies across a population." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000250', 'modified_RNA_base_feature', 'A post_transcriptionally modified base." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000251', 'eight_cutter_restriction_site', 'A post_transcriptionally modified base." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000252', 'rRNA', 'RNA that comprises part of a ribosome, and that can provide both structural scaffolding and catalytic activity." [http://www.ebi.ac.uk/embl/Documentation/FT_definitions/feature_table.html, ISBN:0198506732]');
INSERT INTO soterms (id, name, definition) VALUES ('000253', 'tRNA', 'Transfer RNA (tRNA) molecules are approximately 80 nucleotides in length. Their secondary structure includes four short double-helical elements and three loops (D, anti-codon, and T loops). Further hydrogen bonds mediate the characteristic L-shaped molecular structure. Transfer RNAs have two regions of fundamental functional importance: the anti-codon, which is responsible for specific mRNA codon recognition, and the 3 end, to which the tRNAs corresponding amino acid is attached (by aminoacyl-tRNA synthetases). Transfer RNAs cope with the degeneracy of the genetic code in two manners: having more than one tRNA (with a specific anti-codon) for a particular amino acid; and wobble base-pairing, i.e. permitting non-standard base-pairing at the 3rd anti-codon position." [http://www.sanger.ac.uk/cgi-bin/Rfam/getacc?RF00005, ISBN:0198506732]');
INSERT INTO soterms (id, name, definition) VALUES ('000254', 'alanyl_tRNA', 'A tRNA sequence that has an alanine anticodon, and a 3 alanine binding region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000255', 'rRNA_small_subunit_primary_transcript', 'A primary transcript encoding a small ribosomal subunit RNA." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000256', 'asparaginyl_tRNA', 'A tRNA sequence that has an asparagine anticodon, and a 3 asparagine binding region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000257', 'aspartyl_tRNA', 'A tRNA sequence that has an aspartic acid anticodon, and a 3 aspartic acid binding region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000258', 'cysteinyl_tRNA', 'A tRNA sequence that has a cysteine anticodon, and a 3 cysteine binding region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000259', 'glutaminyl_tRNA', 'A tRNA sequence that has a glutamine anticodon, and a 3 glutamine binding region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000260', 'glutamyl_tRNA', 'A tRNA sequence that has a glutamic acid anticodon, and a 3 glutamic acid binding region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000261', 'glycyl_tRNA', 'A tRNA sequence that has a glycine anticodon, and a 3 glycine binding region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000262', 'histidyl_tRNA', 'A tRNA sequence that has a histidine anticodon, and a 3 histidine binding region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000263', 'isoleucyl_tRNA', 'A tRNA sequence that has an isoleucine anticodon, and a 3 isoleucine binding region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000264', 'leucyl_tRNA', 'A tRNA sequence that has a leucine anticodon, and a 3 leucine binding region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000265', 'lysyl_tRNA', 'A tRNA sequence that has a lysine anticodon, and a 3 lysine binding region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000266', 'methionyl_tRNA', 'A tRNA sequence that has a methionine anticodon, and a 3 methionine binding region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000267', 'phenylalanyl_tRNA', 'A tRNA sequence that has a phenylalanine anticodon, and a 3 phenylalanine binding region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000268', 'prolyl_tRNA', 'A tRNA sequence that has a proline anticodon, and a 3 proline binding region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000269', 'seryl_tRNA', 'A tRNA sequence that has a serine anticodon, and a 3 serine binding region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000270', 'threonyl_tRNA', 'A tRNA sequence that has a threonine anticodon, and a 3 threonine binding region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000271', 'tryptophanyl_tRNA', 'A tRNA sequence that has a tryptophan anticodon, and a 3 tryptophan binding region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000272', 'tyrosyl_tRNA', 'A tRNA sequence that has a tyrosine anticodon, and a 3 tyrosine binding region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000273', 'valyl_tRNA', 'A tRNA sequence that has a valine anticodon, and a 3 valine binding region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000274', 'snRNA', 'A small nuclear RNA molecule involved in pre-mRNA splicing and processing." [http://www.ebi.ac.uk/embl/Documentation/FT_definitions/feature_table.html, PMID:11733745, WB:ems]');
INSERT INTO soterms (id, name, definition) VALUES ('000275', 'snoRNA', 'A snoRNA (small nucleolar RNA) is any one of a class of small RNAs that are associated with the eukaryotic nucleus as components of small nucleolar ribonucleoproteins. They participate in the processing or modifications of many RNAs, mostly ribosomal RNAs (rRNAs) though snoRNAs are also known to target other classes of RNA, including spliceosomal RNAs, tRNAs, and mRNAs via a stretch of sequence that is complementary to a sequence in the targeted RNA." [GOC:kgc]');
INSERT INTO soterms (id, name, definition) VALUES ('000276', 'miRNA', 'Small, ~22-nt, RNA molecule that is the endogenous transcript of a miRNA gene (or the product of other non coding RNA genes. Micro RNAs are produced from precursor molecules (SO:0000647) that can form local hairpin structures, which ordinarily are processed (usually via the Dicer pathway) such that a single miRNA molecule accumulates from one arm of a hairpin precursor molecule. Micro RNAs may trigger the cleavage of their target molecules or act as translational repressors." [PMID:11081512, PMID:12592000]');
INSERT INTO soterms (id, name, definition) VALUES ('000277', 'bound_by_factor', 'An attribute describing a sequence that is bound by another molecule." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000278', 'transcript_bound_by_nucleic_acid', 'A transcript that is bound by a nucleic acid." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000279', 'transcript_bound_by_protein', 'A transcript that is bound by a protein." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000280', 'engineered_gene', 'A gene that is engineered." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000281', 'engineered_foreign_gene', 'A gene that is engineered and foreign." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000282', 'mRNA_with_minus_1_frameshift', 'An mRNA with a minus 1 frameshift." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000283', 'engineered_foreign_transposable_element_gene', 'A transposable_element that is engineered and foreign." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000284', 'type_I_enzyme_restriction_site', 'The recognition site is bipartite and interrupted." [http://www.promega.com]');
INSERT INTO soterms (id, name, definition) VALUES ('000285', 'foreign_gene', 'A gene that is foreign." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000286', 'long_terminal_repeat', 'A sequence directly repeated at both ends of a defined sequence, of the sort typically found in retroviruses." [http://www.ebi.ac.uk/embl/Documentation/FT_definitions/feature_table.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000287', 'fusion_gene', 'A gene that is a fusion." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000288', 'engineered_fusion_gene', 'A fusion gene that is engineered." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000289', 'microsatellite', 'A repeat_region containing repeat_units of 2 to 10 bp repeated in tandem." [http://www.informatics.jax.org/silver/glossary.shtml, NCBI:th]');
INSERT INTO soterms (id, name, definition) VALUES ('000290', 'dinucleotide_repeat_microsatellite_feature', 'A repeat_region containing repeat_units of 2 to 10 bp repeated in tandem." [http://www.informatics.jax.org/silver/glossary.shtml, NCBI:th]');
INSERT INTO soterms (id, name, definition) VALUES ('000291', 'trinucleotide_repeat_microsatellite_feature', 'A repeat_region containing repeat_units of 2 to 10 bp repeated in tandem." [http://www.informatics.jax.org/silver/glossary.shtml, NCBI:th]');
INSERT INTO soterms (id, name, definition) VALUES ('000292', 'repetitive_element', 'A repeat_region containing repeat_units of 2 to 10 bp repeated in tandem." [http://www.informatics.jax.org/silver/glossary.shtml, NCBI:th]');
INSERT INTO soterms (id, name, definition) VALUES ('000293', 'engineered_foreign_repetitive_element', 'A repetitive element that is engineered and foreign." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000294', 'inverted_repeat', 'The sequence is complementarily repeated on the opposite strand. It is a palindrome, and it may, or may not be hyphenated. Examples: GCTGATCAGC, or GCTGA-----TCAGC." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000295', 'U12_intron', 'A type of spliceosomal intron spliced by the U12 spliceosome, that includes U11, U12, U4atac/U6atac and U5 snRNAs." [PMID:9428511]');
INSERT INTO soterms (id, name, definition) VALUES ('000296', 'origin_of_replication', 'The origin of replication; starting site for duplication of a nucleic acid molecule to give two identical copies." [http://www.ebi.ac.uk/embl/Documentation/FT_definitions/feature_table.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000297', 'D_loop', 'Displacement loop; a region within mitochondrial DNA in which a short stretch of RNA is paired with one strand of DNA, displacing the original partner DNA strand in this region; also used to describe the displacement of a region of one strand of duplex DNA by a single stranded invader in the reaction catalyzed by RecA protein." [http://www.ebi.ac.uk/embl/Documentation/FT_definitions/feature_table.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000298', 'recombination_feature', 'Displacement loop; a region within mitochondrial DNA in which a short stretch of RNA is paired with one strand of DNA, displacing the original partner DNA strand in this region; also used to describe the displacement of a region of one strand of duplex DNA by a single stranded invader in the reaction catalyzed by RecA protein." [http://www.ebi.ac.uk/embl/Documentation/FT_definitions/feature_table.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000299', 'specific_recombination_site', 'Displacement loop; a region within mitochondrial DNA in which a short stretch of RNA is paired with one strand of DNA, displacing the original partner DNA strand in this region; also used to describe the displacement of a region of one strand of duplex DNA by a single stranded invader in the reaction catalyzed by RecA protein." [http://www.ebi.ac.uk/embl/Documentation/FT_definitions/feature_table.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000300', 'recombination_feature_of_rearranged_gene', 'Displacement loop; a region within mitochondrial DNA in which a short stretch of RNA is paired with one strand of DNA, displacing the original partner DNA strand in this region; also used to describe the displacement of a region of one strand of duplex DNA by a single stranded invader in the reaction catalyzed by RecA protein." [http://www.ebi.ac.uk/embl/Documentation/FT_definitions/feature_table.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000301', 'vertebrate_immune_system_gene_recombination_feature', 'Displacement loop; a region within mitochondrial DNA in which a short stretch of RNA is paired with one strand of DNA, displacing the original partner DNA strand in this region; also used to describe the displacement of a region of one strand of duplex DNA by a single stranded invader in the reaction catalyzed by RecA protein." [http://www.ebi.ac.uk/embl/Documentation/FT_definitions/feature_table.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000302', 'J_gene_recombination_feature', 'Recombination signal including J-heptamer, J-spacer and J-nonamer in 5 of J-region of a J-gene or J-sequence." [http://www.imgt.org/cgi-bin/IMGTlect.jv?query=7#]');
INSERT INTO soterms (id, name, definition) VALUES ('000303', 'clip', 'Part of the primary transcript that is clipped off during processing." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000304', 'type_II_enzyme_restriction_site', 'The recognition site is either palindromic, partially palindromic or an interrupted palindrome. Cleavage occurs within the recognition site." [http://www.promega.com]');
INSERT INTO soterms (id, name, definition) VALUES ('000305', 'modified_DNA_base', 'A modified nucleotide, i.e. a nucleotide other than A, T, C. G." [http://www.ebi.ac.uk/embl/Documentation/FT_definitions/feature_table.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000306', 'methylated_DNA_base_feature', 'A nucleotide modified by methylation." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000307', 'CpG_island', 'Regions of a few hundred to a few thousand bases in vertebrate genomes that are relatively GC and CpG rich; they are typically unmethylated and often found near the 5 ends of genes." [SO:rd]');
INSERT INTO soterms (id, name, definition) VALUES ('000308', 'sequence_feature_locating_method', 'Regions of a few hundred to a few thousand bases in vertebrate genomes that are relatively GC and CpG rich; they are typically unmethylated and often found near the 5 ends of genes." [SO:rd]');
INSERT INTO soterms (id, name, definition) VALUES ('000309', 'computed_feature', 'Regions of a few hundred to a few thousand bases in vertebrate genomes that are relatively GC and CpG rich; they are typically unmethylated and often found near the 5 ends of genes." [SO:rd]');
INSERT INTO soterms (id, name, definition) VALUES ('000310', 'predicted_ab_initio_computation', 'Regions of a few hundred to a few thousand bases in vertebrate genomes that are relatively GC and CpG rich; they are typically unmethylated and often found near the 5 ends of genes." [SO:rd]');
INSERT INTO soterms (id, name, definition) VALUES ('000311', 'computed_feature_by_similarity', '." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000312', 'experimentally_determined', 'Attribute to describe a feature that has been experimentally verified." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000313', 'stem_loop', 'A double-helical region of nucleic acid formed by base-pairing between adjacent (inverted) complementary sequences." [http://www.ebi.ac.uk/embl/Documentation/FT_definitions/feature_table.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000314', 'direct_repeat', 'A repeat where the same sequence is repeated in the same direction. Example: GCTGA-followed by-GCTGA." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000315', 'TSS', 'The first base where RNA polymerase begins to synthesize the RNA transcript." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000316', 'CDS', 'A contiguous sequence which begins with, and includes, a start codon and ends with, and includes, a stop codon." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000317', 'cDNA_clone', 'Complementary DNA; A piece of DNA copied from an mRNA and spliced into a vector for propagation in a suitable host." [http://seqcore.brcf.med.umich.edu/doc/educ/dnapr/mbglossary/mbgloss.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000318', 'start_codon', 'First codon to be translated by a ribosome." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000319', 'stop_codon', 'In mRNA, a set of three nucleotides that indicates the end of information for protein synthesis." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000320', 'intronic_splice_enhancer', 'Sequences within the intron that modulate splice site selection for some introns." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000321', 'mRNA_with_plus_1_frameshift', 'An mRNA with a plus 1 frameshift." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000322', 'nuclease_hypersensitive_site', 'An mRNA with a plus 1 frameshift." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000323', 'coding_start', 'The first base to be translated into protein." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000324', 'tag', 'A nucleotide sequence that may be used to identify a larger sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000325', 'rRNA_large_subunit_primary_transcript', 'A primary transcript encoding a large ribosomal subunit RNA." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000326', 'SAGE_tag', 'A short diagnostic sequence tag, serial analysis of gene expression (SAGE), that allows the quantitative and simultaneous analysis of a large number of transcripts." [http://www.ncbi.nlm.nih.gov/entrez/query.fcgi?cmd=Retrieve&db=PubMed&list_uids=7570003&dopt=Abstract]');
INSERT INTO soterms (id, name, definition) VALUES ('000327', 'coding_end', 'The last base to be translated into protein. It does not include the stop codon." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000328', 'microarray_oligo', 'The last base to be translated into protein. It does not include the stop codon." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000329', 'mRNA_with_plus_2_frameshift', 'An mRNA with a plus 2 frameshift." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000330', 'conserved_region', 'Region of sequence similarity by descent from a common ancestor." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000331', 'STS', 'Short (typically a few hundred base pairs) DNA sequence that has a single occurrence in a genome and whose location and base sequence are known." [http://www.biospace.com]');
INSERT INTO soterms (id, name, definition) VALUES ('000332', 'coding_conserved_region', 'Coding region of sequence similarity by descent from a common ancestor." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000333', 'exon_junction', 'The boundary between two exons in a processed transcript." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000334', 'nc_conserved_region', 'Non-coding region of sequence similarity by descent from a common ancestor." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000335', 'mRNA_with_minus_2_frameshift', 'A mRNA with a minus 2 frameshift." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000336', 'pseudogene', 'A sequence that closely resembles a known functional gene, at another locus within a genome, that is non-functional as a consequence of (usually several) mutations that prevent either its transcription or translation (or both). In general, pseudogenes result from either reverse transcription of a transcript of their \"normal\" paralog (SO:0000043) (in which case the pseudogene typically lacks introns and includes a poly(A) tail) or from recombination (SO:0000044) (in which case the pseudogene is typically a tandem duplication of its \"normal\" paralog)." [http://www.ucl.ac.uk/~ucbhjow/b241/glossary.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000337', 'RNAi_reagent', 'A double stranded RNA duplex, at least 20bp long, used experimentally to inhibit gene function by RNA interference." [SO:rd]');
INSERT INTO soterms (id, name, definition) VALUES ('000338', 'MITE', 'A highly repetitive and short (100-500 base pair) transposable element with terminal inverted repeats (TIR) and target site duplication (TSD). MITEs do not encode proteins." [http://www.pnas.org/cgi/content/full/97/18/10083]');
INSERT INTO soterms (id, name, definition) VALUES ('000339', 'recombination_hotspot', 'A region in a genome which promotes recombination." [SO:rd]');
INSERT INTO soterms (id, name, definition) VALUES ('000340', 'chromosome', 'Structural unit composed of a nucleic acid molecule which controls its own replication through the interaction of specific proteins at one or more origins of replication." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000341', 'chromosome_band', 'A cytologically distinguishable feature of a chromosome, often made visible by staining, and usually alternating light and dark." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000342', 'site_specific_recombination_target_region', 'A cytologically distinguishable feature of a chromosome, often made visible by staining, and usually alternating light and dark." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000343', 'match', 'A region of sequence, aligned to another sequence with some statistical significance, using an algorithm such as BLAST or SIM4." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000344', 'splice_enhancer', 'Region of a transcript that regulates splicing." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000345', 'EST', 'A tag produced from a single sequencing read from a cDNA clone or PCR product; typically a few hundred base pairs long." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000346', 'loxP_site', 'A tag produced from a single sequencing read from a cDNA clone or PCR product; typically a few hundred base pairs long." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000347', 'nucleotide_match', 'A match against a nucleotide sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000348', 'nucleic_acid', 'An attribute describing a sequence consisting of nucleobases bound to repeating units. The forms found in nature are deoxyribonucleic acid (DNA), where the repeating units are 2-deoxy-D-ribose rings connected to a phosphate backbone, and ribonucleic acid (RNA), where the repeating units are D-ribose rings connected to a phosphate backbone." [CHEBI:33696, RSC:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('000349', 'protein_match', 'A match against a protein sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000350', 'FRT_site', 'An inversion site found on the Saccharomyces cerevisiae 2 micron plasmid." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000351', 'synthetic_sequence', 'An attribute to decide a sequence of nucleotides, nucleotide analogs, or amino acids that has been designed by an experimenter and which may, or may not, correspond with any natural sequence." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000352', 'DNA', 'An attribute describing a sequence consisting of nucleobases bound to a repeating unit made of a 2-deoxy-D-ribose ring connected to a phosphate backbone." [RSC:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('000353', 'sequence_assembly', 'A sequence of nucleotides that has been algorithmically derived from an alignment of two or more different sequences." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000354', 'group_1_intron_homing_endonuclease_target_region', 'A region of intronic nucleotide sequence targeted by a nuclease enzyme." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000355', 'haplotype_block', 'A region of the genome which is co-inherited as the result of the lack of historic recombination within it." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000356', 'RNA', 'An attribute describing a sequence consisting of nucleobases bound to a repeating unit made of a D-ribose ring connected to a phosphate backbone." [RSC:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('000357', 'flanked', 'An attribute describing a region that is bounded either side by a particular kind of region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000359', 'floxed', 'An attribute describing sequence that is flanked by Lox-P sites." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000360', 'codon', 'A set of (usually) three nucleotide bases in a DNA or RNA sequence, which together code for a unique amino acid or the termination of translation and are contained within the CDS." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000361', 'FRT_flanked', 'An attribute to describe sequence that is flanked by the FLP recombinase recognition site, FRT." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000362', 'invalidated_by_chimeric_cDNA', 'A cDNA clone constructed from more than one mRNA. Usually an experimental artifact." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000363', 'floxed_gene', 'A transgene that is floxed." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000364', 'transposable_element_flanking_region', 'The region of sequence surrounding a transposable element." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000365', 'integron', 'A region encoding an integrase which acts at a site adjacent to it (attI_site) to insert DNA which must include but is not limited to an attC_site." [SO:as]');
INSERT INTO soterms (id, name, definition) VALUES ('000366', 'insertion_site', 'The junction where an insertion occurred." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000367', 'attI_site', 'A region within an integron, adjacent to an integrase, at which site specific recombination involving an attC_site takes place." [SO:as]');
INSERT INTO soterms (id, name, definition) VALUES ('000368', 'transposable_element_insertion_site', 'The junction in a genome where a transposable_element has inserted." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000369', 'integrase_coding_region', 'The junction in a genome where a transposable_element has inserted." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000370', 'small_regulatory_ncRNA', 'A non-coding RNA, usually with a specific secondary structure, that acts to regulate gene expression." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000371', 'conjugative_transposon', 'A transposon that encodes function required for conjugation." [http://www.sci.sdsu.edu/~smaloy/Glossary/C.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000372', 'enzymatic_RNA', 'An RNA sequence that has catalytic activity with or without an associated ribonucleoprotein." [RSC:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('000373', 'recombinationally_inverted_gene', 'A recombinationally rearranged gene by inversion." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000374', 'ribozyme', 'An RNA with catalytic activity." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000375', 'rRNA_5_8S', '5_8S ribosomal RNA (5. 8S rRNA) is a component of the large subunit of the eukaryotic ribosome. It is transcribed by RNA polymerase I as part of the 45S precursor that also contains 18S and 28S rRNA. Functionally, it is thought that 5.8S rRNA may be involved in ribosome translocation. It is also known to form covalent linkage to the p53 tumour suppressor protein. 5_8S rRNA is also found in archaea." [http://www.sanger.ac.uk/cgi-bin/Rfam/getacc?RF00002]');
INSERT INTO soterms (id, name, definition) VALUES ('000376', 'RNA_6S', 'A small (184-nt in E. coli) RNA that forms a hairpin type structure. 6S RNA associates with RNA polymerase in a highly specific manner. 6S RNA represses expression from a sigma70-dependent promoter during stationary phase." [http://www.sanger.ac.uk/cgi-bin/Rfam/getacc?RF00013]');
INSERT INTO soterms (id, name, definition) VALUES ('000377', 'CsrB_RsmB_RNA', 'An enterobacterial RNA that binds the CsrA protein. The CsrB RNAs contain a conserved motif CAGGXXG that is found in up to 18 copies and has been suggested to bind CsrA. The Csr regulatory system has a strong negative regulatory effect on glycogen biosynthesis, glyconeogenesis and glycogen catabolism and a positive regulatory effect on glycolysis. In other bacteria such as Erwinia caratovara the RsmA protein has been shown to regulate the production of virulence determinants, such extracellular enzymes. RsmA binds to RsmB regulatory RNA which is also a member of this family." [http://www.sanger.ac.uk/cgi-bin/Rfam/getacc?RF00018]');
INSERT INTO soterms (id, name, definition) VALUES ('000378', 'DsrA_RNA', 'DsrA RNA regulates both transcription, by overcoming transcriptional silencing by the nucleoid-associated H-NS protein, and translation, by promoting efficient translation of the stress sigma factor, RpoS. These two activities of DsrA can be separated by mutation: the first of three stem-loops of the 85 nucleotide RNA is necessary for RpoS translation but not for anti-H-NS action, while the second stem-loop is essential for antisilencing and less critical for RpoS translation. The third stem-loop, which behaves as a transcription terminator, can be substituted by the trp transcription terminator without loss of either DsrA function. The sequence of the first stem-loop of DsrA is complementary with the upstream leader portion of RpoS messenger RNA, suggesting that pairing of DsrA with the RpoS message might be important for translational regulation." [http://www.sanger.ac.uk/cgi-bin/Rfam/getacc?RF00014]');
INSERT INTO soterms (id, name, definition) VALUES ('000379', 'GcvB_RNA', 'A small untranslated RNA involved in expression of the dipeptide and oligopeptide transport systems in Escherichia coli." [http://www.sanger.ac.uk/cgi-bin/Rfam/getacc?RF00022]');
INSERT INTO soterms (id, name, definition) VALUES ('000380', 'hammerhead_ribozyme', 'A small catalytic RNA motif that catalyzes self-cleavage reaction. Its name comes from its secondary structure which resembles a carpenters hammer. The hammerhead ribozyme is involved in the replication of some viroid and some satellite RNAs." [PMID:2436805]');
INSERT INTO soterms (id, name, definition) VALUES ('000381', 'group_IIA_intron', 'A small catalytic RNA motif that catalyzes self-cleavage reaction. Its name comes from its secondary structure which resembles a carpenters hammer. The hammerhead ribozyme is involved in the replication of some viroid and some satellite RNAs." [PMID:2436805]');
INSERT INTO soterms (id, name, definition) VALUES ('000382', 'group_IIB_intron', 'A small catalytic RNA motif that catalyzes self-cleavage reaction. Its name comes from its secondary structure which resembles a carpenters hammer. The hammerhead ribozyme is involved in the replication of some viroid and some satellite RNAs." [PMID:2436805]');
INSERT INTO soterms (id, name, definition) VALUES ('000383', 'MicF_RNA', 'A non-translated 93 nt antisense RNA that binds its target ompF mRNA and regulates ompF expression by inhibiting translation and inducing degradation of the message." [http://www.sanger.ac.uk/cgi-bin/Rfam/getacc?RF00033]');
INSERT INTO soterms (id, name, definition) VALUES ('000384', 'OxyS_RNA', 'A small untranslated RNA which is induced in response to oxidative stress in Escherichia coli. Acts as a global regulator to activate or repress the expression of as many as 40 genes, including the fhlA-encoded transcriptional activator and the rpoS-encoded sigma(s) subunit of RNA polymerase. OxyS is bound by the Hfq protein, that increases the OxyS RNA interaction with its target messages." [http://www.sanger.ac.uk/cgi-bin/Rfam/getacc?RF00035]');
INSERT INTO soterms (id, name, definition) VALUES ('000385', 'RNase_MRP_RNA', 'The RNA molecule essential for the catalytic activity of RNase MRP, an enzymatically active ribonucleoprotein with two distinct roles in eukaryotes. In mitochondria it plays a direct role in the initiation of mitochondrial DNA replication. In the nucleus it is involved in precursor rRNA processing, where it cleaves the internal transcribed spacer 1 between 18S and 5.8S rRNAs." [http://www.sanger.ac.uk/cgi-bin/Rfam/getacc?RF00030]');
INSERT INTO soterms (id, name, definition) VALUES ('000386', 'RNase_P_RNA', 'The RNA component of Ribonuclease P (RNase P), a ubiquitous endoribonuclease, found in archaea, bacteria and eukarya as well as chloroplasts and mitochondria. Its best characterized activity is the generation of mature 5 prime ends of tRNAs by cleaving the 5 prime leader elements of precursor-tRNAs. Cellular RNase Ps are ribonucleoproteins. RNA from bacterial RNase Ps retains its catalytic activity in the absence of the protein subunit, i.e. it is a ribozyme. Isolated eukaryotic and archaeal RNase P RNA has not been shown to retain its catalytic function, but is still essential for the catalytic activity of the holoenzyme. Although the archaeal and eukaryotic holoenzymes have a much greater protein content than the bacterial ones, the RNA cores from all the three lineages are homologous. Helices corresponding to P1, P2, P3, P4, and P10/11 are common to all cellular RNase P RNAs. Yet, there is considerable sequence variation, particularly among the eukaryotic RNAs." [http://www.sanger.ac.uk/cgi-bin/Rfam/getacc?RF00010]');
INSERT INTO soterms (id, name, definition) VALUES ('000387', 'RprA_RNA', 'Translational regulation of the stationary phase sigma factor RpoS is mediated by the formation of a double-stranded RNA stem-loop structure in the upstream region of the rpoS messenger RNA, occluding the translation initiation site. Clones carrying rprA (RpoS regulator RNA) increased the translation of RpoS. The rprA gene encodes a 106 nucleotide regulatory RNA. As with DsrA Rfam:RF00014, RprA is predicted to form three stem-loops. Thus, at least two small RNAs, DsrA and RprA, participate in the positive regulation of RpoS translation. Unlike DsrA, RprA does not have an extensive region of complementarity to the RpoS leader, leaving its mechanism of action unclear. RprA is non-essential." [http://www.sanger.ac.uk/cgi-bin/Rfam/getacc?RF00034]');
INSERT INTO soterms (id, name, definition) VALUES ('000388', 'RRE_RNA', 'The Rev response element (RRE) is encoded within the HIV-env gene. Rev is an essential regulatory protein of HIV that binds an internal loop of the RRE leading, encouraging further Rev-RRE binding. This RNP complex is critical for mRNA export and hence for expression of the HIV structural proteins." [http://www.sanger.ac.uk/cgi-bin/Rfam/getacc?RF00036]');
INSERT INTO soterms (id, name, definition) VALUES ('000389', 'spot_42_RNA', 'A 109-nucleotide RNA of E. coli that seems to have a regulatory role on the galactose operon. Changes in Spot 42 levels are implicated in affecting DNA polymerase I levels." [http://www.sanger.ac.uk/cgi-bin/Rfam/getacc?RF00021]');
INSERT INTO soterms (id, name, definition) VALUES ('000390', 'telomerase_RNA', 'The RNA component of telomerase, a reverse transcriptase that synthesizes telomeric DNA." [http://www.sanger.ac.uk/cgi-bin/Rfam/getacc?RF00025]');
INSERT INTO soterms (id, name, definition) VALUES ('000391', 'U1_snRNA', 'U1 is a small nuclear RNA (snRNA) component of the spliceosome (involved in pre-mRNA splicing). Its 5 end forms complementary base pairs with the 5 splice junction, thus defining the 5 donor site of an intron. There are significant differences in sequence and secondary structure between metazoan and yeast U1 snRNAs, the latter being much longer (568 nucleotides as compared to 164 nucleotides in human). Nevertheless, secondary structure predictions suggest that all U1 snRNAs share a common core consisting of helices I, II, the proximal region of III, and IV." [http://www.sanger.ac.uk/cgi-bin/Rfam/getacc?RF00003]');
INSERT INTO soterms (id, name, definition) VALUES ('000392', 'U2_snRNA', 'U2 is a small nuclear RNA (snRNA) component of the spliceosome (involved in pre-mRNA splicing). Complementary binding between U2 snRNA (in an area lying towards the 5 end but 3 to hairpin I) and the branchpoint sequence (BPS) of the intron results in the bulging out of an unpaired adenine, on the BPS, which initiates a nucleophilic attack at the intronic 5 splice site, thus starting the first of two transesterification reactions that mediate splicing." [http://www.sanger.ac.uk/cgi-bin/Rfam/getacc?RF00004]');
INSERT INTO soterms (id, name, definition) VALUES ('000393', 'U4_snRNA', 'U4 small nuclear RNA (U4 snRNA) is a component of the major U2-dependent spliceosome. It forms a duplex with U6, and with each splicing round, it is displaced from U6 (and the spliceosome) in an ATP-dependent manner, allowing U6 to refold and create the active site for splicing catalysis. A recycling process involving protein Prp24 re-anneals U4 and U6." [http://www.sanger.ac.uk/cgi-bin/Rfam/getacc?RF00015]');
INSERT INTO soterms (id, name, definition) VALUES ('000394', 'U4atac_snRNA', 'An snRNA required for the splicing of the minor U12-dependent class of eukaryotic nuclear introns. It forms a base paired complex with U6atac_snRNA (SO:0000397)." [PMID:=12409455]');
INSERT INTO soterms (id, name, definition) VALUES ('000395', 'U5_snRNA', 'U5 RNA is a component of both types of known spliceosome. The precise function of this molecule is unknown, though it is known that the 5 loop is required for splice site selection and p220 binding, and that both the 3 stem-loop and the Sm site are important for Sm protein binding and cap methylation." [http://www.sanger.ac.uk/cgi-bin/Rfam/getacc?RF00020]');
INSERT INTO soterms (id, name, definition) VALUES ('000396', 'U6_snRNA', 'U6 snRNA is a component of the spliceosome which is involved in splicing pre-mRNA. The putative secondary structure consensus base pairing is confined to a short 5 stem loop, but U6 snRNA is thought to form extensive base-pair interactions with U4 snRNA." [http://www.sanger.ac.uk/cgi-bin/Rfam/getacc?RF00015]');
INSERT INTO soterms (id, name, definition) VALUES ('000397', 'U6atac_snRNA', 'U6atac_snRNA is an snRNA required for the splicing of the minor U12-dependent class of eukaryotic nuclear introns. It forms a base paired complex with U4atac_snRNA (SO:0000394)." [http://www.ncbi.nlm.nih.gov/entrez/query.fcgi?cmd=retrieve&db=pubmed&list_uids=12409455&dopt=Abstract]');
INSERT INTO soterms (id, name, definition) VALUES ('000398', 'U11_snRNA', 'U11 snRNA plays a role in splicing of the minor U12-dependent class of eukaryotic nuclear introns, similar to U1 snRNA in the major class spliceosome it base pairs to the conserved 5 splice site sequence." [PMID:9622129]');
INSERT INTO soterms (id, name, definition) VALUES ('000399', 'U12_snRNA', 'The U12 small nuclear (snRNA), together with U4atac/U6atac, U5, and U11 snRNAs and associated proteins, forms a spliceosome that cleaves a divergent class of low-abundance pre-mRNA introns." [http://www.sanger.ac.uk/cgi-bin/Rfam/getacc?RF00007]');
INSERT INTO soterms (id, name, definition) VALUES ('000400', 'sequence_attribute', 'An attribute describes a quality of sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000401', 'gene_attribute', 'An attribute describes a quality of sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000402', 'enhancer_attribute', 'An attribute describes a quality of sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000403', 'U14_snoRNA', 'U14 small nucleolar RNA (U14 snoRNA) is required for early cleavages of eukaryotic precursor rRNAs. In yeasts, this molecule possess a stem-loop region (known as the Y-domain) which is essential for function. A similar structure, but with a different consensus sequence, is found in plants, but is absent in vertebrates." [http://www.sanger.ac.uk/cgi-bin/Rfam/getacc?RF00016, PMID:2551119]');
INSERT INTO soterms (id, name, definition) VALUES ('000404', 'vault_RNA', 'A family of RNAs are found as part of the enigmatic vault ribonucleoprotein complex. The complex consists of a major vault protein (MVP), two minor vault proteins (VPARP and TEP1), and several small untranslated RNA molecules. It has been suggested that the vault complex is involved in drug resistance." [http://www.sanger.ac.uk/cgi-bin/Rfam/getacc?RF00006]');
INSERT INTO soterms (id, name, definition) VALUES ('000405', 'Y_RNA', 'Y RNAs are components of the Ro ribonucleoprotein particle (Ro RNP), in association with Ro60 and La proteins. The Y RNAs and Ro60 and La proteins are well conserved, but the function of the Ro RNP is not known. In humans the RNA component can be one of four small RNAs: hY1, hY3, hY4 and hY5. These small RNAs are predicted to fold into a conserved secondary structure containing three stem structures. The largest of the four, hY1, contains an additional hairpin." [http://www.sanger.ac.uk/cgi-bin/Rfam/getacc?RF00019]');
INSERT INTO soterms (id, name, definition) VALUES ('000406', 'twintron', 'An intron within an intron. Twintrons are group II or III introns, into which another group II or III intron has been transposed." [PMID:1899376, PMID:7823908]');
INSERT INTO soterms (id, name, definition) VALUES ('000407', 'rRNA_18S', 'A large polynucleotide in eukaryotes, which functions as the small subunit of the ribosome." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000408', 'site', 'The interbase position where something (eg an aberration) occurred." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000409', 'binding_site', 'A biological_region of sequence that, in the molecule, interacts selectively and non-covalently with other molecules. A region on the surface of a molecule that may interact with another molecule. When applied to polypeptides: Amino acids involved in binding or interactions. It can also apply to an amino acid bond which is represented by the positions of the two flanking amino acids." [EBIBS:GAR, SO:ke]');
