INSERT INTO soterms (id, name, definition) VALUES ('000418', 'signal_peptide', 'The signal_peptide is a short region of the peptide located at the N-terminus that directs the protein to be secreted or part of membrane components." [http://www.ebi.ac.uk/embl/Documentation/FT_definitions/feature_table.html]');
