INSERT INTO soterms (id, name, definition) VALUES ('000599', 'edited_by_C_to_U_substitution', 'The insertion and deletion of uridine (U) residues, usually within coding regions of mRNA transcripts of cryptogenes in the mitochondrial genome of kinetoplastid protozoa." [http://www.rna.ucla.edu/index.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000600', 'edited_by_A_to_I_substitution', 'The insertion and deletion of uridine (U) residues, usually within coding regions of mRNA transcripts of cryptogenes in the mitochondrial genome of kinetoplastid protozoa." [http://www.rna.ucla.edu/index.html]');
