INSERT INTO soterms (id, name, definition) VALUES ('000602', 'guide_RNA', 'A short 3-uridylated RNA that can form a duplex (except for its post-transcriptionally added oligo_U tail (SO:0000609)) with a stretch of mature edited mRNA." [http://www.rna.ucla.edu/index.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000603', 'group_II_intron', 'Group II introns are found in rRNA, tRNA and mRNA of organelles in fungi, plants and protists, and also in mRNA in bacteria. They are large self-splicing ribozymes and have 6 structural domains (usually designated dI to dVI). A subset of group II introns also encode essential splicing proteins in intronic ORFs. The length of these introns can therefore be up to 3kb. Splicing occurs in almost identical fashion to nuclear pre-mRNA splicing with two transesterification steps. The 2 hydroxyl of a bulged adenosine in domain VI attacks the 5 splice site, followed by nucleophilic attack on the 3 splice site by the 3 OH of the upstream exon. Protein machinery is required for splicing in vivo, and long range intron to intron and intron-exon interactions are important for splice site positioning. Group II introns are further sub-classified into groups IIA and IIB which differ in splice site consensus, distance of bulged A from 3 splice site, some tertiary interactions, and intronic ORF phylogeny." [http://www.sanger.ac.uk/Software/Rfam/browse/index.shtml]');
INSERT INTO soterms (id, name, definition) VALUES ('000604', 'editing_block', 'Edited mRNA sequence mediated by a single guide RNA (SO:0000602)." [http://dna.kdna.ucla.edu/rna/index.aspx]');
INSERT INTO soterms (id, name, definition) VALUES ('000605', 'intergenic_region', 'A region containing or overlapping no genes that is bounded on either side by a gene, or bounded by a gene and the end of the chromosome." [SO:cjm]');
INSERT INTO soterms (id, name, definition) VALUES ('000606', 'editing_domain', 'Edited mRNA sequence mediated by two or more overlapping guide RNAs (SO:0000602)." [http://dna.kdna.ucla.edu/rna/index.aspx]');
INSERT INTO soterms (id, name, definition) VALUES ('000607', 'unedited_region', 'The region of an edited transcript that will not be edited." [http://dna.kdna.ucla.edu/rna/index.aspx]');
