INSERT INTO soterms (id, name, definition) VALUES ('000611', 'branch_site', 'A pyrimidine rich sequence near the 3 end of an intron to which the 5end becomes covalently bound during nuclear splicing. The resulting structure resembles a lariat." [SO:ke]');
