INSERT INTO soterms (id, name, definition) VALUES ('000650', 'small_subunit_rRNA', 'Ribosomal RNA transcript that structures the small subunit of the ribosome." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000651', 'large_subunit_rRNA', 'Ribosomal RNA transcript that structures the large subunit of the ribosome." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000652', 'rRNA_5S', '5S ribosomal RNA (5S rRNA) is a component of the large ribosomal subunit in both prokaryotes and eukaryotes. In eukaryotes, it is synthesised by RNA polymerase III (the other eukaryotic rRNAs are cleaved from a 45S precursor synthesised by RNA polymerase I). In Xenopus oocytes, it has been shown that fingers 4-7 of the nine-zinc finger transcription factor TFIIIA can bind to the central region of 5S RNA. Thus, in addition to positively regulating 5S rRNA transcription, TFIIIA also stabilizes 5S rRNA until it is required for transcription." [http://www.sanger.ac.uk/cgi-bin/Rfam/getacc?RF00001]');
INSERT INTO soterms (id, name, definition) VALUES ('000653', 'rRNA_28S', 'A component of the large ribosomal subunit." [SO:ke]');
