INSERT INTO soterms (id, name, definition) VALUES ('000685', 'DNAseI_hypersensitive_site', 'A region of nucleotide sequence targeted by a nuclease enzyme." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000686', 'translocation_element', 'A chromosomal translocation whereby the chromosomes carrying non-homologous centromeres may be recovered independently. These chromosomes are described as translocation elements. This occurs for some translocations, particularly but not exclusively, reciprocal translocations." [SO:ma]');
