INSERT INTO soterms (id, name, definition) VALUES ('000691', 'cleaved_initiator_methionine', 'The initiator methionine that has been cleaved from a mature polypeptide sequence." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('000692', 'gene_with_dicistronic_transcript', 'A gene that encodes a dicistronic transcript." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('000693', 'gene_with_recoded_mRNA', 'A gene that encodes an mRNA that is recoded." [SO:xp]');
