INSERT INTO soterms (id, name, definition) VALUES ('000704', 'gene', 'A region (or regions) that includes all of the sequence elements necessary to encode a functional transcript. A gene may include regulatory regions, transcribed regions and/or other functional sequence regions." [SO:immuno_workshop]');
INSERT INTO soterms (id, name, definition) VALUES ('000705', 'tandem_repeat', 'Two or more adjacent copies of a region (of length greater than 1)." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000706', 'trans_splice_acceptor_site', 'The 3 splice site of the acceptor primary transcript." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000707', 'trans_splice_donor_site', 'The 5 five prime splice site region of the donor RNA." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000708', 'SL1_acceptor_site', 'A trans_splicing_acceptor_site which appends the 22nt SL1 RNA leader sequence to the 5 end of most mRNAs." [SO:nlw]');
INSERT INTO soterms (id, name, definition) VALUES ('000709', 'SL2_acceptor_site', 'A trans_splicing_acceptor_site which appends the 22nt SL2 RNA leader sequence to the 5 end of mRNAs. SL2 acceptor sites occur in genes in internal segments of polycistronic transcripts." [SO:nlw]');
INSERT INTO soterms (id, name, definition) VALUES ('000710', 'gene_with_stop_codon_redefined_as_selenocysteine', 'A gene encoding an mRNA that has the stop codon redefined as selenocysteine." [SO:xp]');
