INSERT INTO soterms (id, name, definition) VALUES ('000716', 'dicistronic_mRNA', 'An mRNA that has the quality dicistronic." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000717', 'reading_frame', 'A nucleic acid sequence that when read as sequential triplets, has the potential of encoding a sequential string of amino acids. It need not contain the start or stop codon." [SGD:rb]');
