INSERT INTO soterms (id, name, definition) VALUES ('000723', 'iDNA', 'Genomic sequence removed from the genome, as a normal event, by a process of recombination." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('000724', 'oriT', 'A region of a DNA molecule where transfer is initiated during the process of conjugation or mobilization." [http://www.ebi.ac.uk/embl/Documentation/FT_definitions/feature_table.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000725', 'transit_peptide', 'The transit_peptide is a short region at the N-terminus of the peptide that directs the protein to an organelle (chloroplast, mitochondrion, microbody or cyanelle)." [http://www.ebi.ac.uk/embl/Documentation/FT_definitions/feature_table.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000726', 'repeat_unit', 'The simplest repeated component of a repeat region. A single repeat." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000727', 'CRM', 'A regulatory region where transcription factor binding sites clustered to regulate various aspects of transcription activities. (CRMs can be located a few kb to hundred kb upstream of the basal promoter, in the coding sequence, within introns, or in the downstream 3UTR sequences, as well as on different chromosome). A single gene can be regulated by multiple CRMs to give precise control of its spatial and temporal expression. CRMs function as nodes in large, intertwined regulatory network." [PMID:19660565, SO:SG]');
INSERT INTO soterms (id, name, definition) VALUES ('000728', 'intein', 'A region of a peptide that is able to excise itself and rejoin the remaining portions with a peptide bond." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000729', 'intein_containing', 'An attribute of protein-coding genes where the initial protein product contains an intein." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000730', 'gap', 'A gap in the sequence of known length. The unknown bases are filled in with Ns." [SO:ke]');
