INSERT INTO soterms (id, name, definition) VALUES ('000734', 'exemplar_mRNA', 'An exemplar is a representative cDNA sequence for each gene. The exemplar approach is a method that usually involves some initial clustering into gene groups and the subsequent selection of a representative from each gene group." [http://mged.sourceforge.net/ontologies/MGEDontology.php]');
