INSERT INTO soterms (id, name, definition) VALUES ('000741', 'kinetoplast', 'A kinetoplast is an interlocked network of thousands of minicircles and tens of maxicircles, located near the base of the flagellum of some protozoan species." [PMID:8395055]');
