INSERT INTO soterms (id, name, definition) VALUES ('000770', 'tmRNA_acceptor_piece', 'The acceptor region of a two-piece tmRNA that when mature is charged at its 3 end with alanine. The tmRNA gene undergoes circular permutation in some groups of bacteria; processing of the transcripts from such a gene leaves the mature tmRNA in two pieces, base-paired together." [doi:10.1093/nar/gkh795, Indiana:kw]');
INSERT INTO soterms (id, name, definition) VALUES ('000771', 'QTL', 'A quantitative trait locus (QTL) is a polymorphic locus which contains alleles that differentially affect the expression of a continuously distributed phenotypic trait. Usually it is a marker described by statistical association to quantitative variation in the particular phenotypic trait that is thought to be controlled by the cumulative action of alleles at multiple loci." [http://rgd.mcw.edu/tu/qtls/]');
INSERT INTO soterms (id, name, definition) VALUES ('000772', 'genomic_island', 'A genomic island is an integrated mobile genetic element, characterized by size (over 10 Kb). It that has features that suggest a foreign origin. These can include nucleotide distribution (oligonucleotides signature, CG content etc.) that differs from the bulk of the chromosome and/or genes suggesting DNA mobility." [Phigo:at, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000773', 'pathogenic_island', 'Mobile genetic elements that contribute to rapid changes in virulence potential. They are present on the genomes of pathogenic strains but absent from the genomes of non pathogenic members of the same or related species." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000774', 'metabolic_island', 'A transmissible element containing genes involved in metabolism, analogous to the pathogenicity islands of gram negative bacteria." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000775', 'adaptive_island', 'An adaptive island is a genomic island that provides an adaptive advantage to the host." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000776', 'symbiosis_island', 'A transmissible element containing genes involved in symbiosis, analogous to the pathogenicity islands of gram negative bacteria." [SO:ke]');
