INSERT INTO soterms (id, name, definition) VALUES ('001005', 'low_complexity_region', 'A recombination product between the 2 LTR of the same element." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001006', 'prophage', 'A phage genome after it has established in the host genome in a latent/immune state either as a plasmid or as an integrated \"island\"." [GOC:jl]');
INSERT INTO soterms (id, name, definition) VALUES ('001007', 'cryptic_prophage', 'A remnant of an integrated prophage in the host genome or an \"island\" in the host genome that includes phage like-genes." [GOC:jl]');
INSERT INTO soterms (id, name, definition) VALUES ('001008', 'tetraloop', 'A base-paired stem with loop of 4 non-hydrogen bonded nucleotides." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001009', 'DNA_constraint_sequence', 'A double-stranded DNA used to control macromolecular structure and function." [http:/www.ncbi.nlm.nih.gov/entrez/query.fcgi?cmd=search&db=pubmed&term=SILVERMAN+SK[au\]&dispmax=50]');
INSERT INTO soterms (id, name, definition) VALUES ('001010', 'i_motif', 'A cytosine rich domain whereby strands associate both inter- and intramolecularly at moderately acidic pH." [PMID:9753739]');
INSERT INTO soterms (id, name, definition) VALUES ('001011', 'PNA_oligo', 'Peptide nucleic acid, is a chemical not known to occur naturally but is artificially synthesized and used in some biological research and medical treatments. The PNA backbone is composed of repeating N-(2-aminoethyl)-glycine units linked by peptide bonds. The purine and pyrimidine bases are linked to the backbone by methylene carbonyl bonds." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001012', 'DNAzyme', 'A DNA sequence with catalytic activity." [SO:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001013', 'MNP', 'A multiple nucleotide polymorphism with alleles of common length > 1, for example AAA/TTT." [http://www.ncbi.nlm.nih.gov/SNP/snp_ref.cgi?rs=rs2067431]');
INSERT INTO soterms (id, name, definition) VALUES ('001014', 'intron_domain', 'A multiple nucleotide polymorphism with alleles of common length > 1, for example AAA/TTT." [http://www.ncbi.nlm.nih.gov/SNP/snp_ref.cgi?rs=rs2067431]');
INSERT INTO soterms (id, name, definition) VALUES ('001015', 'wobble_base_pair', 'A type of non-canonical base pairing, most commonly between G and U, which is important for the secondary structure of RNAs. It has similar thermodynamic stability to the Watson-Crick pairing. Wobble base pairs only have two hydrogen bonds. Other wobble base pair possibilities are I-A, I-U and I-C." [PMID:11256617]');
INSERT INTO soterms (id, name, definition) VALUES ('001016', 'internal_guide_sequence', 'A purine-rich sequence in the group I introns which determines the locations of the splice sites in group I intron splicing and has catalytic activity." [SO:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001017', 'silent_mutation', 'A sequence variant that does not affect protein function. Silent mutations may occur in genic ( CDS, UTR, intron etc) and intergenic regions. Silent mutations may have affects on processes such as splicing and regulation." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001018', 'epitope', 'A binding site that, in the molecule, interacts selectively and non-covalently with antibodies, B cells or T cells." [http://en.wikipedia.org/wiki/Epitope, SO:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001019', 'copy_number_variation', 'A variation that increases or decreases the copy number of a given region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001020', 'sequence_variant_affecting_copy_number', 'A variation that increases or decreases the copy number of a given region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001021', 'chromosome_breakpoint', 'A variation that increases or decreases the copy number of a given region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001022', 'inversion_breakpoint', 'The point within a chromosome where an inversion begins or ends." [SO:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001023', 'allele', 'An allele is one of a set of coexisting sequence variants of a gene." [SO:immuno_workshop]');
INSERT INTO soterms (id, name, definition) VALUES ('001024', 'haplotype', 'A haplotype is one of a set of coexisting sequence variants of a haplotype block." [SO:immuno_workshop]');
INSERT INTO soterms (id, name, definition) VALUES ('001025', 'polymorphic_sequence_variant', 'A sequence variant that is segregating in one or more natural populations of a species." [SO:immuno_workshop]');
INSERT INTO soterms (id, name, definition) VALUES ('001026', 'genome', 'A genome is the sum of genetic material within a cell or virion." [SO:immuno_workshop]');
INSERT INTO soterms (id, name, definition) VALUES ('001027', 'genotype', 'A genotype is a variant genome, complete or incomplete." [SO:immuno_workshop]');
INSERT INTO soterms (id, name, definition) VALUES ('001028', 'diplotype', 'A diplotype is a pair of haplotypes from a given individual. It is a genotype where the phase is known." [SO:immuno_workshop]');
INSERT INTO soterms (id, name, definition) VALUES ('001029', 'direction_attribute', 'A diplotype is a pair of haplotypes from a given individual. It is a genotype where the phase is known." [SO:immuno_workshop]');
INSERT INTO soterms (id, name, definition) VALUES ('001030', 'forward', 'Forward is an attribute of the feature, where the feature is in the 5 to 3 direction." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001031', 'reverse', 'Reverse is an attribute of the feature, where the feature is in the 3 to 5 direction. Again could be applied to primer." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001032', 'mitochondrial_DNA', 'Reverse is an attribute of the feature, where the feature is in the 3 to 5 direction. Again could be applied to primer." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001033', 'chloroplast_DNA', 'Reverse is an attribute of the feature, where the feature is in the 3 to 5 direction. Again could be applied to primer." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001034', 'miRtron', 'A de-branched intron which mimics the structure of pre-miRNA and enters the miRNA processing pathway without Drosha mediated cleavage." [PMID:17589500, SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('001035', 'piRNA', 'A small non coding RNA, part of a silencing system that prevents the spreading of selfish genetic elements." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001036', 'arginyl_tRNA', 'A tRNA sequence that has an arginine anticodon, and a 3 arginine binding region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001037', 'mobile_genetic_element', 'A nucleotide region with either intra-genome or intracellular mobility, of varying length, which often carry the information necessary for transfer and recombination with the host genome." [PMID:14681355]');
INSERT INTO soterms (id, name, definition) VALUES ('001038', 'extrachromosomal_mobile_genetic_element', 'An MGE that is not integrated into the host chromosome." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001039', 'integrated_mobile_genetic_element', 'An MGE that is integrated into the host chromosome." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001040', 'integrated_plasmid', 'A plasmid sequence that is integrated within the host chromosome." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001041', 'viral_sequence', 'The region of nucleotide sequence of a virus, a submicroscopic particle that replicates by infecting a host cell." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001042', 'phage_sequence', 'The nucleotide sequence of a virus that infects bacteria." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001043', 'attCtn_site', 'An attachment site located on a conjugative transposon and used for site-specific integration of a conjugative transposon." [Phigo:at]');
INSERT INTO soterms (id, name, definition) VALUES ('001044', 'nuclear_mt_pseudogene', 'A nuclear pseudogene of either coding or non-coding mitochondria derived sequence." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('001045', 'cointegrated_plasmid', 'A MGE region consisting of two fused plasmids resulting from a replicative transposition event." [phigo:at]');
INSERT INTO soterms (id, name, definition) VALUES ('001046', 'IRLinv_site', 'Component of the inversion site located at the left of a region susceptible to site-specific inversion." [Phigo:at]');
INSERT INTO soterms (id, name, definition) VALUES ('001047', 'IRRinv_site', 'Component of the inversion site located at the right of a region susceptible to site-specific inversion." [Phigo:at]');
INSERT INTO soterms (id, name, definition) VALUES ('001048', 'inversion_site_part', 'A region located within an inversion site." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001049', 'defective_conjugative_transposon', 'An island that contains genes for integration/excision and the gene and site for the initiation of intercellular transfer by conjugation. It can be complemented for transfer by a conjugative transposon." [Phigo:ariane]');
INSERT INTO soterms (id, name, definition) VALUES ('001050', 'repeat_fragment', 'A portion of a repeat, interrupted by the insertion of another element." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001051', 'nested_region', 'A portion of a repeat, interrupted by the insertion of another element." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001052', 'nested_repeat', 'A portion of a repeat, interrupted by the insertion of another element." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001053', 'nested_transposon', 'A portion of a repeat, interrupted by the insertion of another element." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001054', 'transposon_fragment', 'A portion of a transposon, interrupted by the insertion of another element." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001055', 'transcriptional_cis_regulatory_region', 'A regulatory_region that modulates the transcription of a gene or genes." [PMID:9679020, SO:regcreative]');
INSERT INTO soterms (id, name, definition) VALUES ('001056', 'splicing_regulatory_region', 'A regulatory_region that modulates splicing." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001057', 'enhanceosome', 'A regulatory_region that modulates splicing." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001058', 'promoter_targeting_sequence', 'A transcriptional_cis_regulatory_region that restricts the activity of a CRM to a single promoter and which functions only when both itself and an insulator are located between the CRM and the promoter." [SO:regcreative]');
INSERT INTO soterms (id, name, definition) VALUES ('001059', 'sequence_alteration', 'A sequence_alteration is a sequence_feature whose extent is the deviation from another sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001060', 'sequence_variant', 'A sequence_variant is a non exact copy of a sequence_feature or genome exhibiting one or more sequence_alteration." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001061', 'propeptide_cleavage_site', 'The propeptide_cleavage_site is the arginine/lysine boundary on a propeptide where cleavage occurs." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('001062', 'propeptide', 'Part of a peptide chain which is cleaved off during the formation of the mature protein." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('001063', 'immature_peptide_region', 'An immature_peptide_region is the extent of the peptide after it has been translated and before any processing occurs." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('001064', 'active_peptide', 'Active peptides are proteins which are biologically active, released from a precursor molecule." [EBIBS:GAR, UniProt:curation_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('001066', 'compositionally_biased_region_of_peptide', 'Polypeptide region that is rich in a particular amino acid or homopolymeric and greater than three residues in length." [EBIBS:GAR, UniProt:curation_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('001067', 'polypeptide_motif', 'A sequence motif is a short (up to 20 amino acids) region of biological interest. Such motifs, although they are too short to constitute functional domains, share sequence similarities and are conserved in different proteins. They display a common function (protein-binding, subcellular location etc.)." [EBIBS:GAR, UniProt:curation_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('001068', 'polypeptide_repeat', 'A polypeptide_repeat is a single copy of an internal sequence repetition." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('001070', 'polypeptide_structural_region', 'Region of polypeptide with a given structural property." [EBIBS:GAR, SO:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001071', 'membrane_structure', 'Arrangement of the polypeptide with respect to the lipid bilayer." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('001072', 'extramembrane_polypeptide_region', 'Polypeptide region that is localized outside of a lipid bilayer." [EBIBS:GAR, SO:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001073', 'cytoplasmic_polypeptide_region', 'Polypeptide region that is localized inside the cytoplasm." [EBIBS:GAR, SO:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001074', 'non_cytoplasmic_polypeptide_region', 'Polypeptide region that is localized outside of a lipid bilayer and outside of the cytoplasm." [EBIBS:GAR, SO:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001075', 'intramembrane_polypeptide_region', 'Polypeptide region present in the lipid bilayer." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('001076', 'membrane_peptide_loop', 'Polypeptide region localized within the lipid bilayer where both ends traverse the same membrane." [EBIBS:GAR, SO:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001077', 'transmembrane_polypeptide_region', 'Polypeptide region traversing the lipid bilayer." [EBIBS:GAR, UniProt:curator_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('001078', 'polypeptide_secondary_structure', 'A region of peptide with secondary structure has hydrogen bonding along the peptide chain that causes a defined conformation of the chain." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('001079', 'polypeptide_structural_motif', 'Motif is a three-dimensional structural element within the chain, which appears also in a variety of other molecules. Unlike a domain, a motif does not need to form a stable globular unit." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('001080', 'coiled_coil', 'A coiled coil is a structural motif in proteins, in which alpha-helices are coiled together like the strands of a rope." [EBIBS:GAR, UniProt:curation_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('001081', 'helix_turn_helix', 'A motif comprising two helices separated by a turn." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('001082', 'polypeptide_sequencing_information', 'Incompatibility in the sequence due to some experimental problem." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('001083', 'non_adjacent_residues', 'Indicates that two consecutive residues in a fragment sequence are not consecutive in the full-length protein and that there are a number of unsequenced residues between them." [EBIBS:GAR, UniProt:curation_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('001084', 'non_terminal_residue', 'The residue at an extremity of the sequence is not the terminal residue." [EBIBS:GAR, UniProt:curation_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('001085', 'sequence_conflict', 'Different sources report differing sequences." [EBIBS:GAR, UniProt:curation_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('001086', 'sequence_uncertainty', 'Describes the positions in a sequence where the authors are unsure about the sequence assignment." [EBIBS:GAR, UniProt:curation_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('001087', 'cross_link', 'Posttranslationally formed amino acid bonds." [EBIBS:GAR, UniProt:curation_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('001088', 'disulfide_bond', 'The covalent bond between sulfur atoms that binds two peptide chains or different parts of one peptide chain and is a structural determinant in many protein molecules." [EBIBS:GAR, UniProt:curation_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('001089', 'post_translationally_modified_region', 'A region where a transformation occurs in a protein after it has been synthesized. This which may regulate, stabilize, crosslink or introduce new chemical functionalities in the protein." [EBIBS:GAR, UniProt:curation_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('001090', 'covalent_binding_site', 'Binding involving a covalent bond." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('001091', 'non_covalent_binding_site', 'Binding site for any chemical group (co-enzyme, prosthetic group, etc.)." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('001092', 'polypeptide_metal_contact', 'A binding site that, in the polypeptide molecule, interacts selectively and non-covalently with metal ions." [EBIBS:GAR, SO:cb, UniProt:curation_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('001093', 'protein_protein_contact', 'A binding site that, in the protein molecule, interacts selectively and non-covalently with polypeptide residues." [EBIBS:GAR, UniProt:Curation_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('001094', 'polypeptide_calcium_ion_contact_site', 'A binding site that, in the polypeptide molecule, interacts selectively and non-covalently with calcium ions." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('001095', 'polypeptide_cobalt_ion_contact_site', 'A binding site that, in the polypeptide molecule, interacts selectively and non-covalently with cobalt ions." [EBIBS:GAR, SO:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001096', 'polypeptide_copper_ion_contact_site', 'A binding site that, in the polypeptide molecule, interacts selectively and non-covalently with copper ions." [EBIBS:GAR, SO:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001097', 'polypeptide_iron_ion_contact_site', 'A binding site that, in the polypeptide molecule, interacts selectively and non-covalently with iron ions." [EBIBS:GAR, SO:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001098', 'polypeptide_magnesium_ion_contact_site', 'A binding site that, in the polypeptide molecule, interacts selectively and non-covalently with magnesium ions." [EBIBS:GAR, SO:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001099', 'polypeptide_manganese_ion_contact_site', 'A binding site that, in the polypeptide molecule, interacts selectively and non-covalently with manganese ions." [EBIBS:GAR, SO:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001100', 'polypeptide_molybdenum_ion_contact_site', 'A binding site that, in the polypeptide molecule, interacts selectively and non-covalently with molybdenum ions." [EBIBS:GAR, SO:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001101', 'polypeptide_nickel_ion_contact_site', 'A binding site that, in the polypeptide molecule, interacts selectively and non-covalently with nickel ions." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('001102', 'polypeptide_tungsten_ion_contact_site', 'A binding site that, in the polypeptide molecule, interacts selectively and non-covalently with tungsten ions." [EBIBS:GAR, SO:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001103', 'polypeptide_zinc_ion_contact_site', 'A binding site that, in the polypeptide molecule, interacts selectively and non-covalently with zinc ions." [EBIBS:GAR, SO:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001104', 'catalytic_residue', 'Amino acid involved in the activity of an enzyme." [EBIBS:GAR, UniProt:curation_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('001105', 'polypeptide_ligand_contact', 'Residues which interact with a ligand." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('001106', 'asx_motif', 'A motif of five consecutive residues and two H-bonds in which: Residue(i) is Aspartate or Asparagine (Asx), side-chain O of residue(i) is H-bonded to the main-chain NH of residue(i+2) or (i+3), main-chain CO of residue(i) is H-bonded to the main-chain NH of residue(i+3) or (i+4)." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001107', 'beta_bulge', 'A motif of three residues within a beta-sheet in which the main chains of two consecutive residues are H-bonded to that of the third, and in which the dihedral angles are as follows: Residue(i): -140 degrees < phi(l) -20 degrees , -90 degrees < psi(l) < 40 degrees. Residue (i+1): -180 degrees < phi < -25 degrees or +120 degrees < phi < +180 degrees, +40 degrees < psi < +180 degrees or -180 degrees < psi < -120 degrees." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001108', 'beta_bulge_loop', 'A motif of three residues within a beta-sheet consisting of two H-bonds. Beta bulge loops often occur at the loop ends of beta-hairpins." [EBIBS:GAR, Http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001109', 'beta_bulge_loop_five', 'A motif of three residues within a beta-sheet consisting of two H-bonds in which: the main-chain NH of residue(i) is H-bonded to the main-chain CO of residue(i+4), the main-chain CO of residue i is H-bonded to the main-chain NH of residue(i+3), these loops have an RL nest at residues i+2 and i+3." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001110', 'beta_bulge_loop_six', 'A motif of three residues within a beta-sheet consisting of two H-bonds in which: the main-chain NH of residue(i) is H-bonded to the main-chain CO of residue(i+5), the main-chain CO of residue i is H-bonded to the main-chain NH of residue(i+4), these loops have an RL nest at residues i+3 and i+4." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001111', 'beta_strand', 'A beta strand describes a single length of polypeptide chain that forms part of a beta sheet. A single continuous stretch of amino acids adopting an extended conformation of hydrogen bonds between the N-O and the C=O of another part of the peptide. This forms a secondary protein structure in which two or more extended polypeptide regions are hydrogen-bonded to one another in a planar array." [EBIBS:GAR, UniProt:curation_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('001112', 'antiparallel_beta_strand', 'A peptide region which hydrogen bonded to another region of peptide running in the oposite direction (one running N-terminal to C-terminal and one running C-terminal to N-terminal). Hydrogen bonding occurs between every other C=O from one strand to every other N-H on the adjacent strand. In this case, if two atoms C-alpha (i) and C-alpha (j) are adjacent in two hydrogen-bonded beta strands, then they form two mutual backbone hydrogen bonds to each others flanking peptide groups; this is known as a close pair of hydrogen bonds. The peptide backbone dihedral angles (phi, psi) are about (-140 degrees, 135 degrees) in antiparallel sheets." [EBIBS:GAR, UniProt:curation_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('001113', 'parallel_beta_strand', 'A peptide region which hydrogen bonded to another region of peptide running in the oposite direction (both running N-terminal to C-terminal). This orientation is slightly less stable because it introduces nonplanarity in the inter-strand hydrogen bonding pattern. Hydrogen bonding occurs between every other C=O from one strand to every other N-H on the adjacent strand. In this case, if two atoms C-alpha (i)and C-alpha (j) are adjacent in two hydrogen-bonded beta strands, then they do not hydrogen bond to each other; rather, one residue forms hydrogen bonds to the residues that flank the other (but not vice versa). For example, residue i may form hydrogen bonds to residues j - 1 and j + 1; this is known as a wide pair of hydrogen bonds. By contrast, residue j may hydrogen-bond to different residues altogether, or to none at all. The dihedral angles (phi, psi) are about (-120 degrees, 115 degrees) in parallel sheets." [EBIBS:GAR, UniProt:curation_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('001114', 'peptide_helix', 'A helix is a secondary_structure conformation where the peptide backbone forms a coil." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('001115', 'left_handed_peptide_helix', 'A left handed helix is a region of peptide where the coiled conformation turns in an anticlockwise, left handed screw." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('001116', 'right_handed_peptide_helix', 'A right handed helix is a region of peptide where the coiled conformation turns in a clockwise, right handed screw." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('001117', 'alpha_helix', 'The helix has 3.6 residues per turn which corresponds to a translation of 1.5 angstroms (= 0.15 nm) along the helical axis. Every backbone N-H group donates a hydrogen bond to the backbone C=O group of the amino acid four residues earlier." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('001118', 'pi_helix', 'The pi helix has 4.1 residues per turn and a translation of 1.15  (=0.115 nm) along the helical axis. The N-H group of an amino acid forms a hydrogen bond with the C=O group of the amino acid five residues earlier." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('001119', 'three_ten_helix', 'The 3-10 helix has 3 residues per turn with a translation of 2.0 angstroms (=0.2 nm) along the helical axis. The N-H group of an amino acid forms a hydrogen bond with the C=O group of the amino acid three residues earlier." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('001120', 'polypeptide_nest_motif', 'A motif of two consecutive residues with dihedral angles. Nest should not have Proline as any residue. Nests frequently occur as parts of other motifs such as Schellman loops." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001121', 'polypeptide_nest_left_right_motif', 'A motif of two consecutive residues with dihedral angles: Residue(i): +20 degrees < phi < +140 degrees, -40 degrees < psi < +90 degrees. Residue(i+1): -140 degrees < phi < -20 degrees, -90 degrees < psi < +40 degrees." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001122', 'polypeptide_nest_right_left_motif', 'A motif of two consecutive residues with dihedral angles: Residue(i): -140 degrees < phi < -20 degrees, -90 degrees < psi < +40 degrees. Residue(i+1): +20 degrees < phi < +140 degrees, -40 degrees < psi < +90 degrees." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001123', 'schellmann_loop', 'A motif of six or seven consecutive residues that contains two H-bonds." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001124', 'schellmann_loop_seven', 'Wild type: A motif of seven consecutive residues that contains two H-bonds in which: the main-chain CO of residue(i) is H-bonded to the main-chain NH of residue(i+6), the main-chain CO of residue(i+1) is H-bonded to the main-chain NH of residue(i+5)." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001125', 'schellmann_loop_six', 'Common Type: A motif of six consecutive residues that contains two H-bonds in which: the main-chain CO of residue(i) is H-bonded to the main-chain NH of residue(i+5) the main-chain CO of residue(i+1) is H-bonded to the main-chain NH of residue(i+4)." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001126', 'serine_threonine_motif', 'A motif of five consecutive residues and two hydrogen bonds in which: residue(i) is Serine (S) or Threonine (T), the side-chain O of residue(i) is H-bonded to the main-chain NH of residue(i+2) or (i+3) , the main-chain CO group of residue(i) is H-bonded to the main-chain NH of residue(i+3) or (i+4)." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001127', 'serine_threonine_staple_motif', 'A motif of four or five consecutive residues and one H-bond in which: residue(i) is Serine (S) or Threonine (T), the side-chain OH of residue(i) is H-bonded to the main-chain CO of residue(i3) or (i4), Phi angles of residues(i1), (i2) and (i3) are negative." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001128', 'polypeptide_turn_motif', 'A reversal in the direction of the backbone of a protein that is stabilized by hydrogen bond between backbone NH and CO groups, involving no more than 4 amino acid residues." [EBIBS:GAR, uniprot:feature_type]');
INSERT INTO soterms (id, name, definition) VALUES ('001129', 'asx_turn_left_handed_type_one', 'Left handed type I (dihedral angles):- Residue(i): -140 degrees < chi (1) -120 degrees < -20 degrees, -90 degrees < psi +120 degrees < +40 degrees. Residue(i+1): -140 degrees < phi < -20 degrees, -90 degrees < psi < +40 degrees." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001130', 'asx_turn_left_handed_type_two', 'Left handed type II (dihedral angles):- Residue(i): -140 degrees < chi (1) -120 degrees < -20 degrees, +80 degrees < psi +120 degrees < +180 degrees. Residue(i+1): +20 degrees < phi < +140 degrees, -40 degrees < psi < +90 degrees." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001131', 'asx_turn_right_handed_type_two', 'Right handed type II (dihedral angles):- Residue(i): -140 degrees < chi (1) -120 degrees < -20 degrees, +80 degrees < psi +120 degrees < +180 degrees. Residue(i+1): +20 degrees < phi < +140 degrees, -40 degrees < psi < +90 degrees." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001132', 'asx_turn_right_handed_type_one', 'Right handed type I (dihedral angles):- Residue(i): -140 degrees < chi (1) -120 degrees < -20 degrees, -90 degrees < psi +120 degrees < +40 degrees. Residue(i+1): -140 degrees < phi < -20 degrees, -90 degrees < psi < +40 degrees." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001133', 'beta_turn', 'A motif of four consecutive residues that may contain one H-bond, which, if present, is between the main-chain CO of the first residue and the main-chain NH of the fourth. It is characterized by the dihedral angles of the second and third residues, which are the basis for sub-categorization." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001134', 'beta_turn_left_handed_type_one', 'Left handed type I:A motif of four consecutive residues that may contain one H-bond, which, if present, is between the main-chain CO of the first residue and the main-chain NH of the fourth. It is characterized by the dihedral angles:- Residue(i+1): -140 degrees > phi > -20 degrees, -90 degrees > psi > +40 degrees. Residue(i+2): -140 degrees > phi > -20 degrees, -90 degrees > psi > +40 degrees." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001135', 'beta_turn_left_handed_type_two', 'Left handed type II: A motif of four consecutive residues that may contain one H-bond, which, if present, is between the main-chain CO of the first residue and the main-chain NH of the fourth. It is characterized by the dihedral angles: Residue(i+1): -140 degrees > phi > -20 degrees, +80 degrees > psi > +180 degrees. Residue(i+2): +20 degrees > phi > +140 degrees, -40 degrees > psi > +90 degrees." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001136', 'beta_turn_right_handed_type_one', 'Right handed type I:A motif of four consecutive residues that may contain one H-bond, which, if present, is between the main-chain CO of the first residue and the main-chain NH of the fourth. It is characterized by the dihedral angles: Residue(i+1): -140 degrees < phi < -20 degrees, -90 degrees < psi < +40 degrees. Residue(i+2): -140 degrees < phi < -20 degrees, -90 degrees < psi < +40 degrees." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001137', 'beta_turn_right_handed_type_two', 'Right handed type II:A motif of four consecutive residues that may contain one H-bond, which, if present, is between the main-chain CO of the first residue and the main-chain NH of the fourth. It is characterized by the dihedral angles: Residue(i+1): -140 degrees < phi < -20 degrees, +80 degrees < psi < +180 degrees. Residue(i+2): +20 degrees < phi < +140 degrees, -40 degrees < psi < +90 degrees." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001138', 'gamma_turn', 'Gamma turns, defined for 3 residues i,( i+1),( i+2) if a hydrogen bond exists between residues i and i+2 and the phi and psi angles of residue i+1 fall within 40 degrees." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001139', 'gamma_turn_classic', 'Gamma turns, defined for 3 residues i, i+1, i+2 if a hydrogen bond exists between residues i and i+2 and the phi and psi angles of residue i+1 fall within 40 degrees: phi(i+1)=75.0 - psi(i+1)=-64.0." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001140', 'gamma_turn_inverse', 'Gamma turns, defined for 3 residues i, i+1, i+2 if a hydrogen bond exists between residues i and i+2 and the phi and psi angles of residue i+1 fall within 40 degrees: phi(i+1)=-79.0 - psi(i+1)=69.0." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001141', 'serine_threonine_turn', 'A motif of three consecutive residues and one H-bond in which: residue(i) is Serine (S) or Threonine (T), the side-chain O of residue(i) is H-bonded to the main-chain NH of residue(i+2)." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001142', 'st_turn_left_handed_type_one', 'The peptide twists in an anticlockwise, left handed manner. The dihedral angles for this turn are: Residue(i): -140 degrees < chi(1) -120 degrees < -20 degrees, -90 degrees psi +120 degrees < +40 degrees, residue(i+1): -140 degrees < phi < -20 degrees, -90 < psi < +40 degrees." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001143', 'st_turn_left_handed_type_two', 'The peptide twists in an anticlockwise, left handed manner. The dihedral angles for this turn are: Residue(i): -140 degrees < chi(1) -120 degrees < -20 degrees, +80 degrees psi +120 degrees < +180 degrees, residue(i+1): +20 degrees < phi < +140 degrees, -40 < psi < +90 degrees." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001144', 'st_turn_right_handed_type_one', 'The peptide twists in an clockwise, right handed manner. The dihedral angles for this turn are: Residue(i): -140 degrees < chi(1) -120 degrees < -20 degrees, -90 degrees psi +120 degrees < +40 degrees, residue(i+1): -140 degrees < phi < -20 degrees, -90 < psi < +40 degrees." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001145', 'st_turn_right_handed_type_two', 'The peptide twists in an clockwise, right handed manner. The dihedral angles for this turn are: Residue(i): -140 degrees < chi(1) -120 degrees < -20 degrees, +80 degrees psi +120 degrees < +180 degrees, residue(i+1): +20 degrees < phi < +140 degrees, -40 < psi < +90 degrees." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('001146', 'polypeptide_variation_site', 'A site of sequence variation (alteration). Alternative sequence due to naturally occurring events such as polymorphisms and alternative splicing or experimental methods such as site directed mutagenesis." [EBIBS:GAR, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001147', 'natural_variant_site', 'Describes the natural sequence variants due to polymorphisms, disease-associated mutations, RNA editing and variations between strains, isolates or cultivars." [EBIBS:GAR, UniProt:curation_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('001148', 'mutated_variant_site', 'Site which has been experimentally altered." [EBIBS:GAR, UniProt:curation_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('001149', 'alternate_sequence_site', 'Description of sequence variants produced by alternative splicing, alternative promoter usage, alternative initiation and ribosomal frameshifting." [EBIBS:GAR, UniProt:curation_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('001150', 'beta_turn_type_six', 'A motif of four consecutive peptide resides of type VIa or type VIb and where the i+2 residue is cis-proline." [SO:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001151', 'beta_turn_type_six_a', 'A motif of four consecutive peptide residues, of which the i+2 residue is proline, and that may contain one H-bond, which, if present, is between the main-chain CO of the first residue and the main-chain NH of the fourth and is characterized by the dihedral angles: Residue(i+1): phi ~ -60 degrees, psi ~ 120 degrees. Residue(i+2): phi ~ -90 degrees, psi ~ 0 degrees." [PMID:2371257, SO:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001152', 'beta_turn_type_six_a_one', 'A motif of four consecutive peptide residues, of which the i+2 residue is proline, and that may contain one H-bond, which, if present, is between the main-chain CO of the first residue and the main-chain NH of the fourth and is characterized by the dihedral angles: Residue(i+1): phi ~ -60 degrees, psi ~ 120 degrees. Residue(i+2): phi ~ -90 degrees, psi ~ 0 degrees." [PMID:2371257, SO:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001153', 'beta_turn_type_six_a_two', 'A motif of four consecutive peptide residues, of which the i+2 residue is proline, and that may contain one H-bond, which, if present, is between the main-chain CO of the first residue and the main-chain NH of the fourth and is characterized by the dihedral angles: Residue(i+1): phi ~ -60 degrees, psi ~ 120 degrees. Residue(i+2): phi ~ -90 degrees, psi ~ 0 degrees." [PMID:2371257, SO:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001154', 'beta_turn_type_six_b', 'A motif of four consecutive peptide residues, of which the i+2 residue is proline, and that may contain one H-bond, which, if present, is between the main-chain CO of the first residue and the main-chain NH of the fourth and is characterized by the dihedral angles: Residue(i+1): phi ~ -120 degrees, psi ~ 120 degrees. Residue(i+2): phi ~ -60 degrees, psi ~ 0 degrees." [PMID:2371257, SO:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001155', 'beta_turn_type_eight', 'A motif of four consecutive peptide residues that may contain one H-bond, which, if present, is between the main-chain CO of the first residue and the main-chain NH of the fourth and is characterized by the dihedral angles: Residue(i+1): phi ~ -60 degrees, psi ~ -30 degrees. Residue(i+2): phi ~ -120 degrees, psi ~ 120 degrees." [PMID:2371257, SO:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001156', 'DRE_motif', 'A sequence element characteristic of some RNA polymerase II promoters, usually located between -10 and -60 relative to the TSS. Consensus sequence is WATCGATW." [PMID:12537576]');
INSERT INTO soterms (id, name, definition) VALUES ('001157', 'DMv4_motif', 'A sequence element characteristic of some RNA polymerase II promoters, located immediately upstream of some TATA box elements with respect to the TSS (+1). Consensus sequence is YGGTCACACTR. Marked spatial preference within core promoter; tend to occur near the TSS, although not as tightly as INR (SO:0000014)." [PMID:16827941\:12537576]');
INSERT INTO soterms (id, name, definition) VALUES ('001158', 'E_box_motif', 'A sequence element characteristic of some RNA polymerase II promoters, usually located between -60 and +1 relative to the TSS. Consensus sequence is AWCAGCTGWT. Tends to co-occur with DMv2 (SO:0001161). Tends to not occur with DPE motif (SO:0000015)." [PMID:12537576\:16827941]');
INSERT INTO soterms (id, name, definition) VALUES ('001159', 'DMv5_motif', 'A sequence element characteristic of some RNA polymerase II promoters, usually located between -50 and -10 relative to the TSS. Consensus sequence is KTYRGTATWTTT. Tends to co-occur with DMv4 (SO:0001157) . Tends to not occur with DPE motif (SO:0000015) or MTE (SO:0001162)." [PMID:12537576\:16827941]');
INSERT INTO soterms (id, name, definition) VALUES ('001160', 'DMv3_motif', 'A sequence element characteristic of some RNA polymerase II promoters, usually located between -30 and +15 relative to the TSS. Consensus sequence is KNNCAKCNCTRNY. Tends to co-occur with DMv2 (SO:0001161). Tends to not occur with DPE motif (SO:0000015) or MTE (0001162)." [PMID:12537576\:16827941]');
INSERT INTO soterms (id, name, definition) VALUES ('001161', 'DMv2_motif', 'A sequence element characteristic of some RNA polymerase II promoters, usually located between -60 and -45 relative to the TSS. Consensus sequence is MKSYGGCARCGSYSS. Tends to co-occur with DMv3 (SO:0001160). Tends to not occur with DPE motif (SO:0000015) or MTE (SO:0001162)." [PMID:12537576\:16827941]');
INSERT INTO soterms (id, name, definition) VALUES ('001162', 'MTE', 'A sequence element characteristic of some RNA polymerase II promoters, usually located between +20 and +30 relative to the TSS. Consensus sequence is CSARCSSAACGS. Tends to co-occur with INR motif (SO:0000014). Tends to not occur with DPE motif (SO:0000015) or DMv5 (SO:0001159)." [PMID:12537576\:15231738, PMID:16858867]');
INSERT INTO soterms (id, name, definition) VALUES ('001163', 'INR1_motif', 'A promoter motif with consensus sequence TCATTCG." [PMID:16827941]');
INSERT INTO soterms (id, name, definition) VALUES ('001164', 'DPE1_motif', 'A promoter motif with consensus sequence CGGACGT." [PMID:16827941]');
INSERT INTO soterms (id, name, definition) VALUES ('001165', 'DMv1_motif', 'A promoter motif with consensus sequence CARCCCT." [PMID:16827941]');
INSERT INTO soterms (id, name, definition) VALUES ('001166', 'GAGA_motif', 'A non directional promoter motif with consensus sequence GAGAGCG." [PMID:16827941]');
INSERT INTO soterms (id, name, definition) VALUES ('001167', 'NDM2_motif', 'A non directional promoter motif with consensus CGMYGYCR." [PMID:16827941]');
INSERT INTO soterms (id, name, definition) VALUES ('001168', 'NDM3_motif', 'A non directional promoter motif with consensus sequence GAAAGCT." [PMID:16827941]');
INSERT INTO soterms (id, name, definition) VALUES ('001169', 'ds_RNA_viral_sequence', 'A ds_RNA_viral_sequence is a viral_sequence that is the sequence of a virus that exists as double stranded RNA." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001170', 'polinton', 'A kind of DNA transposon that populates the genomes of protists, fungi, and animals, characterized by a unique set of proteins necessary for their transposition, including a protein-primed DNA polymerase B, retroviral integrase, cysteine protease, and ATPase. Polintons are characterized by 6-bp target site duplications, terminal-inverted repeats that are several hundred nucleotides long, and 5-AG and TC-3 termini. Polintons exist as autonomous and nonautonomous elements." [PMID:16537396]');
INSERT INTO soterms (id, name, definition) VALUES ('001171', 'rRNA_21S', 'A component of the large ribosomal subunit in mitochondrial rRNA." [RSC:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001172', 'tRNA_region', 'A region of a tRNA." [RSC:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001173', 'anticodon_loop', 'A sequence of seven nucleotide bases in tRNA which contains the anticodon. It has the sequence 5-pyrimidine-purine-anticodon-modified purine-any base-3." [ISBN:0716719207]');
INSERT INTO soterms (id, name, definition) VALUES ('001174', 'anticodon', 'A sequence of three nucleotide bases in tRNA which recognizes a codon in mRNA." [RSC:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001175', 'CCA_tail', 'Base sequence at the 3 end of a tRNA. The 3-hydroxyl group on the terminal adenosine is the attachment point for the amino acid." [ISBN:0716719207]');
INSERT INTO soterms (id, name, definition) VALUES ('001176', 'DHU_loop', 'Non-base-paired sequence of nucleotide bases in tRNA. It contains several dihydrouracil residues." [ISBN:071671920]');
INSERT INTO soterms (id, name, definition) VALUES ('001177', 'T_loop', 'Non-base-paired sequence of three nucleotide bases in tRNA. It has sequence T-Psi-C." [ISBN:0716719207]');
INSERT INTO soterms (id, name, definition) VALUES ('001178', 'pyrrolysine_tRNA_primary_transcript', 'A primary transcript encoding pyrrolysyl tRNA (SO:0000766)." [RSC:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001179', 'U3_snoRNA', 'U3 snoRNA is a member of the box C/D class of small nucleolar RNAs. The U3 snoRNA secondary structure is characterised by a small 5 domain (with boxes A and A), and a larger 3 domain (with boxes B, C, C, and D), the two domains being linked by a single-stranded hinge. Boxes B and C form the B/C motif, which appears to be exclusive to U3 snoRNAs, and boxes C and D form the C/D motif. The latter is functionally similar to the C/D motifs found in other snoRNAs. The 5 domain and the hinge region act as a pre-rRNA-binding domain. The 3 domain has conserved protein-binding sites. Both the box B/C and box C/D motifs are sufficient for nuclear retention of U3 snoRNA. The box C/D motif is also necessary for nucleolar localization, stability and hypermethylation of U3 snoRNA. Both box B/C and C/D motifs are involved in specific protein interactions and are necessary for the rRNA processing functions of U3 snoRNA." [http://www.sanger.ac.uk/cgi-bin/Rfam/getacc?RF00012]');
INSERT INTO soterms (id, name, definition) VALUES ('001180', 'AU_rich_element', 'A cis-acting element found in the 3 UTR of some mRNA which is rich in AUUUA pentamers. Messenger RNAs bearing multiple AU-rich elements are often unstable." [PMID:7892223]');
INSERT INTO soterms (id, name, definition) VALUES ('001181', 'Bruno_response_element', 'A cis-acting element found in the 3 UTR of some mRNA which is bound by the Drosophila Bruno protein and its homologs." [PMID:10893231]');
INSERT INTO soterms (id, name, definition) VALUES ('001182', 'iron_responsive_element', 'A regulatory sequence found in the 5 and 3 UTRs of many mRNAs which encode iron-binding proteins. It has a hairpin structure and is recognized by trans-acting proteins known as iron-regulatory proteins." [PMID:3198610, PMID:8710843]');
INSERT INTO soterms (id, name, definition) VALUES ('001183', 'morpholino_backbone', 'An attribute describing a sequence composed of nucleobases bound to a morpholino backbone. A morpholino backbone consists of morpholine (CHEBI:34856) rings connected by phosphorodiamidate linkages." [RSC:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001184', 'PNA', 'An attribute describing a sequence composed of peptide nucleic acid (CHEBI:48021), a chemical consisting of nucleobases bound to a backbone composed of repeating N-(2-aminoethyl)-glycine units linked by peptide bonds. The purine and pyrimidine bases are linked to the backbone by methylene carbonyl bonds." [RSC:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001185', 'enzymatic', 'An attribute describing the sequence of a transcript that has catalytic activity with or without an associated ribonucleoprotein." [RSC:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001186', 'ribozymic', 'An attribute describing the sequence of a transcript that has catalytic activity even without an associated ribonucleoprotein." [RSC:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001187', 'pseudouridylation_guide_snoRNA', 'A snoRNA that specifies the site of pseudouridylation in an RNA molecule by base pairing with a short sequence around the target residue." [GOC:mah, PMID:12457565]');
INSERT INTO soterms (id, name, definition) VALUES ('001188', 'LNA', 'An attribute describing a sequence consisting of nucleobases attached to a repeating unit made of locked deoxyribose rings connected to a phosphate backbone. The deoxyribose units conformation is locked by a 2-C,4-C-oxymethylene link." [CHEBI:48010]');
INSERT INTO soterms (id, name, definition) VALUES ('001189', 'LNA_oligo', 'An oligo composed of LNA residues." [RSC:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001190', 'TNA', 'An attribute describing a sequence consisting of nucleobases attached to a repeating unit made of threose rings connected to a phosphate backbone." [CHEBI:48019]');
INSERT INTO soterms (id, name, definition) VALUES ('001191', 'TNA_oligo', 'An oligo composed of TNA residues." [RSC:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001192', 'GNA', 'An attribute describing a sequence consisting of nucleobases attached to a repeating unit made of an acyclic three-carbon propylene glycol connected to a phosphate backbone. It has two enantiomeric forms, (R)-GNA and (S)-GNA." [CHEBI:48015]');
INSERT INTO soterms (id, name, definition) VALUES ('001193', 'GNA_oligo', 'An oligo composed of GNA residues." [RSC:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001194', 'R_GNA', 'An attribute describing a GNA sequence in the (R)-GNA enantiomer." [CHEBI:48016]');
INSERT INTO soterms (id, name, definition) VALUES ('001195', 'R_GNA_oligo', 'An oligo composed of (R)-GNA residues." [RSC:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001196', 'S_GNA', 'An attribute describing a GNA sequence in the (S)-GNA enantiomer." [CHEBI:48017]');
INSERT INTO soterms (id, name, definition) VALUES ('001197', 'S_GNA_oligo', 'An oligo composed of (S)-GNA residues." [RSC:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('001198', 'ds_DNA_viral_sequence', 'A ds_DNA_viral_sequence is a viral_sequence that is the sequence of a virus that exists as double stranded DNA." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001199', 'ss_RNA_viral_sequence', 'A ss_RNA_viral_sequence is a viral_sequence that is the sequence of a virus that exists as single stranded RNA." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001200', 'negative_sense_ssRNA_viral_sequence', 'A negative_sense_RNA_viral_sequence is a ss_RNA_viral_sequence that is the sequence of a single stranded RNA virus that is complementary to mRNA and must be converted to positive sense RNA by RNA polymerase before translation." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001201', 'positive_sense_ssRNA_viral_sequence', 'A positive_sense_RNA_viral_sequence is a ss_RNA_viral_sequence that is the sequence of a single stranded RNA virus that can be immediately translated by the host." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001202', 'ambisense_ssRNA_viral_sequence', 'A ambisense_RNA_virus is a ss_RNA_viral_sequence that is the sequence of a single stranded RNA virus with both messenger and anti messenger polarity." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001203', 'RNA_polymerase_promoter', 'A region (DNA) to which RNA polymerase binds, to begin transcription." [xenbase:jb]');
INSERT INTO soterms (id, name, definition) VALUES ('001204', 'Phage_RNA_Polymerase_Promoter', 'A region (DNA) to which Bacteriophage RNA polymerase binds, to begin transcription." [xenbase:jb]');
INSERT INTO soterms (id, name, definition) VALUES ('001205', 'SP6_RNA_Polymerase_Promoter', 'A region (DNA) to which the SP6 RNA polymerase binds, to begin transcription." [xenbase:jb]');
INSERT INTO soterms (id, name, definition) VALUES ('001206', 'T3_RNA_Polymerase_Promoter', 'A DNA sequence to which the T3 RNA polymerase binds, to begin transcription." [xenbase:jb]');
INSERT INTO soterms (id, name, definition) VALUES ('001207', 'T7_RNA_Polymerase_Promoter', 'A region (DNA) to which the T7 RNA polymerase binds, to begin transcription." [xenbase:jb]');
INSERT INTO soterms (id, name, definition) VALUES ('001208', 'five_prime_EST', 'An EST read from the 5 end of a transcript that usually codes for a protein. These regions tend to be conserved across species and do not change much within a gene family." [http://www.ncbi.nlm.nih.gov/About/primer/est.html]');
INSERT INTO soterms (id, name, definition) VALUES ('001209', 'three_prime_EST', 'An EST read from the 3 end of a transcript. They are more likely to fall within non-coding, or untranslated regions(UTRs)." [http://www.ncbi.nlm.nih.gov/About/primer/est.html]');
INSERT INTO soterms (id, name, definition) VALUES ('001210', 'translational_frameshift', 'The region of mRNA (not divisible by 3 bases) that is skipped during the process of translational frameshifting (GO:0006452), causing the reading frame to be different." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001211', 'plus_1_translational_frameshift', 'The region of mRNA 1 base long that is skipped during the process of translational frameshifting (GO:0006452), causing the reading frame to be different." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001212', 'plus_2_translational_frameshift', 'The region of mRNA 2 bases long that is skipped during the process of translational frameshifting (GO:0006452), causing the reading frame to be different." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001213', 'group_III_intron', 'Group III introns are introns found in the mRNA of the plastids of euglenoid protists. They are spliced by a two step transesterification with bulged adenosine as initiating nucleophile." [PMID:11377794]');
INSERT INTO soterms (id, name, definition) VALUES ('001214', 'noncoding_region_of_exon', 'The maximal intersection of exon and UTR." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001215', 'coding_region_of_exon', 'The region of an exon that encodes for protein sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001216', 'endonuclease_spliced_intron', 'An intron that spliced via endonucleolytic cleavage and ligation rather than transesterification." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001217', 'protein_coding_gene', 'An intron that spliced via endonucleolytic cleavage and ligation rather than transesterification." [SO:ke]');
