INSERT INTO soterms (id, name, definition) VALUES ('001249', 'fragment_assembly', 'A fragment assembly is a genome assembly that orders overlapping fragments of the genome based on landmark sequences. The base pair distance between the landmarks is known allowing additivity of lengths." [SO:ke]');
