INSERT INTO soterms (id, name, definition) VALUES ('001554', 'polypeptide_function_variant', 'A sequence variant which changes polypeptide functioning with respect to a reference sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001555', 'decreased_translational_product_level', 'A sequence variant which decreases the translational product level with respect to a reference sequence." [SO:ke]');
