INSERT INTO soterms (id, name, definition) VALUES ('001571', 'cryptic_splice_donor', 'A sequence variant whereby a new splice site is created due to the activation of a new donor." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001572', 'exon_loss_variant', 'A sequence variant whereby an exon is lost from the transcript." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001573', 'intron_gain_variant', 'A sequence variant whereby an intron is gained by the processed transcript; usually a result of an alteration of the donor or acceptor." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('001574', 'splice_acceptor_variant', 'A splice variant that changes the 2 base region at the 3 end of an intron." [SO:ke]');
