INSERT INTO soterms (id, name, definition) VALUES ('001583', 'missense_variant', 'A sequence variant, that changes one or more bases, resulting in a different amino acid sequence but where the length is preserved." [EBI:fc, EBI:gr, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001585', 'conservative_missense_variant', 'A sequence variant whereby at least one base of a codon is changed resulting in a codon that encodes for a different but similar amino acid. These variants may or may not be deleterious." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001586', 'non_conservative_missense_variant', 'A sequence variant whereby at least one base of a codon is changed resulting in a codon that encodes for an amino acid with different biochemical properties." [SO:ke]');
