INSERT INTO soterms (id, name, definition) VALUES ('001593', 'minus_2_frameshift_variant', 'A sequence variant which causes a disruption of the translational reading frame, by shifting one base ahead." [http://arjournals.annualreviews.org/doi/pdf/10.1146/annurev.ge.08.120174.001535]');
