INSERT INTO soterms (id, name, definition) VALUES ('001754', 'SL9_acceptor_site', 'A SL2_acceptor_site which appends the SL9 RNA leader sequence to the 5 end of an mRNA. SL9 acceptor sites occur in genes in internal segments of polycistronic transcripts." [SO:nlw]');
INSERT INTO soterms (id, name, definition) VALUES ('001755', 'SL10_acceptor_site', 'A SL2_acceptor_site which appends the SL10 RNA leader sequence to the 5 end of an mRNA. SL10 acceptor sites occur in genes in internal segments of polycistronic transcripts." [SO:nlw]');
INSERT INTO soterms (id, name, definition) VALUES ('001756', 'SL11_acceptor_site', 'A SL2_acceptor_site which appends the SL11 RNA leader sequence to the 5 end of an mRNA. SL11 acceptor sites occur in genes in internal segments of polycistronic transcripts." [SO:nlw]');
INSERT INTO soterms (id, name, definition) VALUES ('001757', 'SL12_acceptor_site', 'A SL2_acceptor_site which appends the SL12 RNA leader sequence to the 5 end of an mRNA. SL12 acceptor sites occur in genes in internal segments of polycistronic transcripts." [SO:nlw]');
INSERT INTO soterms (id, name, definition) VALUES ('001758', 'duplicated_pseudogene', 'A pseudogene that arose via gene duplication. Generally duplicated pseudogenes have the same structure as the original gene, including intron-exon structure and some regulatory sequence." [http://en.wikipedia.org/wiki/Pseudogene]');
INSERT INTO soterms (id, name, definition) VALUES ('001759', 'unitary_pseudogene', 'A pseudogene, deactivated from original state by mutation, fixed in a population,where the ortholog in a reference species such as mouse remains functional." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html, http://en.wikipedia.org/wiki/Pseudogene, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001760', 'non_processed_pseudogene', 'A pseudogene that arose from a means other than retrotransposition. A pseudogene created via genomic duplication of a functional protein-coding parent gene followed by accumulation of deleterious mutations." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001761', 'variant_quality', 'A dependent entity that inheres in a bearer, a sequence variant." [PMID:17597783, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001762', 'variant_origin', 'A quality inhering in a variant by virtue of its origin." [PMID:17597783, SO:ke]');
