INSERT INTO soterms (id, name, definition) VALUES ('001781', 'de_novo_variant', 'A quality inhering in a variant by virtue of its phenotype." [PMID:17597783, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001782', 'TF_binding_site_variant', 'A sequence variant located within a transcription factor binding site." [EBI:fc]');
INSERT INTO soterms (id, name, definition) VALUES ('001784', 'complex_structural_alteration', 'A structural sequence alteration or rearrangement encompassing one or more genome fragments, with 4 or more breakpoints." [FB:reference_manual, NCBI:th, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001785', 'structural_alteration', 'A structural sequence alteration or rearrangement encompassing one or more genome fragments, with 4 or more breakpoints." [FB:reference_manual, NCBI:th, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001786', 'loss_of_heterozygosity', 'A functional variant whereby the sequence alteration causes a loss of function of one allele of a gene." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001787', 'splice_donor_5th_base_variant', 'A sequence variant that causes a change at the 5th base pair after the start of the intron in the orientation of the transcript." [EBI:gr]');
INSERT INTO soterms (id, name, definition) VALUES ('001788', 'U_box', 'An U-box is a conserved T-rich region upstream of a retroviral polypurine tract that is involved in PPT primer creation during reverse transcription." [PMID:10556309, PMID:11577982, PMID:9649446]');
INSERT INTO soterms (id, name, definition) VALUES ('001789', 'mating_type_region', 'A specialized region in the genomes of some yeast and fungi, the genes of which regulate mating type." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001790', 'paired_end_fragment', 'An assembly region that has been sequenced from both ends resulting in a read_pair (mate_pair)." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001791', 'exon_variant', 'A sequence variant that changes exon sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001792', 'non_coding_transcript_exon_variant', 'A sequence variant that changes non-coding exon sequence in a non-coding transcript." [EBI:fc, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001793', 'clone_end', 'A read from an end of the clone sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001794', 'point_centromere', 'A point centromere is a relatively small centromere (about 125 bp DNA) in discrete sequence, found in some yeast including S. cerevisiae." [PMID:7502067, SO:vw]');
INSERT INTO soterms (id, name, definition) VALUES ('001795', 'regional_centromere', 'A regional centromere is a large modular centromere found in fission yeast and higher eukaryotes. It consist of a central core region flanked by inverted inner and outer repeat regions." [PMID:7502067, SO:vw]');
INSERT INTO soterms (id, name, definition) VALUES ('001796', 'regional_centromere_central_core', 'A conserved region within the central region of a modular centromere, where the kinetochore is formed." [SO:vw]');
INSERT INTO soterms (id, name, definition) VALUES ('001797', 'centromeric_repeat', 'A repeat region found within the modular centromere." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001798', 'regional_centromere_inner_repeat_region', 'The inner inverted repeat region of a modular centromere and part of the central core surrounding a non-conserved central region. This region is adjacent to the central core, on each chromosome arm." [SO:vw]');
INSERT INTO soterms (id, name, definition) VALUES ('001799', 'regional_centromere_outer_repeat_region', 'The heterochromatic outer repeat region of a modular centromere. These repeats exist in tandem arrays on both chromosome arms." [SO:vw]');
INSERT INTO soterms (id, name, definition) VALUES ('001800', 'tasiRNA', 'The sequence of a 21 nucleotide double stranded, polyadenylated non coding RNA, transcribed from the TAS gene." [PMID:16145017]');
INSERT INTO soterms (id, name, definition) VALUES ('001801', 'tasiRNA_primary_transcript', 'A primary transcript encoding a tasiRNA." [PMID:16145017]');
INSERT INTO soterms (id, name, definition) VALUES ('001802', 'increased_polyadenylation_variant', 'A transcript processing variant whereby polyadenylation of the encoded transcript is increased with respect to the reference." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001803', 'decreased_polyadenylation_variant', 'A transcript processing variant whereby polyadenylation of the encoded transcript is decreased with respect to the reference." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001804', 'DDB_box', 'A conserved polypeptide motif that mediates protein-protein interaction and defines adaptor proteins for DDB1/cullin 4 ubiquitin ligases." [PMID:18794354, PMID:19818632]');
INSERT INTO soterms (id, name, definition) VALUES ('001805', 'destruction_box', 'A conserved polypeptide motif that can be recognized by both Fizzy/Cdc20- and FZR/Cdh1-activated anaphase-promoting complex/cyclosome (APC/C) and targets a protein for ubiquitination and subsequent degradation by the APC/C. The consensus sequence is RXXLXXXXN." [PMID:12208841, PMID:1842691]');
INSERT INTO soterms (id, name, definition) VALUES ('001806', 'ER_retention_signal', 'A C-terminal tetrapeptide motif that mediates retention of a protein in (or retrieval to) the endoplasmic reticulum. In mammals the sequence is KDEL, and in fungi HDEL or DDEL." [doi:10.1093/jxb/50.331.157, PMID:2077689]');
INSERT INTO soterms (id, name, definition) VALUES ('001807', 'KEN_box', 'A conserved polypeptide motif that can be recognized by FZR/Cdh1-activated anaphase-promoting complex/cyclosome (APC/C) and targets a protein for ubiquitination and subsequent degradation by the APC/C. The consensus sequence is KENXXXN." [PMID:10733526, PMID:1220884, PMID:18426916]');
INSERT INTO soterms (id, name, definition) VALUES ('001808', 'mitochondrial_targeting_signal', 'A polypeptide region that targets a polypeptide to the mitochondrion." [PomBase:mah]');
INSERT INTO soterms (id, name, definition) VALUES ('001809', 'signal_anchor', 'A signal sequence that is not cleaved from the polypeptide. Anchors a Type II membrane protein to the membrane." [http://www.cbs.dtu.dk/services/SignalP/background/biobackground.php]');
INSERT INTO soterms (id, name, definition) VALUES ('001810', 'PIP_box', 'A polypeptide region that mediates binding to PCNA. The consensus sequence is QXX(hh)XX(aa), where (h) denotes residues with moderately hydrophobic side chains and (a) denotes residues with highly hydrophobic aromatic side chains." [PMID:9631646]');
INSERT INTO soterms (id, name, definition) VALUES ('001811', 'phosphorylation_site', 'A post-translationally modified region in which residues of the protein are modified by phosphorylation." [PomBase:mah]');
INSERT INTO soterms (id, name, definition) VALUES ('001812', 'transmembrane_helix', 'A region that traverses the lipid bilayer and adopts a helical secondary structure." [PomBase:mah]');
INSERT INTO soterms (id, name, definition) VALUES ('001813', 'vacuolar_sorting_signal', 'A polypeptide region that targets a polypeptide to the vacuole." [PomBase:mah]');
INSERT INTO soterms (id, name, definition) VALUES ('001814', 'coding_variant_quality', 'A polypeptide region that targets a polypeptide to the vacuole." [PomBase:mah]');
INSERT INTO soterms (id, name, definition) VALUES ('001815', 'synonymous', 'A polypeptide region that targets a polypeptide to the vacuole." [PomBase:mah]');
INSERT INTO soterms (id, name, definition) VALUES ('001816', 'non_synonymous', 'A polypeptide region that targets a polypeptide to the vacuole." [PomBase:mah]');
INSERT INTO soterms (id, name, definition) VALUES ('001817', 'inframe', 'An attribute describing a sequence that contains a mutation involving the deletion or insertion of one or more bases, where this number is divisible by 3." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001818', 'protein_altering_variant', 'A sequence_variant which is predicted to change the protein encoded in the coding sequence." [EBI:gr]');
INSERT INTO soterms (id, name, definition) VALUES ('001819', 'synonymous_variant', 'A sequence variant where there is no resulting change to the encoded amino acid." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001820', 'inframe_indel', 'A coding sequence variant where the change does not alter the frame of the transcript." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001821', 'inframe_insertion', 'An inframe non synonymous variant that inserts bases into in the coding sequence." [EBI:gr]');
INSERT INTO soterms (id, name, definition) VALUES ('001822', 'inframe_deletion', 'An inframe non synonymous variant that deletes bases from the coding sequence." [EBI:gr]');
INSERT INTO soterms (id, name, definition) VALUES ('001823', 'conservative_inframe_insertion', 'An inframe increase in cds length that inserts one or more codons into the coding sequence between existing codons." [EBI:gr]');
INSERT INTO soterms (id, name, definition) VALUES ('001824', 'disruptive_inframe_insertion', 'An inframe increase in cds length that inserts one or more codons into the coding sequence within an existing codon." [EBI:gr]');
INSERT INTO soterms (id, name, definition) VALUES ('001825', 'conservative_inframe_deletion', 'An inframe decrease in cds length that deletes one or more entire codons from the coding sequence but does not change any remaining codons." [EBI:gr]');
INSERT INTO soterms (id, name, definition) VALUES ('001826', 'disruptive_inframe_deletion', 'An inframe decrease in cds length that deletes bases from the coding sequence starting within an existing codon." [EBI:gr]');
INSERT INTO soterms (id, name, definition) VALUES ('001827', 'mRNA_read', 'A sequencer read of an mRNA substrate." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001828', 'genomic_DNA_read', 'A sequencer read of a genomic DNA substrate." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001829', 'mRNA_contig', 'A contig composed of mRNA_reads." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001830', 'AFLP_fragment', 'A PCR product obtained by applying the AFLP technique, based on a restriction enzyme digestion of genomic DNA and an amplification of the resulting fragments." [GMOD:ea]');
INSERT INTO soterms (id, name, definition) VALUES ('001831', 'protein_hmm_match', 'A match to a protein HMM such as pfam." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001832', 'immunoglobulin_region', 'A region of immunoglobulin sequence, either constant or variable." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001833', 'V_region', 'The variable region of an immunoglobulin polypeptide sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001834', 'C_region', 'The constant region of an immunoglobulin polypeptide sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001835', 'N_region', 'Extra nucleotides inserted between rearranged immunoglobulin segments." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001836', 'S_region', 'The switch region of immunoglobulin heavy chains; it is involved in the rearrangement of heavy chain DNA leading to the expression of a different immunoglobulin classes from the same B-cell." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001837', 'mobile_element_insertion', 'A kind of insertion where the inserted sequence is a mobile element." [EBI:dvga]');
INSERT INTO soterms (id, name, definition) VALUES ('001838', 'novel_sequence_insertion', 'An insertion the sequence of which cannot be mapped to the reference genome." [NCBI:th]');
INSERT INTO soterms (id, name, definition) VALUES ('001839', 'CSL_response_element', 'A promoter element with consensus sequence GTGRGAA, bound by CSL (CBF1/RBP-JK/Suppressor of Hairless/LAG-1) transcription factors." [PMID:19101542]');
INSERT INTO soterms (id, name, definition) VALUES ('001840', 'GATA_box', 'A GATA transcription factor element containing the consensus sequence WGATAR (in which W indicates A/T and R indicates A/G)." [PMID:8321208]');
INSERT INTO soterms (id, name, definition) VALUES ('001841', 'polymorphic_pseudogene', 'A pseudogene in the reference genome, though known to be intact in the genomes of other individuals of the same species. The annotation process has confirmed that the pseudogenisation event is not a genomic sequencing error." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html, JAX:hd]');
INSERT INTO soterms (id, name, definition) VALUES ('001842', 'AP_1_binding_site', 'A promoter element with consensus sequence TGACTCA, bound by AP-1 and related transcription factors." [PMID:1899230, PMID:3034432, PMID:3125983]');
INSERT INTO soterms (id, name, definition) VALUES ('001843', 'CRE', 'A promoter element with consensus sequence TGACGTCA; bound by the ATF/CREB family of transcription factors." [PMID:11483355, PMID:11483993]');
INSERT INTO soterms (id, name, definition) VALUES ('001844', 'CuRE', 'A promoter element bound by copper ion-sensing transcription factors such as S. cerevisiae Mac1p or S. pombe Cuf1; the consensus sequence is HTHNNGCTGD (more specifically TTTGCKCR in budding yeast)." [PMID:10593913, PMID:9188496, PMID:9211922]');
INSERT INTO soterms (id, name, definition) VALUES ('001845', 'DRE', 'A promoter element with consensus sequence CGWGGWNGMM, bound by transcription factors related to RecA and found in promoters of genes expressed following several types of DNA damage or inhibition of DNA synthesis." [PMID:11073995, PMID:8668127]');
INSERT INTO soterms (id, name, definition) VALUES ('001846', 'FLEX_element', 'A promoter element that has consensus sequence GTAAACAAACAAAM and contains a heptameric core GTAAACA, bound by transcription factors with a forkhead DNA-binding domain." [PMID:10747048, PMID:14871934]');
INSERT INTO soterms (id, name, definition) VALUES ('001847', 'forkhead_motif', 'A promoter element with consensus sequence TTTRTTTACA, bound by transcription factors with a forkhead DNA-binding domain." [PMID:15195092]');
INSERT INTO soterms (id, name, definition) VALUES ('001848', 'homol_D_box', 'A core promoter element that has the consensus sequence CAGTCACA (or its inverted form TGTGACTG), and plays the role of a TATA box in promoters that do not contain a canonical TATA sequence." [PMID:21673110, PMID:7501449, PMID:8458332]');
INSERT INTO soterms (id, name, definition) VALUES ('001849', 'homol_E_box', 'A core promoter element that has the consensus sequence ACCCTACCCT (or its inverted form AGGGTAGGGT), and is found near the homol D box in some promoters that use a homol D box instead of a canonical TATA sequence." [PMID:7501449]');
INSERT INTO soterms (id, name, definition) VALUES ('001850', 'HSE', 'A promoter element that consists of at least three copies of the pentanucleotide NGAAN, bound by the heat shock transcription factor HSF." [PMID:17347150, PMID:8689565]');
INSERT INTO soterms (id, name, definition) VALUES ('001851', 'iron_repressed_GATA_element', 'A GATA promoter element with consensus sequence WGATAA, found in promoters of genes repressed in the presence of iron." [PMID:11956219, PMID:17211681]');
INSERT INTO soterms (id, name, definition) VALUES ('001852', 'mating_type_M_box', 'A promoter element with consensus sequence ACAAT, found in promoters of mating type M-specific genes in fission yeast and bound by the transcription factor Mat1-Mc." [PMID:9233811]');
INSERT INTO soterms (id, name, definition) VALUES ('001853', 'androgen_response_element', 'A non-palindromic sequence found in the promoters of genes whose expression is regulated in response to androgen." [PMID:21796522]');
INSERT INTO soterms (id, name, definition) VALUES ('001854', 'smFISH_probe', 'A smFISH is a probe that binds RNA in a single molecule in situ hybridization experiment." [PMID:18806792]');
INSERT INTO soterms (id, name, definition) VALUES ('001855', 'MCB', 'A promoter element with consensus sequence ACGCGT, bound by the transcription factor complex MBF (MCB-binding factor) and found in promoters of genes expressed during the G1/S transition of the cell cycle." [PMID:16285853]');
INSERT INTO soterms (id, name, definition) VALUES ('001856', 'CCAAT_motif', 'A promoter element with consensus sequence CCAAT, bound by a protein complex that represses transcription in response to low iron levels." [PMID:16963626]');
INSERT INTO soterms (id, name, definition) VALUES ('001857', 'Ace2_UAS', 'A promoter element with consensus sequence CCAGCC, bound by the fungal transcription factor Ace2." [PMID:16678171]');
INSERT INTO soterms (id, name, definition) VALUES ('001858', 'TR_box', 'A promoter element with consensus sequence TTCTTTGTTY, bound an HMG-box transcription factor such as S. pombe Ste11, and found in promoters of genes up-regulated early in meiosis." [PMID:1657709]');
INSERT INTO soterms (id, name, definition) VALUES ('001859', 'STREP_motif', 'A promoter element with consensus sequence CCCCTC, bound by the PKA-responsive zinc finger transcription factor Rst2." [PMID:11739717]');
INSERT INTO soterms (id, name, definition) VALUES ('001860', 'rDNA_intergenic_spacer_element', 'A DNA motif that contains a core consensus sequence AGGTAAGGGTAATGCAC, is found in the intergenic regions of rDNA repeats, and is bound by an RNA polymerase I transcription termination factor (e.g. S. pombe Reb1)." [PMID:9016645]');
INSERT INTO soterms (id, name, definition) VALUES ('001861', 'sterol_regulatory_element', 'A 10-bp promoter element bound by sterol regulatory element binding proteins (SREBPs), found in promoters of genes involved in sterol metabolism. Many variants of the sequence ATCACCCCAC function as SREs." [GO:mah, PMID:11111080, PMID:16537923]');
INSERT INTO soterms (id, name, definition) VALUES ('001862', 'GT_dinucleotide_repeat', 'A dinucleotide repeat region composed of GT repeating elements." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001863', 'GTT_trinucleotide_repeat', 'A trinucleotide repeat region composed of GTT repeating elements." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001864', 'Sap1_recognition_motif', 'A DNA motif to which the S. pombe Sap1 protein binds. The consensus sequence is 5-TARGCAGNTNYAACGMG-3; it is found at the mating type locus, where it is important for mating type switching, and at replication fork barriers in rDNA repeats." [PMID:16166653, PMID:7651412]');
INSERT INTO soterms (id, name, definition) VALUES ('001865', 'CDRE_motif', 'An RNA polymerase II promoter element found in the promoters of genes regulated by calcineurin. The consensus sequence is GNGGCKCA." [PMID:16928959]');
INSERT INTO soterms (id, name, definition) VALUES ('001866', 'BAC_read_contig', 'A contig of BAC reads." [GMOD:ea]');
INSERT INTO soterms (id, name, definition) VALUES ('001867', 'candidate_gene', 'A gene suspected of being involved in the expression of a trait." [GMOD:ea]');
INSERT INTO soterms (id, name, definition) VALUES ('001868', 'positional_candidate_gene', 'A candidate gene whose association with a trait is based on the genes location on a chromosome." [GMOD:ea]');
INSERT INTO soterms (id, name, definition) VALUES ('001869', 'functional_candidate_gene', 'A candidate gene whose function has something in common biologically with the trait under investigation." [GMOD:ea]');
INSERT INTO soterms (id, name, definition) VALUES ('001870', 'enhancerRNA', 'A short ncRNA that is transcribed from an enhancer. May have a regulatory function." [doi:10.1038/465173a, SO:cjm]');
INSERT INTO soterms (id, name, definition) VALUES ('001871', 'PCB', 'A promoter element with consensus sequence GNAACR, bound by the transcription factor complex PBF (PCB-binding factor) and found in promoters of genes expressed during the M/G1 transition of the cell cycle." [GO:mah, PMID:12411492]');
INSERT INTO soterms (id, name, definition) VALUES ('001872', 'rearrangement_region', 'A region of a chromosome, where the chromosome has undergone a large structural rearrangement that altered the genome organization. There is no longer synteny to the reference genome." [NCBI:th, PMID:18564416]');
INSERT INTO soterms (id, name, definition) VALUES ('001873', 'interchromosomal_breakpoint', 'A rearrangement breakpoint between two different chromosomes." [NCBI:th]');
INSERT INTO soterms (id, name, definition) VALUES ('001874', 'intrachromosomal_breakpoint', 'A rearrangement breakpoint within the same chromosome." [NCBI:th]');
INSERT INTO soterms (id, name, definition) VALUES ('001875', 'unassigned_supercontig', 'A supercontig that is not been assigned to any ultracontig during a genome assembly project." [GMOD:ea]');
INSERT INTO soterms (id, name, definition) VALUES ('001876', 'partial_genomic_sequence_assembly', 'A partial DNA sequence assembly of a chromosome or full genome, which contains gaps that are filled with Ns." [GMOD:ea]');
INSERT INTO soterms (id, name, definition) VALUES ('001877', 'lnc_RNA', 'A non-coding RNA over 200nucleotides in length." [HGNC:mw]');
INSERT INTO soterms (id, name, definition) VALUES ('001878', 'feature_variant', 'A sequence variant that falls entirely or partially within a genomic feature." [EBI:fc, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001879', 'feature_ablation', 'A sequence variant, caused by an alteration of the genomic sequence, where the deletion, is greater than the extent of the underlying genomic features." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001880', 'feature_amplification', 'A sequence variant, caused by an alteration of the genomic sequence, where the structural change, an amplification of sequence, is greater than the extent of the underlying genomic features." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001881', 'feature_translocation', 'A sequence variant, caused by an alteration of the genomic sequence, where the structural change, a translocation, is greater than the extent of the underlying genomic features." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001882', 'feature_fusion', 'A sequence variant, caused by an alteration of the genomic sequence, where a deletion fuses genomic features." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001883', 'transcript_translocation', 'A feature translocation where the region contains a transcript." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001884', 'regulatory_region_translocation', 'A feature translocation where the region contains a regulatory region." [SO:ke]');
