INSERT INTO soterms (id, name, definition) VALUES ('001886', 'transcript_fusion', 'A feature fusion where the deletion brings together transcript regions." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001887', 'regulatory_region_fusion', 'A feature fusion where the deletion brings together regulatory regions." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001888', 'TFBS_fusion', 'A fusion where the deletion brings together transcription factor binding sites." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001889', 'transcript_amplification', 'A feature amplification of a region containing a transcript." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001890', 'transcript_regulatory_region_fusion', 'A feature fusion where the deletion brings together a regulatory region and a transcript region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001891', 'regulatory_region_amplification', 'A feature amplification of a region containing a regulatory region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001892', 'TFBS_amplification', 'A feature amplification of a region containing a transcription factor binding site." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001893', 'transcript_ablation', 'A feature ablation whereby the deleted region includes a transcript feature." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001894', 'regulatory_region_ablation', 'A feature ablation whereby the deleted region includes a regulatory region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001895', 'TFBS_ablation', 'A feature ablation whereby the deleted region includes a transcription factor binding site." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001896', 'transposable_element_CDS', 'A CDS that is part of a transposable element." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001897', 'transposable_element_pseudogene', 'A pseudogene contained within a transposable element." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001898', 'dg_repeat', 'A repeat region which is part of the regional centromere outer repeat region." [PMID:16407326, SO:vw]');
INSERT INTO soterms (id, name, definition) VALUES ('001899', 'dh_repeat', 'A repeat region which is part of the regional centromere outer repeat region." [PMID:16407326, SO:vw]');
INSERT INTO soterms (id, name, definition) VALUES ('001900', 'M26_binding_site', 'A promoter element that contains a core sequence TGACGT, bound by a protein complex that regulates transcription of genes encoding PKA pathway components." [PMID:15448137]');
INSERT INTO soterms (id, name, definition) VALUES ('001901', 'AACCCT_box', 'A conserved 17-bp sequence (5-ATCA(C/A)AACCCTAACCCT-3) commonly present upstream of the start site of histone transcription units functioning as a transcription factor binding site." [PMID:17452352, PMID:4092687]');
INSERT INTO soterms (id, name, definition) VALUES ('001902', 'splice_region', 'A region surrounding a cis_splice site, either within 1-3 bases of the exon or 3-8 bases of the intron." [SO:bm]');
INSERT INTO soterms (id, name, definition) VALUES ('001903', 'intronic_lncRNA', 'A lnc_RNA totally contained within an intron." [PMID:19071207, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001904', 'antisense_lncRNA', 'Non-coding RNA transcribed from the opposite DNA strand compared with other transcripts and overlap in part with sense RNA." [PMID:19638999]');
INSERT INTO soterms (id, name, definition) VALUES ('001905', 'regional_centromere_outer_repeat_transcript', 'A transcript that is transcribed from the outer repeat region of a regional centromere." [PomBase:mah]');
INSERT INTO soterms (id, name, definition) VALUES ('001906', 'feature_truncation', 'A sequence variant that causes the reduction of a genomic feature, with regard to the reference sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001907', 'feature_elongation', 'A sequence variant that causes the extension of a genomic feature, with regard to the reference sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001908', 'internal_feature_elongation', 'A sequence variant that causes the extension of a genomic feature from within the feature rather than from the terminus of the feature, with regard to the reference sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001909', 'frameshift_elongation', 'A frameshift variant that causes the translational reading frame to be extended relative to the reference feature." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001910', 'frameshift_truncation', 'A frameshift variant that causes the translational reading frame to be shortened relative to the reference feature." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001911', 'copy_number_increase', 'A sequence variant where copies of a feature are increased relative to the reference." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001912', 'copy_number_decrease', 'A sequence variant where copies of a feature are decreased relative to the reference." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001913', 'bacterial_RNApol_promoter_sigma_ecf', 'A bacterial promoter with sigma ecf factor binding dependency. This is a type of bacterial promoters that requires a sigma ECF factor to bind to identified -10 and -35 sequence regions in order to mediate binding of the RNA polymerase to the promoter region as part of transcription initiation." [Invitrogen:kc]');
INSERT INTO soterms (id, name, definition) VALUES ('001914', 'rDNA_replication_fork_barrier', 'A DNA motif that is found in eukaryotic rDNA repeats, and is a site of replication fork pausing." [PMID:14645529]');
INSERT INTO soterms (id, name, definition) VALUES ('001915', 'transcription_start_cluster', 'A region defined by a cluster of experimentally determined transcription starting sites." [PMID:19624849, PMID:21372179, SO:andrewgibson]');
INSERT INTO soterms (id, name, definition) VALUES ('001916', 'CAGE_tag', 'A CAGE tag is a sequence tag hat corresponds to 5 ends of mRNA at cap sites, produced by cap analysis gene expression and used to identify transcriptional start sites." [SO:andrewgibson]');
INSERT INTO soterms (id, name, definition) VALUES ('001917', 'CAGE_cluster', 'A kind of transcription_initiation_cluster defined by the clustering of CAGE tags on a sequence region." [PMID:16645617, SO:andrewgibson]');
INSERT INTO soterms (id, name, definition) VALUES ('001918', '5_methylcytosine', 'A cytosine methylated at the 5 carbon." [SO:rtapella]');
INSERT INTO soterms (id, name, definition) VALUES ('001919', '4_methylcytosine', 'A cytosine methylated at the 4 nitrogen." [SO:rtapella]');
INSERT INTO soterms (id, name, definition) VALUES ('001920', 'N6_methyladenine', 'An adenine methylated at the 6 nitrogen." [SO:rtapella]');
INSERT INTO soterms (id, name, definition) VALUES ('001921', 'mitochondrial_contig', 'A contig of mitochondria derived sequences." [GMOD:ea]');
INSERT INTO soterms (id, name, definition) VALUES ('001922', 'mitochondrial_supercontig', 'A scaffold composed of mitochondrial contigs." [GMOD:ea]');
INSERT INTO soterms (id, name, definition) VALUES ('001923', 'TERRA', 'A non-coding RNA transcript, derived from the transcription of the telomere. These  transcripts contain G rich telomeric RNA repeats and RNA tracts corresponding to adjacent subtelomeric sequences. They are 100-9000 bases long." [PMID:22139915]');
INSERT INTO soterms (id, name, definition) VALUES ('001924', 'ARRET', 'A non coding RNA transcript, complementary to subtelomeric tract of TERRA transcript but devoid of the repeats." [PMID:2139915]');
INSERT INTO soterms (id, name, definition) VALUES ('001925', 'ARIA', 'A non-coding RNA transcript, derived from the transcription of the telomere. These  transcripts consist of C rich repeats." [PMID:22139915]');
INSERT INTO soterms (id, name, definition) VALUES ('001926', 'anti_ARRET', 'A non-coding RNA transcript, derived from the transcription of the telomere. These  transcripts are antisense of ARRET transcripts." [PMID:22139915]');
INSERT INTO soterms (id, name, definition) VALUES ('001927', 'telomeric_transcript', 'A non-coding transcript derived from the transcript of the telomere." [PMID:22139915]');
INSERT INTO soterms (id, name, definition) VALUES ('001928', 'distal_duplication', 'A duplication of the distal region of a chromosome." [SO:bm]');
INSERT INTO soterms (id, name, definition) VALUES ('001929', 'mitochondrial_DNA_read', 'A sequencer read of a mitochondrial DNA sample." [GMOD:ea]');
INSERT INTO soterms (id, name, definition) VALUES ('001930', 'chloroplast_DNA_read', 'A sequencer read of a chloroplast DNA sample." [GMOD:ea]');
INSERT INTO soterms (id, name, definition) VALUES ('001931', 'consensus_gDNA', 'Genomic DNA sequence produced from some base calling or alignment algorithm which uses aligned or assembled multiple gDNA sequences as input." [GMOD:ea]');
INSERT INTO soterms (id, name, definition) VALUES ('001932', 'restriction_enzyme_five_prime_single_strand_overhang', 'A terminal region of DNA sequence where the end of the region is not blunt ended and the exposed single strand terminates at the 5 end." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001933', 'restriction_enzyme_three_prime_single_strand_overhang', 'A terminal region of DNA sequence where the end of the region is not blunt ended and the exposed single strand terminates at the 3 end." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001934', 'monomeric_repeat', 'A repeat_region containing repeat_units of 1 bp that is repeated multiple times in tandem." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001935', 'H3K20_trimethylation_site', 'A kind of histone modification site, whereby the 20th residue (a lysine), from the start of the H3 protein is tri-methylated." [EBI:nj]');
INSERT INTO soterms (id, name, definition) VALUES ('001936', 'H3K36_acetylation_site', 'A kind of histone modification site, whereby the 36th residue (a lysine), from the start of the H3 histone protein is acetylated." [EBI:nj]');
INSERT INTO soterms (id, name, definition) VALUES ('001937', 'H2BK12_acetylation_site', 'A kind of histone modification site, whereby the 12th residue (a lysine), from the start of the H2B protein is acetylated." [EBI:nj]');
INSERT INTO soterms (id, name, definition) VALUES ('001938', 'H2AK5_acetylation_site', 'A kind of histone modification site, whereby the 5th residue (a lysine), from the start of the H2A histone protein is acetylated." [EBI:nj]');
INSERT INTO soterms (id, name, definition) VALUES ('001939', 'H4K12_acetylation_site', 'A kind of histone modification site, whereby the 12th residue (a lysine), from the start of the H4 histone protein is acetylated." [EBI:nj]');
INSERT INTO soterms (id, name, definition) VALUES ('001940', 'H2BK120_acetylation_site', 'A kind of histone modification site, whereby the 120th residue (a lysine), from the start of the H2B histone protein is acetylated." [EBI:nj, http://dx.doi.org/10.4161/epi.6.5.15623]');
INSERT INTO soterms (id, name, definition) VALUES ('001941', 'H4K91_acetylation_site', 'A kind of histone modification site, whereby the 91st residue (a lysine), from the start of the H4 histone protein is acetylated." [EBI:nj]');
INSERT INTO soterms (id, name, definition) VALUES ('001942', 'H2BK20_acetylation_site', 'A kind of histone modification site, whereby the 20th residue (a lysine), from the start of the H2B histone protein is acetylated." [EBI:nj]');
INSERT INTO soterms (id, name, definition) VALUES ('001943', 'H3K4_acetylation_site', 'A kind of histone modification site, whereby the 4th residue (a lysine), from the start of the H3 histone protein is acetylated." [EBI:nj]');
INSERT INTO soterms (id, name, definition) VALUES ('001944', 'H2AK9_acetylation_site', 'A kind of histone modification site, whereby the 9th residue (a lysine), from the start of the H2A histone protein is acetylated." [EBI:nj]');
INSERT INTO soterms (id, name, definition) VALUES ('001945', 'H3K56_acetylation_site', 'A kind of histone modification site, whereby the 56th residue (a lysine), from the start of the H3 histone protein is acetylated." [EBI:nj]');
INSERT INTO soterms (id, name, definition) VALUES ('001946', 'H2BK15_acetylation_site', 'A kind of histone modification site, whereby the 15th residue (a lysine), from the start of the H2B histone protein is acetylated." [EBI:nj]');
INSERT INTO soterms (id, name, definition) VALUES ('001947', 'H3R2_monomethylation_site', 'A kind of histone modification site, whereby the 2nd residue (an arginine), from the start of the H3 protein is mono-methylated." [EBI:nj]');
INSERT INTO soterms (id, name, definition) VALUES ('001948', 'H3R2_dimethylation_site', 'A kind of histone modification site, whereby the 2nd residue (an arginine), from the start of the H3 protein is di-methylated." [EBI:nj]');
INSERT INTO soterms (id, name, definition) VALUES ('001949', 'H4R3_dimethylation_site', 'A kind of histone modification site, whereby the 3nd residue (an arginine), from the start of the H4 protein is di-methylated." [EBI:nj]');
INSERT INTO soterms (id, name, definition) VALUES ('001950', 'H4K4_trimethylation_site', 'A kind of histone modification site, whereby the 4th residue (a lysine), from the start of the H4 protein is tri-methylated." [EBI:nj]');
INSERT INTO soterms (id, name, definition) VALUES ('001951', 'H3K23_dimethylation_site', 'A kind of histone modification site, whereby the 23rd residue (a lysine), from the start of the H3 protein is di-methylated." [EBI:nj]');
INSERT INTO soterms (id, name, definition) VALUES ('001952', 'promoter_flanking_region', 'A region immediately adjacent to a promoter which may or may not contain transcription factor binding sites." [EBI:nj]');
INSERT INTO soterms (id, name, definition) VALUES ('001953', 'restriction_enzyme_assembly_scar', 'A region of DNA sequence formed from the ligation of two sticky ends where the palindrome is broken and no longer comprises the recognition site and thus cannot be re-cut by the restriction enzymes used to create the sticky ends." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001954', 'restriction_enzyme_region', 'A region related to restriction enzyme function." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001955', 'protein_stability_element', 'A polypeptide region that proves structure in a protein that affects the stability of the protein." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001956', 'protease_site', 'A polypeptide_region that  codes for a protease cleavage site." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001957', 'RNA_stability_element', 'RNA secondary structure that affects the stability of an RNA molecule." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001958', 'lariat_intron', 'A kind of intron whereby the excision is driven by lariat formation." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001959', 'TCT_motif', 'A cis-regulatory element, conserved sequence YYC+1TTTYY, and spans -2 to +6 relative to +1 TSS. It is present in most ribosomal protein genes in Drosophila and mammals but not in the yeast Saccharomyces cerevisiae. Resembles the initiator (TCAKTY in Drosophila) but functionally distinct from initiator." [PMID:20801935, SO:myl]');
INSERT INTO soterms (id, name, definition) VALUES ('001960', '5_hydroxymethylcytosine', 'A modified DNA cytosine base feature, modified by a hydroxymethyl group at the 5 carbon." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001961', '5_formylcytosine', 'A modified DNA cytosine base feature, modified by a formyl group at the 5 carbon." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001962', 'modified_adenine', 'A modified adenine DNA base feature." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001963', 'modified_cytosine', 'A modified cytosine DNA base feature." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001964', 'modified_guanine', 'A modified guanine DNA base feature." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001965', '8_oxoguanine', 'A modified DNA guanine base,at the 8 carbon, often the product of DNA damage." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001966', '5_carboxylcytosine', 'A modified DNA cytosine base feature, modified by a carboxy group at the 5 carbon." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001967', '8_oxoadenine', 'A modified DNA adenine base,at the 8 carbon, often the product of DNA damage." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001968', 'coding_transcript_variant', 'A transcript variant of a protein coding gene." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001969', 'coding_transcript_intron_variant', 'A transcript variant occurring within an intron of a coding transcript." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001970', 'non_coding_transcript_intron_variant', 'A transcript variant occurring within an intron of a non coding transcript." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001971', 'zinc_finger_binding_site', 'A transcript variant occurring within an intron of a non coding transcript." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001972', 'histone_4_acetylation_site', 'A histone 4 modification where the modification is the acetylation of the residue." [EBI:nj, ISBN:0815341059, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001973', 'histone_3_acetylation_site', 'A histone 3 modification where the modification is the acetylation of the residue." [EBI:nj, ISBN:0815341059, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001974', 'CTCF_binding_site', 'A transcription factor binding site with consensus sequence CCGCGNGGNGGCAG, bound by CCCTF-binding factor." [EBI:nj]');
INSERT INTO soterms (id, name, definition) VALUES ('001975', 'five_prime_sticky_end_restriction_enzyme_cleavage_site', 'A restriction enzyme recognition site that, when cleaved, results in 5 prime overhangs." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001976', 'three_prime_sticky_end_restriction_enzyme_cleavage_site', 'A restriction enzyme recognition site that, when cleaved, results in 3 prime overhangs." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001977', 'ribonuclease_site', 'A region of a transcript encoding the cleavage site for a ribonuclease enzyme." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001978', 'signature', 'A region of  sequence where developer information is encoded." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001979', 'RNA_stability_element', 'A motif that affects the stability of RNA." [PMID:22495308, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001980', 'G_box', 'A regulatory promoter element identified in mutation experiments, with consensus sequence: CACGTG. Present in promoters, intergenic regions, coding regions, and introns. They are involved in gene expression responses to light and interact with G-box binding factor and I-box binding factor 1a." [PMID:19249238, PMID:8571452, SO:ml]');
INSERT INTO soterms (id, name, definition) VALUES ('001981', 'L_box', 'An orientation dependent  regulatory promoter element, with consensus sequence of TTGCACAN4TTGCACA, found in plants." [PMID:17381552, PMID:2902624, SO:ml]');
INSERT INTO soterms (id, name, definition) VALUES ('001982', 'I-box', 'A plant regulatory promoter motif,  composed of a highly conserved hexamer GATAAG (I-box core)." [PMID:2347304, PMID:2902624, SO:ml]');
INSERT INTO soterms (id, name, definition) VALUES ('001983', '5_prime_UTR_premature_start_codon_variant', 'A 5 UTR variant where a premature start codon is introduced, moved or lost." [SANGER:am]');
INSERT INTO soterms (id, name, definition) VALUES ('001984', 'silent_mating_type_cassette_array', 'A gene cassette array that corresponds to a silenced version of a mating type region." [PomBase:mah]');
INSERT INTO soterms (id, name, definition) VALUES ('001985', 'Okazaki_fragment', 'Any of the DNA segments produced by discontinuous synthesis of the lagging strand during DNA replication." [ISBN:0805350152]');
INSERT INTO soterms (id, name, definition) VALUES ('001986', 'upstream_transcript_variant', 'A feature variant, where the alteration occurs upstream of the transcript TSS." [EBI:gr]');
INSERT INTO soterms (id, name, definition) VALUES ('001987', 'downstream_transcript_variant', 'A feature variant, where the alteration occurs upstream of the transcript TSS." [EBI:gr]');
INSERT INTO soterms (id, name, definition) VALUES ('001988', '5_prime_UTR_premature_start_codon_gain_variant', 'A 5 UTR variant where a premature start codon is gained." [Sanger:am]');
INSERT INTO soterms (id, name, definition) VALUES ('001989', '5_prime_UTR_premature_start_codon_loss_variant', 'A 5 UTR variant where a premature start codon is lost." [SANGER:am]');
INSERT INTO soterms (id, name, definition) VALUES ('001990', 'five_prime_UTR_premature_start_codon_location_variant', 'A 5 UTR variant where a premature start codon is moved." [SANGER:am]');
INSERT INTO soterms (id, name, definition) VALUES ('001991', 'consensus_AFLP_fragment', 'A consensus AFLP fragment is an AFLP sequence produced from any alignment algorithm which uses assembled multiple AFLP sequences as input." [GMOD:ea]');
INSERT INTO soterms (id, name, definition) VALUES ('001992', 'nonsynonymous_variant', 'A non-synonymous variant is an inframe, protein altering variant, resulting in a codon change." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001993', 'extended_cis_splice_site', 'Intronic positions associated with cis-splicing. Contains the first and second positions immediately before the exon and the first, second and fifth positions immediately after." [SANGER:am]');
INSERT INTO soterms (id, name, definition) VALUES ('001994', 'intron_base_5', 'Fifth intronic position after the intron exon boundary, close to the 5 edge of the intron." [SANGER:am]');
INSERT INTO soterms (id, name, definition) VALUES ('001995', 'extended_intronic_splice_region_variant', 'A sequence variant occurring in the intron, within 10 bases of exon." [sanger:am]');
INSERT INTO soterms (id, name, definition) VALUES ('001996', 'extended_intronic_splice_region', 'Region of intronic sequence within 10 bases of an exon." [SANGER:am]');
INSERT INTO soterms (id, name, definition) VALUES ('001997', 'subtelomere', 'A heterochromatic region of the chromosome,  adjacent to the telomere (on the centromeric side) that contains repetitive DNA and sometimes genes and it is transcribed." [POMBE:al]');
INSERT INTO soterms (id, name, definition) VALUES ('001998', 'sgRNA', 'A small RNA oligo, typically about 20 bases, that guides the cas nuclease to a target DNA sequence in the CRISPR/cas mutagenesis method." [PMID:23934893]');
INSERT INTO soterms (id, name, definition) VALUES ('001999', 'mating_type_region_motif', 'DNA motif that is a component of a mating type region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002001', 'Y_region', 'A segment of non-homology between a and alpha mating alleles, found at all three mating loci (HML, MAT, and HMR), has two forms (Ya and Yalpha)." [SGD:jd]');
INSERT INTO soterms (id, name, definition) VALUES ('002002', 'Z1_region', 'A mating type region motif, one of two segments of homology found at all three mating loci (HML, MAT, and HMR)." [SGD:jd]');
INSERT INTO soterms (id, name, definition) VALUES ('002003', 'Z2_region', 'A mating type region motif, the rightmost segment of homology in the HML and MAT mating loci (not present in HMR)." [SGD:jd]');
INSERT INTO soterms (id, name, definition) VALUES ('002004', 'ARS_consensus_sequence', 'The ACS is an 11-bp sequence of the form 5-WTTTAYRTTTW-3 which is at the core of every yeast ARS, and is necessary but not sufficient for recognition and binding by the origin recognition complex (ORC). Functional ARSs require an ACS, as well as other cis elements in the 5 (C domain) and 3 (B domain) flanking sequences of the ACS." [SGD:jd]');
INSERT INTO soterms (id, name, definition) VALUES ('002005', 'DSR_motif', 'The determinant of selective removal (DSR) motif consists of repeats of U(U/C)AAAC. The motif targets meiotic transcripts for removal during mitosis via the exosome." [PMID:22645662]');
INSERT INTO soterms (id, name, definition) VALUES ('002006', 'zinc_repressed_element', 'A promoter element that has the consensus sequence GNMGATC, and is found in promoters of genes repressed in the presence of zinc." [PMID:24003116, POMBE:mh]');
INSERT INTO soterms (id, name, definition) VALUES ('002007', 'MNV', 'An MNV is a multiple nucleotide variant (substitution) in which the inserted sequence is the same length as the replaced sequence." [NCBI:th]');
INSERT INTO soterms (id, name, definition) VALUES ('002008', 'rare_amino_acid_variant', 'A sequence variant whereby at least one base of a codon encoding a rare amino acid is changed, resulting in a different encoded amino acid." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002009', 'selenocysteine_loss', 'A sequence variant whereby at least one base of a codon encoding selenocysteine  is changed, resulting in a different encoded amino acid." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002010', 'pyrrolysine_loss', 'A sequence variant whereby at least one base of a codon encoding pyrrolysine is changed, resulting in a different encoded amino acid." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002011', 'intragenic_variant', 'A variant that occurs within a gene but falls outside of all transcript features. This occurs when alternate transcripts of a gene do not share overlapping  sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002012', 'start_lost', 'A codon variant that changes at least one base of the canonical start codon." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002013', '5_prime_UTR_truncation', 'A sequence variant that causes the reduction of a the 5UTR  with regard to the reference sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002014', '5_prime_UTR_elongation', 'A sequence variant that causes the extension of 5 UTR, with regard to the reference sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002015', '3_prime_UTR_truncation', 'A sequence variant that causes the reduction of a the 3 UTR  with regard to the reference sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002016', '3_prime_UTR_elongation', 'A sequence variant that causes the extension of 3 UTR, with regard to the reference sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002017', 'conserved_intergenic_variant', 'A sequence variant located in a conserved intergenic region, between genes." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002018', 'conserved_intron_variant', 'A transcript variant occurring within a conserved region of an intron." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002019', 'start_retained_variant', 'A sequence variant where at least one base in the start codon is changed, but the start remains." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002020', 'boundary_element', 'Boundary elements are DNA motifs that prevent heterochromatin from spreading into neighboring euchromatic regions." [PMID:24013502]');
INSERT INTO soterms (id, name, definition) VALUES ('002021', 'mating_type_region_replication_fork_barrier', 'A DNA motif that is found in eukaryotic rDNA repeats, and is a site of replication fork pausing." [PMID:17614787]');
INSERT INTO soterms (id, name, definition) VALUES ('002022', 'priRNA', 'A small RNA molecule, 22-23 nt in size, that is the product of a longer RNA. The production of priRNAs is independent of dicer and involves binding of RNA by argonaute and trimming by triman. In fission yeast, priRNAs trigger the establishment of heterochromatin. PriRNAs are primarily generated from centromeric transcripts (dg and dh repeats), but may also be produced from degradation products of primary transcripts." [PMID:20178743, PMID:24095277, PomBase:al]');
INSERT INTO soterms (id, name, definition) VALUES ('002023', 'multiplexing_sequence_identifier', 'A nucleic tag which is used in a ligation step of library preparation process to allow pooling of samples while maintaining ability to identify individual source material and creation of a multiplexed library." [OBO:prs, PMID:22574170]');
INSERT INTO soterms (id, name, definition) VALUES ('002024', 'W_region', 'The leftmost segment of homology in the HML and MAT mating loci,  but not present in HMR." [SGD:jd]');
INSERT INTO soterms (id, name, definition) VALUES ('002025', 'cis_acting_homologous_chromosome_pairing_region', 'A genome region where chromosome pairing occurs preferentially during homologous chromosome pairing during early meiotic prophase of Meiosis I." [PMID:22582262, PMID:23117617, PMID:24173580, PomBase:vw]');
INSERT INTO soterms (id, name, definition) VALUES ('002026', 'intein_encoding_region', 'The nucleotide sequence which encodes the intein portion of the precursor gene." [PMID:8165123]');
INSERT INTO soterms (id, name, definition) VALUES ('002027', 'uORF', 'A short open reading frame that is found in the 5 untranslated region of an mRNA and plays a role in translational regulation." [PMID:12890013, PMID:16153175, POMBASE:mah]');
INSERT INTO soterms (id, name, definition) VALUES ('002028', 'sORF', 'An open reading frame that encodes a peptide of less than 100 amino acids." [PMID:23970561, PMID:24705786, POMBASE:mah]');
INSERT INTO soterms (id, name, definition) VALUES ('002029', 'tnaORF', 'A translated ORF encoded entirely within the antisense strand of a known protein coding gene." [POMBASE:vw]');
INSERT INTO soterms (id, name, definition) VALUES ('002030', 'X_region', 'One of two segments of homology found at all three mating loci (HML, MAT and HMR)." [SGD:jd]');
INSERT INTO soterms (id, name, definition) VALUES ('002031', 'shRNA', 'A short hairpin RNA (shRNA) is an RNA transcript that makes a tight hairpin turn that can be used to silence target gene expression via RNA interference." [PMID:6699500, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002032', 'moR', 'A non-coding transcript encoded by sequences adjacent to the ends of the 5 and 3 miR-encoding sequences that abut the loop in precursor miRNA." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002033', 'loR', 'A short, non coding transcript of loop-derived sequences encoded in precursor miRNA." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002034', 'miR_encoding_snoRNA_primary_transcript', 'A snoRNA primary transcript that also encodes pre-miR sequence that is processed to form functionally active miRNA." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002035', 'lncRNA_primary_transcript', 'A primary transcript encoding a  lncRNA." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002036', 'miR_encoding_lncRNA_primary_transcript', 'A lncRNA primary transcript that also encodes pre-miR sequence that is processed to form functionally active miRNA." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002037', 'miR_encoding_tRNA_primary_transcript', 'A tRNA primary transcript that also encodes pre-miR sequence that is processed to form functionally active miRNA." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002038', 'shRNA_primary_transcript', 'A primary transcript encoding an shRNA." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002039', 'miR_encoding_shRNA_primary_transcript', 'A shRNA primary transcript that also encodes pre-miR sequence that is processed to form functionally active miRNA." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002040', 'vaultRNA_primary_transcript', 'A primary transcript encoding a  vaultRNA." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002041', 'miR_encoding_vaultRNA_primary_transcript', 'A vaultRNA primary transcript that also encodes pre-miR sequence that is processed to form functionally active miRNA." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002042', 'Y_RNA_primary_transcript', 'A primary transcript encoding a  Y-RNA." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002043', 'miR_encoding_Y_RNA_primary_transcript', 'A Y-RNA primary transcript that also encodes pre-miR sequence that is processed to form functionally active miRNA." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002044', 'TCS_element', 'A TCS element is a (yeast) transcription factor binding site, bound by the TEA DNA binding domain (DBD) of transcription factors. The consensus site is CATTCC or CATTCT." [PMID:1489142, PMID:20118212, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002045', 'pheromone_response_element', 'A PRE is a (yeast) TFBS with consensus site [TGAAAC(A/G)]." [PMID:1489142, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002046', 'FRE', 'A FRE is an enhancer element necessary and sufficient to confer filamentation associated expression in S. cerevisiae." [PMID:1489142, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002047', 'transcription_pause_site', 'Transcription pause sites are regions of a gene where RNA polymerase may pause during transcription. The functional role of pausing may be to facilitate factor recruitment, RNA folding, and synchronization with translation. Consensus transcription pause site have been observed in E. coli." [PMID:24789973, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002048', 'disabled_reading_frame', 'A reading frame that could encode a full-length protein but which contains obvious mid-sequence disablements (frameshifts or premature stop codons)." [SGD:se]');
INSERT INTO soterms (id, name, definition) VALUES ('002049', 'H3K27_acetylation_site', 'A kind of histone modification site, whereby the 27th residue (a lysine), from the start of the H3 histone protein is acetylated." [SO:rs]');
INSERT INTO soterms (id, name, definition) VALUES ('002050', 'constitutive_promoter', 'A promoter that allows for continual transcription of gene." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002051', 'inducible_promoter', 'A promoter whereby activity is induced by the presence or absence of biotic or abiotic factors." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002052', 'dominant_negative_variant', 'A variant where the mutated gene product adversely affects the other (wild type) gene product." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002053', 'gain_of_function_variant', 'A sequence variant whereby new or enhanced function is conferred on the gene product." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002054', 'loss_of_function_variant', 'A sequence variant whereby the gene product has diminished or abolished function." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002055', 'null_mutation', 'A variant whereby the gene product is not functional or the gene product is not produced." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002056', 'intronic_splicing_silencer', 'An intronic  splicing regulatory element that functions to recruit trans acting splicing factors suppress the transcription of the gene or genes they control." [PMID:23241926, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002057', 'intronic_splicing_enhancer', 'An intronic  splicing regulatory element that functions to recruit trans acting splicing factors suppress the transcription of the gene or genes they control." [PMID:23241926, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002058', 'exonic_splicing_silencer', 'An exonic  splicing regulatory element that functions to recruit trans acting splicing factors suppress the transcription of the gene or genes they control." [PMID:23241926, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002059', 'recombination_enhancer', 'A regulatory_region that promotes or induces the process of recombination." [PMID:8861911, SGD:se]');
INSERT INTO soterms (id, name, definition) VALUES ('002060', 'interchromosomal_translocation', 'A translocation where the regions involved are from different chromosomes." [NCBI:th]');
INSERT INTO soterms (id, name, definition) VALUES ('002061', 'intrachromosomal_translocation', 'A translocation where the regions involved are from the same chromosome." [NCBI:th]');
INSERT INTO soterms (id, name, definition) VALUES ('002062', 'complex_chromosomal_rearrangement', 'A contiguous cluster of translocations, usually the result of a single catastrophic event such as chromothripsis or chromoanasynthesis." [NCBI:th]');
INSERT INTO soterms (id, name, definition) VALUES ('002063', 'Alu_insertion', 'An insertion of sequence from the Alu family of mobile elements." [NCBI:th]');
INSERT INTO soterms (id, name, definition) VALUES ('002064', 'LINE1_insertion', 'An insertion from the Line1 family of mobile elements." [NCBI:th]');
INSERT INTO soterms (id, name, definition) VALUES ('002065', 'SVA_insertion', 'An insertion of sequence from the SVA family of mobile elements." [NCBI:th]');
INSERT INTO soterms (id, name, definition) VALUES ('002066', 'mobile_element_deletion', 'A deletion of a mobile element when comparing a reference sequence (has mobile element) to a individual sequence (does not have mobile element)." [NCBI:th]');
INSERT INTO soterms (id, name, definition) VALUES ('002067', 'HERV_deletion', 'A deletion of the HERV mobile element with respect to  a reference." [NCBI:th]');
INSERT INTO soterms (id, name, definition) VALUES ('002068', 'SVA_deletion', 'A deletion of an SVA mobile element." [NCBI:th]');
INSERT INTO soterms (id, name, definition) VALUES ('002069', 'LINE1_deletion', 'A deletion of a LINE1 mobile element with respect to a reference." [NCBI:th]');
INSERT INTO soterms (id, name, definition) VALUES ('002070', 'Alu_deletion', 'A deletion of an Alu mobile element with respect to a reference." [NCBI:th]');
INSERT INTO soterms (id, name, definition) VALUES ('002071', 'CDS_supported_by_peptide_spectrum_match', 'A CDS that is supported by proteomics data." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002072', 'sequence_comparison', 'A CDS that is supported by proteomics data." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002073', 'no_sequence_alteration', 'A position or feature within a sequence that is identical to the comparable position or feature of a specified reference sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002074', 'intergenic_1kb_variant', 'A variant that falls in an intergenic region that is 1 kb or less between 2 genes." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002075', 'incomplete_transcript_variant', 'A sequence variant that intersects an incompletely annotated transcript." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002076', 'incomplete_transcript_3UTR_variant', 'A sequence variant that intersects the 3 UTR of an incompletely annotated transcript." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002077', 'incomplete_transcript_5UTR_variant', 'A sequence variant that intersects the 5 UTR of an incompletely annotated transcript." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002078', 'incomplete_transcript_intronic_variant', 'A sequence variant that intersects the intron of an incompletely annotated transcript." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002079', 'incomplete_transcript_splice_region_variant', 'A sequence variant that intersects the splice region of an incompletely annotated transcript." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002080', 'incomplete_transcript_exonic_variant', 'A sequence variant that intersects the exon of an incompletely annotated transcript." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002081', 'incomplete_transcript_CDS', 'A sequence variant that intersects the coding regions of an incompletely annotated transcript." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002082', 'incomplete_transcript_coding_splice_variant', 'A sequence variant that intersects the coding sequence near a splice region of an incompletely annotated transcript." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002083', '2KB_downstream_variant', 'A sequence variant located within 2KB 3 of a gene." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002084', 'exonic_splice_region_variant', 'A sequence variant in which a change has occurred within the exonic region of the splice site, 1-2 bases from boundary." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002085', 'unidirectional_gene_fusion', 'A sequence variant whereby two genes, on the same strand have become joined." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002086', 'bidirectional_gene_fusion', 'A sequence variant whereby two genes, on alternate strands have become joined." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002087', 'pseudogenic_CDS', 'A non functional descendant of the coding portion of a coding transcript, part of a pseudogene." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002088', 'non_coding_transcript_splice_region_variant', 'A transcript variant occurring within the splice region (1-3 bases of the exon or 3-8 bases of the intron) of a non coding transcript." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002089', '3_prime_UTR_exon_variant', 'A UTR variant of exonic sequence of the 3 UTR." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002090', '3_prime_UTR_intron_variant', 'A UTR variant of intronic sequence of the 3 UTR." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002091', '5_prime_UTR_intron_variant', 'A UTR variant of intronic sequence of the 5 UTR." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002092', '5_prime_UTR_exon_variant', 'A UTR variant of exonic sequence of the 5 UTR." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002093', 'structural_interaction_variant', 'A variant that impacts the internal interactions of the resulting polypeptide structure." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002094', 'non_allelic_homologous_recombination_region', 'A genomic region at a non-allelic position where exchange of genetic material happens as a result of homologous recombination." []');
INSERT INTO soterms (id, name, definition) VALUES ('002095', 'scaRNA', 'A ncRNA, specific to the Cajal body, that has been demonstrated to function as a guide RNA in the site-specific synthesis of 2-O-ribose-methylated nucleotides and pseudouridines in the RNA polymerase II-transcribed U1, U2, U4 and U5 spliceosomal small nuclear RNAs (snRNAs)." [PMC:126017, SO:nrs]');
INSERT INTO soterms (id, name, definition) VALUES ('002096', 'short_tandem_repeat_variation', 'A variation that expands or contracts a tandem repeat with regard to a reference." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002097', 'vertebrate_immune_system_pseudogene', 'A pseudogene derived from a vertebrate immune system gene." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002098', 'immunoglobulin_pseudogene', 'A pseudogene derived from an immunoglobulin gene." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002099', 'T_cell_receptor_pseudogene', 'A pseudogene derived from a T-cell receptor gene." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002100', 'IG_C_pseudogene', 'A pseudogenic constant region of an immunoglobulin gene which closely resembles a known functional Imunoglobulin constant gene but in which the coding region has stop codons, frameshift mutations or a mutation that effects the initiation codon." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html, IGMT:http\://www.imgt.org/IMGTScientificChart/SequenceDescription/Keywords.php]');
INSERT INTO soterms (id, name, definition) VALUES ('002101', 'IG_J_pseudogene', 'A pseudogenic joining region which closely resembles a known functional imunoglobulin joining gene but in which the coding region has stop codons, frameshift mutations or a mutation that effects the initiation codon that rearranges at the DNA level and codes the joining region of the variable domain of an immunoglobulin chain." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html, IGMT:http\://www.imgt.org/IMGTScientificChart/SequenceDescription/Keywords.php]');
INSERT INTO soterms (id, name, definition) VALUES ('002102', 'IG_V_pseudogene', 'A pseudogenic variable region which closely resembles a known functional imunoglobulin variable gene but in which the coding region has stop codons, frameshift mutations or a mutation that effects the initiation codon that rearranges at the DNA level and codes the variable region of  an immunoglobulin chain." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html, IGMT:http\://www.imgt.org/IMGTScientificChart/SequenceDescription/Keywords.php]');
INSERT INTO soterms (id, name, definition) VALUES ('002103', 'TR_V_pseudogene', 'A pseudogenic variable region which closely resembles a known functional T receptor variable gene but in which the coding region has stop codons, frameshift mutations or a mutation that effects the initiation codon that rearranges at the DNA level and codes the variable region of  an immunoglobulin chain." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html, IGMT:http\://www.imgt.org/IMGTScientificChart/SequenceDescription/Keywords.php]');
INSERT INTO soterms (id, name, definition) VALUES ('002104', 'TR_J_pseudogene', 'A pseudogenic joining region which closely resembles a known functional T receptor (TR) joining gene but in which the coding region has stop codons, frameshift mutations or a mutation that effects the initiation codon that rearranges at the DNA level and codes the joining region of the variable domain of an immunoglobulin chain." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html, IGMT:http\://www.imgt.org/IMGTScientificChart/SequenceDescription/Keywords.php]');
INSERT INTO soterms (id, name, definition) VALUES ('002105', 'translated_processed_pseudogene', 'A processed pseudogene where there is evidence, (mass spec data) suggesting that it is also translated." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html]');
INSERT INTO soterms (id, name, definition) VALUES ('002106', 'translated_unprocessed_pseudogene', 'A non-processed pseudogene where there is evidence, (mass spec data) suggesting that it is also translated." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html]');
INSERT INTO soterms (id, name, definition) VALUES ('002107', 'transcribed_unprocessed_pseudogene', 'A unprocessed pseudogene supported by locus-specific evidence of transcription." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html]');
INSERT INTO soterms (id, name, definition) VALUES ('002108', 'transcribed_unitary_pseudogene', 'A species specific unprocessed pseudogene without a parent gene, as it has an active orthologue in another species." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html]');
INSERT INTO soterms (id, name, definition) VALUES ('002109', 'transcribed_processed_pseudogene', 'A processed_pseudogene overlapped by locus-specific evidence of transcription." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html]');
INSERT INTO soterms (id, name, definition) VALUES ('002110', 'polymorphic_pseudogene_with_retained_intron', 'A polymorphic  pseudogene in the reference genome, containing a retained intron,  known to be intact in the genomes of other individuals of the same species. The annotation process has confirmed that the pseudogenisation event is not a genomic sequencing error." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html]');
INSERT INTO soterms (id, name, definition) VALUES ('002111', 'pseudogene_processed_transcript', 'A processed_transcript supported by EST and/or mRNA evidence that aligns unambiguously to a pseudogene locus (i.e. alignment to the pseudogene locus clearly better than alignment to parent locus)." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html]');
INSERT INTO soterms (id, name, definition) VALUES ('002112', 'coding_transcript_with_retained_intron', 'A protein coding transcript containing a retained intron." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html]');
INSERT INTO soterms (id, name, definition) VALUES ('002113', 'lncRNA_with_retained_intron', 'A lncRNA transcript containing a retained intron." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html]');
INSERT INTO soterms (id, name, definition) VALUES ('002114', 'NMD_transcript', 'A protein coding transcript that contains a CDS but has one or more splice junctions >50bp downstream of stop codon, making it susceptible to nonsense mediated decay." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html]');
INSERT INTO soterms (id, name, definition) VALUES ('002115', 'pseudogenic_transcript_with_retained_intron', 'A transcript supported by EST and/or mRNA evidence that aligns unambiguously to the pseudogene locus; has retained intronic sequence compared to a reference transcript sequence." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html]');
INSERT INTO soterms (id, name, definition) VALUES ('002116', 'polymorphic_pseudogene_processed_transcript', 'A processed transcript that does not contain a CDS that fullfills annotation criteria and not necessarily functionally non-coding." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html]');
INSERT INTO soterms (id, name, definition) VALUES ('002117', '<new term>', 'A processed transcript that does not contain a CDS that fullfills annotation criteria and not necessarily functionally non-coding." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html]');
INSERT INTO soterms (id, name, definition) VALUES ('002118', 'NMD_polymorphic_pseudogene_transcript', 'A polymorphic pseudogene transcript that contains a CDS but has one or more splice junctions >50bp downstream of stop codon. Premature stop codon is not introduced, directly or indirectly, as a result of the variation i.e. must be present in both protein_coding and pseudogenic alleles." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html]');
INSERT INTO soterms (id, name, definition) VALUES ('002119', 'allelic_frequency', 'A physical quality which inheres to the allele by virtue of the number instances of the allele within a population. This is the relative frequency of the allele at a given locus in a population." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002120', 'three_prime_overlapping_ncrna', 'Transcript where ditag (digital gene expression profiling)and/or published experimental data strongly supports the existence of short non-coding transcripts transcribed from the 3UTR." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html]');
INSERT INTO soterms (id, name, definition) VALUES ('002121', 'vertebrate_immune_system_gene', 'The configuration of the  IG and TR variable (V), diversity (D) and joining (J) germline genes before DNA rearrangements  (with or without constant (C) genes in undefined configuration. (germline, non rearranged regions of the IG DNA loci)." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html, IGMT:http\://www.imgt.org/IMGTScientificChart/SequenceDescription/Keywords.php]');
INSERT INTO soterms (id, name, definition) VALUES ('002122', 'immunoglobulin_gene', 'A germline immunoglobulin  gene." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html]');
INSERT INTO soterms (id, name, definition) VALUES ('002123', 'IG_C_gene', 'A constant (C) gene, a gene that codes the constant region of an immunoglobulin chain." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html, IGMT:http\://www.imgt.org/IMGTScientificChart/SequenceDescription/Keywords.php]');
INSERT INTO soterms (id, name, definition) VALUES ('002124', 'IG_D_gene', 'A gene that rearranges at the DNA level and codes the diversity region of the variable domain of an immunoglobuin (IG) gene." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html, IGMT:http\://www.imgt.org/IMGTScientificChart/SequenceDescription/Keywords.php]');
INSERT INTO soterms (id, name, definition) VALUES ('002125', 'IG_J_gene', 'A joining gene that rearranges at the DNA level and codes the joining region of the variable domain of an immunoglobulin chain." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html, IGMT:http\://www.imgt.org/IMGTScientificChart/SequenceDescription/Keywords.php]');
INSERT INTO soterms (id, name, definition) VALUES ('002126', 'IG_V_gene', 'A variable gene that rearranges at the DNA level and codes the variable region of the variable domain of an Immunoglobulin chain." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html, IGMT:http\://www.imgt.org/IMGTScientificChart/SequenceDescription/Keywords.php]');
INSERT INTO soterms (id, name, definition) VALUES ('002127', 'lncRNA_gene', 'A gene that encodes a long non-coding RNA." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html]');
INSERT INTO soterms (id, name, definition) VALUES ('002128', 'mt_rRNA', 'Mitochondrial ribosomal RNA." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html]');
INSERT INTO soterms (id, name, definition) VALUES ('002129', 'mt_tRNA', 'Mitochondrial transfer RNA." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html]');
INSERT INTO soterms (id, name, definition) VALUES ('002130', 'NSD_transcript', 'A transcript that contains a CDS but has no stop codon before the polyA site is reached." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html]');
INSERT INTO soterms (id, name, definition) VALUES ('002131', 'sense_intronic_ncRNA', 'A non-coding transcript found within an intron of a coding or non-coding gene, with no overlap of exonic sequence." [GENECODE:http\://www.gencodegenes.org/gencode_biotypes.html]');
INSERT INTO soterms (id, name, definition) VALUES ('002132', 'sense_overlap_ncRNA', 'A non-coding transcript that contains a protein coding gene within its intronic sequence on the same strand, with no overlap of exonic sequence." [GENECODE:http\://www.gencodegenes.org/gencode_biotypes.html]');
INSERT INTO soterms (id, name, definition) VALUES ('002133', 'T_cell_receptor_gene', 'A T-cell receptor germline gene." []');
INSERT INTO soterms (id, name, definition) VALUES ('002134', 'TR_C_Gene', 'A constant (C) gene, a gene that codes the constant region of a T-cell receptor chain." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html, IGMT:http\://www.imgt.org/IMGTScientificChart/SequenceDescription/Keywords.php]');
INSERT INTO soterms (id, name, definition) VALUES ('002135', 'TR_D_Gene', 'A gene that rearranges at the DNA level and codes the diversity region of the variable domain of aT-cell receptor gene." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html, IGMT:http\://www.imgt.org/IMGTScientificChart/SequenceDescription/Keywords.php]');
INSERT INTO soterms (id, name, definition) VALUES ('002136', 'TR_J_Gene', 'A joining gene that rearranges at the DNA level and codes the joining region of the variable domain of aT-cell receptor chain." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html, IGMT:http\://www.imgt.org/IMGTScientificChart/SequenceDescription/Keywords.php]');
INSERT INTO soterms (id, name, definition) VALUES ('002137', 'TR_V_Gene', 'A variable gene that rearranges at the DNA level and codes the variable region of the variable domain of aT-cell receptor chain." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html, IGMT:http\://www.imgt.org/IMGTScientificChart/SequenceDescription/Keywords.php]');
INSERT INTO soterms (id, name, definition) VALUES ('002138', 'predicted_transcript', 'A transcript feature that has been predicted but is not yet validated." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002139', 'unconfirmed_transcript', 'This is used for non-spliced EST clusters that have polyA features. This category has been specifically created for the ENCODE project to highlight regions that could indicate the presence of protein coding genes that require experimental validation, either by 5 RACE or RT-PCR to extend the transcripts, or by confirming expression of the putatively-encoded peptide with specific antibodies." [GENCODE:http\://www.gencodegenes.org/gencode_biotypes.html]');
INSERT INTO soterms (id, name, definition) VALUES ('002140', 'early_origin_of_replication', 'An origin of replication that initiates early in S phase." [PMID:23348837, PMID:9115207]');
INSERT INTO soterms (id, name, definition) VALUES ('002141', 'late_origin_of_replication', 'An origin of replication that initiates late in S phase." [PMID:23348837, PMID:9115207]');
INSERT INTO soterms (id, name, definition) VALUES ('002142', 'histone_2A_acetylation_site', 'A histone 2A modification where the modification is the acetylation of the residue." [ISBN:0815341059]');
INSERT INTO soterms (id, name, definition) VALUES ('002143', 'histone_2B_acetylation_site', 'A histone 2B modification where the modification is the acetylation of the residue." [ISBN:0815341059]');
INSERT INTO soterms (id, name, definition) VALUES ('002144', 'histone_2AZ_acetylation_site', 'A histone 2AZ modification where the modification is the acetylation of the residue." [PMID:19385636, PMID:24316985, PMID:27087541]');
INSERT INTO soterms (id, name, definition) VALUES ('002145', 'H2AZK4_acetylation_site', 'A kind of histone modification site, whereby the 4th residue (a lysine), from the start of the H2AZ histone protein is acetylated." [PMID:19385636, PMID:24316985, PMID:27087541]');
INSERT INTO soterms (id, name, definition) VALUES ('002146', 'H2AZK7_acetylation_site', 'A kind of histone modification site, whereby the 7th residue (a lysine), from the start of the H2AZ histone protein is acetylated." [PMID:19385636, PMID:24316985, PMID:27087541]');
INSERT INTO soterms (id, name, definition) VALUES ('002147', 'H2AZK11_acetylation_site', 'A kind of histone modification site, whereby the 11th residue (a lysine), from the start of the H2AZ histone protein is acetylated." [PMID:19385636, PMID:24316985, PMID:27087541]');
INSERT INTO soterms (id, name, definition) VALUES ('002148', 'H2AZK13_acetylation_site', 'A kind of histone modification site, whereby the 13th residue (a lysine), from the start of the H2AZ histone protein is acetylated." [PMID:19385636, PMID:24316985, PMID:27087541]');
INSERT INTO soterms (id, name, definition) VALUES ('002149', 'H2AZK15_acetylation_site', 'A kind of histone modification site, whereby the 15th residue (a lysine), from the start of the H2AZ histone protein is acetylated." [PMID:19385636, PMID:24316985, PMID:27087541]');
INSERT INTO soterms (id, name, definition) VALUES ('002150', 'AUG_initiated_uORF', 'A uORF beginning with the canonical start codon AUG." [PMID:26684391, PMID:27313038]');
INSERT INTO soterms (id, name, definition) VALUES ('002151', 'non_AUG_initiated_uORF', 'A uORF beginning with a codon other than AUG." [PMID:26684391, PMID:27313038]');
INSERT INTO soterms (id, name, definition) VALUES ('002152', 'genic_downstream_transcript_variant', 'A variant that falls downstream of a transcript, but within the genic region of the gene due to alternately transcribed isoforms." [NCBI:dm, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002153', 'genic_upstream_transcript_variant', 'A variant that falls upstream of a transcript, but within the genic region of the gene due to alternately transcribed isoforms." [NCBI:dm, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002154', 'mitotic_recombination_region', 'A genomic region where there is an exchange of genetic material with another genomic region, occurring in somatic cells." [NCBI:cf, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002155', 'meiotic_recombination_region', 'A genomic region in which there is an exchange of genetic material as a result of the repair of meiosis-specific double strand breaks that occur during meiotic prophase." [NCBI:cf, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('002156', 'CArG_box', 'A promoter element bound by the MADS family of transcription factors with consensus 5-(C/T)TA(T/A)4TA(G/A)-3." [PMID:1748287, PMID:7623803]');
INSERT INTO soterms (id, name, definition) VALUES ('002157', 'Mat2P', 'A gene cassette array containing H+ mating type specific information." [PMID:18354497]');
INSERT INTO soterms (id, name, definition) VALUES ('002158', 'Mat3M', 'A gene cassette array containing H- mating type specific information." [PMID:18354497]');
INSERT INTO soterms (id, name, definition) VALUES ('002159', 'SHP_box', 'A conserved Cdc48/p97 interaction motif with strict consensus sequence F[PI]GKG[TK][RK]LG[GT] and relaxed consensus sequence FXGKGX[RK]LG." [PMID:17083136, PMID:27655872]');
INSERT INTO soterms (id, name, definition) VALUES ('002160', 'sequence_length_variant', 'A sequence variant that changes the length of one or more sequence features." []');
INSERT INTO soterms (id, name, definition) VALUES ('002161', 'short_tandem_repeat_change', 'A sequence variant where the copies of a short tandem repeat (STR) feature are either contracted or expanded." []');
INSERT INTO soterms (id, name, definition) VALUES ('002162', 'short_tandem_repeat_expansion', 'A short tandem repeat variant containing more repeat units than the reference sequence." []');
INSERT INTO soterms (id, name, definition) VALUES ('002163', 'short_tandem_repeat_contraction', 'A short tandem repeat variant containing fewer repeat units than the reference sequence." []');
INSERT INTO soterms (id, name, definition) VALUES ('002164', 'H2BK5_acetylation_site', 'A kind of histone modification site, whereby the 5th residue (a lysine), from the start of the H2B histone protein is acetylated." [http://www.actrec.gov.in/histome/ptm_sp.php?ptm_sp=H2BK5ac, PMID:18552846]');
INSERT INTO soterms (id, name, definition) VALUES ('002165', 'trinucleotide_repeat_expansion', 'A short tandem repeat expansion with an increase in a sequence of three nucleotide units repeated in tandem compared to a reference sequence." []');
INSERT INTO soterms (id, name, definition) VALUES ('002166', 'ref_miRNA', 'A ref_miRNA (RefSeq-miRNA) sequence is assigned at the creation of a new mature miRNA entry in a database. The ref_miRNA sequence designation remains unchanged even if a different isomiR is later shown to be expressed at a higher level. A ref_miRNA can be produced by one or multiple pre-miRNA." [PMID:26453491]');
INSERT INTO soterms (id, name, definition) VALUES ('002167', 'isomiR', 'IsomiRs are all the bona fide variants of a mature product. IsomiRs should be connected to the ref_miRNA it is most likely to be the variant of. Some isomiRs can be variations of one or multiple ref_miRNA." [PMID:26453491]');
INSERT INTO soterms (id, name, definition) VALUES ('002168', 'RNA_thermometer', 'An RNA_thermometer is a cis element in the 5 end of an mRNA that can change its secondary structure in response to temperature and coordinate temperature-dependent gene expression." [PMID:22421878]');
INSERT INTO soterms (id, name, definition) VALUES ('002169', 'splice_polypyrimidine_tract_variant', 'A sequence variant that falls in the polypyrimidine tract at 3 end of intron between 17 and 3 bases from the end (acceptor -3 to acceptor -17)." []');
INSERT INTO soterms (id, name, definition) VALUES ('002170', 'splice_donor_region_variant', 'A sequence variant that falls in the region between the 3rd and 6th base after splice junction (5 end of intron)." []');
INSERT INTO soterms (id, name, definition) VALUES ('002171', 'telomeric_D_loop', 'A telomeric D-loop is a three-stranded DNA displacement loop that forms at the site where the telomeric 3 single-stranded DNA overhang (formed of the repeat sequence TTAGGG in mammals) is tucked back inside the double-stranded component of telomeric DNA molecule, thus forming a t-loop or telomeric-loop and protecting the chromosome terminus." [PMID:10338204, PMID:15071557, PMID:24012755]');
INSERT INTO soterms (id, name, definition) VALUES ('005836', 'regulatory_region', 'A region of sequence that is involved in the control of a biological process." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('005837', 'U14_snoRNA_primary_transcript', 'The primary transcript of an evolutionarily conserved eukaryotic low molecular weight RNA capable of intermolecular hybridization with both homologous and heterologous 18S rRNA." [PMID:2251119]');
INSERT INTO soterms (id, name, definition) VALUES ('005841', 'methylation_guide_snoRNA', 'A snoRNA that specifies the site of 2-O-ribose methylation in an RNA molecule by base pairing with a short sequence around the target residue." [GOC:mah, PMID:12457565]');
INSERT INTO soterms (id, name, definition) VALUES ('005843', 'rRNA_cleavage_RNA', 'An ncRNA that is part of a ribonucleoprotein that cleaves the primary pre-rRNA transcript in the process of producing mature rRNA molecules." [GOC:kgc]');
INSERT INTO soterms (id, name, definition) VALUES ('005845', 'exon_of_single_exon_gene', 'An exon that is the only exon in a gene." [RSC:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('005847', 'cassette_array_member', 'An exon that is the only exon in a gene." [RSC:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('005848', 'gene_cassette_member', 'An exon that is the only exon in a gene." [RSC:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('005849', 'gene_subarray_member', 'An exon that is the only exon in a gene." [RSC:cb]');
INSERT INTO soterms (id, name, definition) VALUES ('005850', 'primer_binding_site', 'Non-covalent primer binding site for initiation of replication, transcription, or reverse transcription." [http://www.ebi.ac.uk/embl/Documentation/FT_definitions/feature_table.html]');
INSERT INTO soterms (id, name, definition) VALUES ('005851', 'gene_array', 'An array includes two or more genes, or two or more gene subarrays, contiguously arranged where the individual genes, or subarrays, are either identical in sequence, or essentially so." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('005852', 'gene_subarray', 'A subarray is, by defintition, a member of a gene array (SO:0005851); the members of a subarray may differ substantially in sequence, but are closely related in function." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('005853', 'gene_cassette', 'A gene that can be substituted for a related gene at a different site in the genome." [SGD:se]');
INSERT INTO soterms (id, name, definition) VALUES ('005854', 'gene_cassette_array', 'An array of non-functional genes whose members, when captured by recombination form functional genes." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('005855', 'gene_group', 'A collection of related genes." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('005856', 'selenocysteine_tRNA_primary_transcript', 'A primary transcript encoding seryl tRNA (SO:000269)." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('005857', 'selenocysteinyl_tRNA', 'A tRNA sequence that has a selenocysteine anticodon, and a 3 selenocysteine binding region." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('005858', 'syntenic_region', 'A region in which two or more pairs of homologous markers occur on the same chromosome in two or more species." [http://www.informatics.jax.org/silverbook/glossary.shtml]');
INSERT INTO soterms (id, name, definition) VALUES ('100001', 'biochemical_region_of_peptide', 'A region of a peptide that is involved in a biochemical function." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('100002', 'molecular_contact_region', 'A region that is involved a contact with another molecule." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('100003', 'intrinsically_unstructured_polypeptide_region', 'A region of polypeptide chain with high conformational flexibility." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('100004', 'catmat_left_handed_three', 'A motif of 3 consecutive residues with dihedral angles as follows: res i: phi -90 bounds -120 to -60, res i: psi -10 bounds -50 to 30, res i+1: phi -75 bounds -100 to -50, res i+1: psi 140 bounds 110 to 170. An extra restriction of the length of the O to O distance would be useful, that it be less than 5 Angstrom. More precisely these two oxygens are the main chain carbonyl oxygen atoms of residues i-1 and i+1." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('100005', 'catmat_left_handed_four', 'A motif of 4 consecutive residues with dihedral angles as follows: res i: phi -90 bounds -120 to -60, res i psi -10 bounds -50 to 30, res i+1: phi -90 bounds -120 to -60, res i+1: psi -10 bounds -50 to 30, res i+2: phi -75 bounds -100 to -50, res i+2: psi 140 bounds 110 to 170.  The extra restriction of the length of the O to O distance is similar, that it be less than 5 Angstrom. In this case these two Oxygen atoms are the main chain carbonyl oxygen atoms of residues i-1 and i+2." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('100006', 'catmat_right_handed_three', 'A motif of 3 consecutive residues with dihedral angles as follows: res i: phi -90 bounds -120 to -60, res i: psi -10 bounds -50 to 30, res i+1: phi -75 bounds -100 to -50, res i+1: psi 140 bounds 110 to 170. An extra restriction of the length of the O to O distance would be useful, that it be less than 5 Angstrom. More precisely these two oxygens are the main chain carbonyl oxygen atoms of residues i-1 and i+1." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('100007', 'catmat_right_handed_four', 'A motif of 4 consecutive residues with dihedral angles as follows: res i: phi -90 bounds -120 to -60, res i: psi -10 bounds -50 to 30, res i+1: phi -90 bounds -120 to -60, res i+1: psi -10 bounds -50 to 30, res i+2: phi -75 bounds -100 to -50, res i+2: psi 140 bounds 110 to 170. The extra restriction of the length of the O to O distance is similar, that it be less than 5 Angstrom. In this case these two Oxygen atoms are the main chain carbonyl oxygen atoms of residues i-1 and i+2." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('100008', 'alpha_beta_motif', 'A motif of five consecutive residues and two H-bonds in which: H-bond between CO of residue(i) and NH of residue(i+4), H-bond between CO of residue(i) and NH of residue(i+3),Phi angles of residues(i+1), (i+2) and (i+3) are negative." [EBIBS:GAR, http://www.ebi.ac.uk/msd-srv/msdmotif/]');
INSERT INTO soterms (id, name, definition) VALUES ('100009', 'lipoprotein_signal_peptide', 'A peptide that acts as a signal for both membrane translocation and lipid attachment in prokaryotes." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('100010', 'no_output', 'An experimental region wherean analysis has been run and not produced any annotation." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('100011', 'cleaved_peptide_region', 'The cleaved_peptide_region is the region of a peptide sequence that is cleaved during maturation." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('100012', 'peptide_coil', 'Irregular, unstructured regions of a proteins backbone, as distinct from the regular region (namely alpha helix and beta strand - characterised by specific patterns of main-chain hydrogen bonds)." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('100013', 'hydrophobic_region_of_peptide', 'Hydrophobic regions are regions with a low affinity for water." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('100014', 'n_terminal_region', 'The amino-terminal positively-charged region of a signal peptide (approx 1-5 aa)." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('100015', 'c_terminal_region', 'The more polar, carboxy-terminal region of the signal peptide (approx 3-7 aa)." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('100016', 'central_hydrophobic_region_of_signal_peptide', 'The central, hydrophobic region of the signal peptide (approx 7-15 aa)." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('100017', 'polypeptide_conserved_motif', 'A conserved motif is a short (up to 20 amino acids) region of biological interest that is conserved in different proteins. They may or may not have functional or structural significance within the proteins in which they are found." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('100018', 'polypeptide_binding_motif', 'A polypeptide binding motif is a short (up to 20 amino acids) polypeptide region of biological interest that contains one or more amino acids experimentally shown to bind to a ligand." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('100019', 'polypeptide_catalytic_motif', 'A polypeptide catalytic motif is a short (up to 20 amino acids) polypeptide region that contains one or more active site residues." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('100020', 'polypeptide_DNA_contact', 'A binding site that, in the polypeptide molecule, interacts selectively and non-covalently with DNA." [EBIBS:GAR, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('100021', 'polypeptide_conserved_region', 'A subsection of sequence with biological interest that is conserved in different proteins. They may or may not have functional or structural significance within the proteins in which they are found." [EBIBS:GAR]');
INSERT INTO soterms (id, name, definition) VALUES ('000002', 'substitution', 'A sequence alteration where the length of the change in the variant is the same as that of the reference." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000005', 'complex_substitution', 'When no simple or well defined DNA mutation event describes the observed DNA change, the keyword \"complex\" should be used. Usually there are multiple equally plausible explanations for the change." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000008', 'point_mutation', 'A single nucleotide change which has occurred at the same position of a corresponding nucleotide in a reference sequence." [SO:immuno_workshop]');
INSERT INTO soterms (id, name, definition) VALUES ('000009', 'transition', 'Change of a pyrimidine nucleotide, C or T, into an other pyrimidine nucleotide, or change of a purine nucleotide, A or G, into an other purine nucleotide." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000010', 'pyrimidine_transition', 'A substitution of a pyrimidine, C or T, for another pyrimidine." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000011', 'C_to_T_transition', 'A transition of a cytidine to a thymine." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000012', 'C_to_T_transition_at_pCpG_site', 'The transition of cytidine to thymine occurring at a pCpG site as a consequence of the spontaneous deamination of 5-methylcytidine." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000013', 'T_to_C_transition', 'The transition of cytidine to thymine occurring at a pCpG site as a consequence of the spontaneous deamination of 5-methylcytidine." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000014', 'purine_transition', 'A substitution of a purine, A or G, for another purine." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000015', 'A_to_G_transition', 'A transition of an adenine to a guanine." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000016', 'G_to_A_transition', 'A transition of a guanine to an adenine." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000017', 'transversion', 'Change of a pyrimidine nucleotide, C or T, into a purine nucleotide, A or G, or vice versa." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000018', 'pyrimidine_to_purine_transversion', 'Change of a pyrimidine nucleotide, C or T, into a purine nucleotide, A or G." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000019', 'C_to_A_transversion', 'A transversion from cytidine to adenine." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000020', 'C_to_G_transversion', 'A transversion from cytidine to adenine." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000021', 'T_to_A_transversion', 'A transversion from T to A." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000022', 'T_to_G_transversion', 'A transversion from T to G." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000023', 'purine_to_pyrimidine_transversion', 'Change of a purine nucleotide, A or G , into a pyrimidine nucleotide C or T." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000024', 'A_to_C_transversion', 'A transversion from adenine to cytidine." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000025', 'A_to_T_transversion', 'A transversion from adenine to thymine." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000026', 'G_to_C_transversion', 'A transversion from guanine to cytidine." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000027', 'G_to_T_transversion', 'A transversion from guanine to thymine." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000028', 'intrachromosomal_mutation', 'A chromosomal structure variation within a single chromosome." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000029', 'chromosomal_deletion', 'An incomplete chromosome." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000030', 'chromosomal_inversion', 'An interchromosomal mutation where a region of the chromosome is inverted with respect to wild type." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000031', 'interchromosomal_mutation', 'A chromosomal structure variation whereby more than one chromosome is involved." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000032', 'indel', 'A sequence alteration which included an insertion and a deletion, affecting 2 or more bases." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html, http:http\://www.hgvs.org/mutnomen/recs-DNA.html#indel]');
INSERT INTO soterms (id, name, definition) VALUES ('000035', 'duplication', 'An insertion which derives from, or is identical in sequence to, nucleotides present at a known location in the genome." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html, NCBI:th]');
INSERT INTO soterms (id, name, definition) VALUES ('000036', 'inversion', 'A continuous nucleotide sequence is inverted in the same position." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000037', 'chromosomal_duplication', 'An extra chromosome." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000038', 'intrachromosomal_duplication', 'A duplication that occurred within a chromosome." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000039', 'direct_tandem_duplication', 'A tandem duplication where the individual regions are in the same orientation." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000040', 'inverted_tandem_duplication', 'A tandem duplication where the individual regions are not in the same orientation." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000041', 'intrachromosomal_transposition', 'A chromosome structure variation whereby a transposition occurred within a chromosome." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000042', 'compound_chromosome', 'A chromosome structure variant where a monocentric element is caused by the fusion of two chromosome arms." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000043', 'Robertsonian_fusion', 'A non reciprocal translocation whereby the participating chromosomes break at their centromeres and the long arms fuse to form a single chromosome with a single centromere." [http://en.wikipedia.org/wiki/Robertsonian_translocation]');
INSERT INTO soterms (id, name, definition) VALUES ('000044', 'chromosomal_translocation', 'A chromosomal mutation. Rearrangements that alter the pairing of telomeres are classified as translocations." [FB:reference_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('000045', 'ring_chromosome', 'A ring chromosome is a chromosome whose arms have fused together to form a ring, often with the loss of the ends of the chromosome." [http://en.wikipedia.org/wiki/Ring_chromosome]');
INSERT INTO soterms (id, name, definition) VALUES ('000046', 'pericentric_inversion', 'A chromosomal inversion that includes the centromere." [FB:reference_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('000047', 'paracentric_inversion', 'A chromosomal inversion that does not include the centromere." [FB:reference_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('000048', 'reciprocal_chromosomal_translocation', 'A chromosomal translocation with two breaks; two chromosome segments have simply been exchanged." [FB:reference_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('000049', 'sequence_variation_affecting_transcript', 'Any change in mature, spliced and processed, RNA that results from a change in the corresponding DNA sequence." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000050', 'sequence_variant_causing_no_change_in_transcript', 'No effect on the state of the RNA." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000054', 'sequence_variation_affecting_coding_sequence', 'Any of the amino acid coding triplets of a gene are affected by the DNA mutation." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000055', 'sequence_variant_causing_initiator_codon_change_in_transcript', 'The DNA mutation changes, usually destroys, the first coding triplet of a gene. Usually prevents translation although another initiator codon may be used." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000056', 'sequence_variant_causing_amino_acid_coding_codon_change_in_transcript', 'The DNA mutation affects the amino acid coding sequence of a gene; this region includes both the initiator and terminator codons." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000057', 'sequence_variant_causing_synonymous_codon_change_in_transcript', 'The changed codon has the same translation product as the original codon." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000058', 'sequence_variant_causing_non_synonymous_codon_change_in_transcript', 'A DNA point mutation that causes a substitution of an amino acid by an other." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000059', 'sequence_variant_causing_missense_codon_change_in_transcript', 'The nucleotide change in the codon leads to a new codon coding for a new amino acid." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000060', 'sequence_variant_causing_conservative_missense_codon_change_in_transcript', 'The amino acid change following from the codon change does not change the gross properties (size, charge, hydrophobicity) of the amino acid at that position." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000061', 'sequence_variant_causing_nonconservative_missense_codon_change_in_transcript', 'The amino acid change following from the codon change changes the gross properties (size, charge, hydrophobicity) of the amino acid in that position." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000062', 'sequence_variant_causing_nonsense_codon_change_in_transcript', 'The nucleotide change in the codon triplet creates a terminator codon." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000063', 'sequence_variant_causing_terminator_codon_change_in_transcript', 'The nucleotide change in the codon triplet changes the stop codon, causing an elongated transcript sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000064', 'sequence_variation_affecting_reading_frame', 'An umbrella term for terms describing an effect of a sequence variation on the frame of translation." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000065', 'frameshift_sequence_variation', 'A mutation causing a disruption of the translational reading frame, because the number of nucleotides inserted or deleted is not a multiple of three." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000066', 'sequence_variant_causing_plus_1_frameshift_mutation', 'A mutation causing a disruption of the translational reading frame, due to the insertion of a nucleotide." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000067', 'sequence_variant_causing_minus_1_frameshift', 'A mutation causing a disruption of the translational reading frame, due to the deletion of a nucleotide." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000068', 'sequence_variant_causing_plus_2_frameshift', 'A mutation causing a disruption of the translational reading frame, due to the insertion of two nucleotides." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000069', 'sequence_variant_causing_minus_2_frameshift', 'A mutation causing a disruption of the translational reading frame, due to the deletion of two nucleotides." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000070', 'sequence_variant_affecting_transcript_processing', 'Sequence variant affects the way in which the primary transcriptional product is processed to form the mature transcript." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000071', 'sequence_variant_affecting_splicing', 'A sequence_variant_effect where the way in which the primary transcriptional product is processed to form the mature transcript, specifically by the removal (splicing) of intron sequences is changed." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000072', 'sequence_variant_affecting_splice_donor', 'A sequence_variant_effect that changes the splice donor sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000073', 'sequence_variant_affecting_splice_acceptor', 'A sequence_variant_effect that changes the splice acceptor sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000074', 'sequence_variant_causing_cryptic_splice_activation', 'A sequence variant causing a new (functional) splice site." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000075', 'sequence_variant_affecting_editing', 'Sequence variant affects the editing of the transcript." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000076', 'sequence_variant_affecting_transcription', 'Mutation affects the process of transcription, its initiation, progression or termination." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000078', 'sequence_variant_decreasing_rate_of_transcription', 'A sequence variation that decreases the rate a which transcription of the sequence occurs." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000079', 'sequence_variation_affecting_transcript_sequence', 'A sequence variation that decreases the rate a which transcription of the sequence occurs." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000080', 'sequence_variant_increasing_rate_of_transcription', 'A sequence variation that decreases the rate a which transcription of the sequence occurs." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000081', 'sequence_variant_affecting_rate_of_transcription', 'A mutation that alters the rate a which transcription of the sequence occurs." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000082', 'sequence variant_affecting_transcript_stability', 'Sequence variant affects the stability of the transcript." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000083', 'sequence_variant_increasing_transcript_stability', 'Sequence variant increases the stability (half-life) of the transcript." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000084', 'sequence_variant_decreasing_transcript_stability', 'Sequence variant decreases the stability (half-life) of the transcript." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000085', 'sequence_variation_affecting_level_of_transcript', 'A sequence variation that causes a change in the level of mature, spliced and processed RNA, resulting from a change in the corresponding DNA sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000086', 'sequence_variation_decreasing_level_of_transcript', 'A sequence variation that causes a decrease in the level of mature, spliced and processed RNA, resulting from a change in the corresponding DNA sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000087', 'sequence_variation_increasing_level_of_transcript', 'A sequence_variation that causes an increase in the level of mature, spliced and processed RNA, resulting from a change in the corresponding DNA sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000088', 'sequence_variant_affecting_translational_product', 'A sequence variant causing a change in primary translation product of a transcript." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000089', 'sequence_variant_causing_no_change_of_translational_product', 'The sequence variant at RNA level does not lead to any change in polypeptide." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000092', 'sequence_variant_causing_complex_change_of_translational_product', 'Any sequence variant effect that is known at nucleotide level but cannot be explained by using other key terms." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000093', 'sequence_variant_causing_amino_acid_substitution', 'The replacement of a single amino acid by another." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000094', 'sequence_variant_causing_conservative_amino_acid_substitution', 'The replacement of a single amino acid by another." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000095', 'sequence_variant_causing_nonconservative_amino_acid_substitution', 'The replacement of a single amino acid by another." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000096', 'sequence_variant_causing_amino_acid_insertion', 'The insertion of one or more amino acids from the polypeptide, without affecting the surrounding sequence." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000097', 'sequence_variant_causing_amino_acid_deletion', 'The deletion of one or more amino acids from the polypeptide, without affecting the surrounding sequence." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000098', 'sequence_variant_causing_polypeptide_truncation', 'The translational product is truncated at its C-terminus, usually a result of a nonsense codon change in transcript (SO:1000062)." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000099', 'sequence_variant_causing_polypeptide_elongation', 'The extension of the translational product at either (or both) the N-terminus and/or the C-terminus." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
