INSERT INTO soterms (id, name, definition) VALUES ('000102', 'sequence_variant_affecting_level_of_translational_product', '." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000103', 'sequence_variant_decreasing_level_of_translation_product', '." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000104', 'sequence_variant_increasing_level_of_translation_product', '." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
