INSERT INTO soterms (id, name, definition) VALUES ('000106', 'mutation_causing_inframe_polypeptide_N_terminal_elongation', '." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000107', 'mutation_causing_out_of_frame_polypeptide_N_terminal_elongation', '." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000108', 'mutaton_causing_inframe_polypeptide_C_terminal_elongation', '." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
