INSERT INTO soterms (id, name, definition) VALUES ('000110', 'frame_restoring_sequence_variant', 'A mutation that reverts the sequence of a previous frameshift mutation back to the initial frame." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000111', 'sequence_variant_affecting_3D_structure_of_polypeptide', 'A mutation that changes the amino acid sequence of the peptide in such a way that it changes the 3D structure of the molecule." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000112', 'sequence_variant_causing_no_3D_structural_change', 'A mutation that changes the amino acid sequence of the peptide in such a way that it changes the 3D structure of the molecule." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000115', 'sequence_variant_causing_complex_3D_structural_change', 'A mutation that changes the amino acid sequence of the peptide in such a way that it changes the 3D structure of the molecule." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000116', 'sequence_variant_causing_conformational_change', 'A mutation that changes the amino acid sequence of the peptide in such a way that it changes the 3D structure of the molecule." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000117', 'sequence_variant_affecting_polypeptide_function', 'A mutation that changes the amino acid sequence of the peptide in such a way that it changes the 3D structure of the molecule." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000118', 'sequence_variant_causing_loss_of_function_of_polypeptide', 'A mutation that changes the amino acid sequence of the peptide in such a way that it changes the 3D structure of the molecule." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000119', 'sequence_variant_causing_inactive_ligand_binding_site', 'A mutation that changes the amino acid sequence of the peptide in such a way that it changes the 3D structure of the molecule." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000120', 'sequence_variant_causing_inactive_catalytic_site', 'A mutation that changes the amino acid sequence of the peptide in such a way that it changes the 3D structure of the molecule." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000121', 'sequence_variant_causing_polypeptide_localization_change', 'A mutation that changes the amino acid sequence of the peptide in such a way that it changes the 3D structure of the molecule." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000122', 'sequence_variant_causing_polypeptide_post_translational_processing_change', 'A mutation that changes the amino acid sequence of the peptide in such a way that it changes the 3D structure of the molecule." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000123', 'polypeptide_post_translational_processing_affected', 'A mutation that changes the amino acid sequence of the peptide in such a way that it changes the 3D structure of the molecule." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000124', 'sequence_variant_causing_partial_loss_of_function_of_polypeptide', 'A mutation that changes the amino acid sequence of the peptide in such a way that it changes the 3D structure of the molecule." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000125', 'sequence_variant_causing_gain_of_function_of_polypeptide', 'A mutation that changes the amino acid sequence of the peptide in such a way that it changes the 3D structure of the molecule." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000126', 'sequence_variant_affecting_transcript_secondary_structure', 'A sequence variant that affects the secondary structure (folding) of the RNA transcript molecule." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000127', 'sequence_variant_causing_compensatory_transcript_secondary_structure_mutation', 'A sequence variant that affects the secondary structure (folding) of the RNA transcript molecule." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000132', 'sequence_variant_effect', 'The effect of a change in nucleotide sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000134', 'sequence_variant_causing_polypeptide_fusion', 'The effect of a change in nucleotide sequence." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000136', 'autosynaptic_chromosome', 'An autosynaptic chromosome is the aneuploid product of recombination between a pericentric inversion and a cytologically wild-type chromosome." [PMID:6804304]');
INSERT INTO soterms (id, name, definition) VALUES ('000138', 'homo_compound_chromosome', 'A compound chromosome whereby two copies of the same chromosomal arm attached to a common centromere. The chromosome is diploid for the arm involved." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000140', 'hetero_compound_chromosome', 'A compound chromosome whereby two arms from different chromosomes are connected through the centromere of one of them." [FB:reference_manual, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000141', 'chromosome_fission', 'A chromosome that occurred by the division of a larger chromosome." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000142', 'dexstrosynaptic_chromosome', 'An autosynaptic chromosome carrying the two right (D = dextro) telomeres." [FB:manual]');
INSERT INTO soterms (id, name, definition) VALUES ('000143', 'laevosynaptic_chromosome', 'LS is an autosynaptic chromosome carrying the two left (L = levo) telomeres." [FB:manual]');
INSERT INTO soterms (id, name, definition) VALUES ('000144', 'free_duplication', 'A chromosome structure variation whereby the duplicated sequences are carried as a free centric element." [FB:reference_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('000145', 'free_ring_duplication', 'A ring chromosome which is a copy of another chromosome." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000147', 'deficient_translocation', 'A chromosomal deletion whereby a translocation occurs in which one of the four broken ends loses a segment before re-joining." [FB:reference_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('000148', 'inversion_cum_translocation', 'A chromosomal translocation whereby the first two breaks are in the same chromosome, and the region between them is rejoined in inverted order to the other side of the first break, such that both sides of break one are present on the same chromosome. The remaining free ends are joined as a translocation with those resulting from the third break." [FB:reference_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('000149', 'bipartite_duplication', 'An interchromosomal mutation whereby the (large) region between the first two breaks listed is lost, and the two flanking segments (one of them centric) are joined as a translocation to the free ends resulting from the third break." [FB:reference_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('000150', 'cyclic_translocation', 'A chromosomal translocation whereby three breaks occurred in three different chromosomes. The centric segment resulting from the first break listed is joined to the acentric segment resulting from the second, rather than the third." [FB:reference_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('000151', 'bipartite_inversion', 'A chromosomal inversion caused by three breaks in the same chromosome; both central segments are inverted in place (i.e., they are not transposed)." [FB:reference_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('000152', 'uninverted_insertional_duplication', 'An insertional duplication where a copy of the segment between the first two breaks listed is inserted at the third break; the insertion is in cytologically the same orientation as its flanking segments." [FB:reference_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('000153', 'inverted_insertional_duplication', 'An insertional duplication where a copy of the segment between the first two breaks listed is inserted at the third break; the insertion is in cytologically inverted orientation with respect to its flanking segments." [FB:reference_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('000154', 'insertional_duplication', 'A chromosome duplication involving the insertion of a duplicated region (as opposed to a free duplication)." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000155', 'interchromosomal_transposition', 'A chromosome structure variation whereby a transposition occurred between chromosomes." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000156', 'inverted_interchromosomal_transposition', 'An interchromosomal transposition whereby a copy of the segment between the first two breaks listed is inserted at the third break; the insertion is in cytologically inverted orientation with respect to its flanking segment." [FB:reference_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('000157', 'uninverted_interchromosomal_transposition', 'An interchromosomal transition where the segment between the first two breaks listed is removed and inserted at the third break; the insertion is in cytologically the same orientation as its flanking segments." [FB:reference_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('000158', 'inverted_intrachromosomal_transposition', 'An intrachromosomal transposition whereby the segment between the first two breaks listed is removed and inserted at the third break; the insertion is in cytologically inverted orientation with respect to its flanking segments." [FB:reference_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('000159', 'uninverted_intrachromosomal_transposition', 'An intrachromosomal transposition whereby the segment between the first two breaks listed is removed and inserted at the third break; the insertion is in cytologically the same orientation as its flanking segments." [FB:reference_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('000160', 'unoriented_insertional_duplication', 'An insertional duplication where a copy of the segment between the first two breaks listed is inserted at the third break; the orientation of the insertion with respect to its flanking segments is not recorded." [FB:reference_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('000161', 'unoriented_interchromosomal_transposition', 'An interchromosomal transposition whereby a copy of the segment between the first two breaks listed is inserted at the third break; the orientation of the insertion with respect to its flanking segments is not recorded." [FB:reference_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('000162', 'unoriented_intrachromosomal_transposition', 'An intrachromosomal transposition whereby the segment between the first two breaks listed is removed and inserted at the third break; the orientation of the insertion with respect to its flanking segments is not recorded." [FB:reference_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('000170', 'uncharacterized_chromosomal_mutation', 'An intrachromosomal transposition whereby the segment between the first two breaks listed is removed and inserted at the third break; the orientation of the insertion with respect to its flanking segments is not recorded." [FB:reference_manual]');
INSERT INTO soterms (id, name, definition) VALUES ('000171', 'deficient_inversion', 'A chromosomal deletion whereby three breaks occur in the same chromosome; one central region is lost, and the other is inverted." [FB:reference_manual, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000173', 'tandem_duplication', 'A duplication consisting of 2 identical adjacent regions." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000175', 'partially_characterized_chromosomal_mutation', 'A duplication consisting of 2 identical adjacent regions." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000180', 'sequence_variant_affecting_gene_structure', 'A sequence_variant_effect that changes the gene structure." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000181', 'sequence_variant_causing_gene_fusion', 'A sequence_variant_effect that changes the gene structure by causing a fusion to another gene." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000182', 'chromosome_number_variation', 'A kind of chromosome variation where the chromosome complement is not an exact multiple of the haploid number." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000183', 'chromosome_structure_variation', 'A kind of chromosome variation where the chromosome complement is not an exact multiple of the haploid number." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000184', 'sequence_variant_causes_exon_loss', 'A sequence variant affecting splicing and causes an exon loss." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('000185', 'sequence_variant_causes_intron_gain', 'A sequence variant effect, causing an intron to be gained by the processed transcript; usually a result of a donor acceptor mutation (SO:1000072)." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('000186', 'sequence_variant_causing_cryptic_splice_donor_activation', 'A sequence variant effect, causing an intron to be gained by the processed transcript; usually a result of a donor acceptor mutation (SO:1000072)." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('001186', 'sequence_variant_causing_cryptic_splice_acceptor_activation', 'A sequence variant effect, causing an intron to be gained by the processed transcript; usually a result of a donor acceptor mutation (SO:1000072)." [EBI:www.ebi.ac.uk/mutations/recommendations/mutevent.html]');
INSERT INTO soterms (id, name, definition) VALUES ('001187', 'alternatively_spliced_transcript', 'A transcript that is alternatively spliced." [SO:xp]');
INSERT INTO soterms (id, name, definition) VALUES ('001188', 'encodes_1_polypeptide', 'A gene that is alternately spliced, but encodes only one polypeptide." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001189', 'encodes_greater_than_1_polypeptide', 'A gene that is alternately spliced, and encodes more than one polypeptide." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001190', 'encodes_different_polypeptides_different_stop', 'A gene that is alternately spliced, and encodes more than one polypeptide, that have overlapping peptide sequences, but use different stop codons." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001191', 'encodes_overlapping_peptides_different_start', 'A gene that is alternately spliced, and encodes more than one polypeptide, that have overlapping peptide sequences, but use different start codons." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001192', 'encodes_disjoint_polypeptides', 'A gene that is alternately spliced, and encodes more than one polypeptide, that do not have overlapping peptide sequences." [SO:ke]');
