INSERT INTO soterms (id, name, definition) VALUES ('001260', 'internal_Shine_Dalgarno_sequence', 'A Shine-Dalgarno sequence that stimulates recoding through interactions with the anti-Shine-Dalgarno in the RNA of small ribosomal subunits of translating ribosomes. The signal is only operative in Bacteria." [PMID:12519954, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001261', 'recoded_mRNA', 'The sequence of a mature mRNA transcript, modified before translation or during translation, usually by special cis-acting signals." [http://www.ncbi.nlm.nih.gov/entrez/query.fcgi?cmd=Retrieve&db=PubMed&list_uids=8811194&dopt=Abstract]');
