INSERT INTO soterms (id, name, definition) VALUES ('001264', 'mRNA_recoded_by_translational_bypass', 'A recoded_mRNA where translation was suspended at a particular codon and resumed at a particular non-overlapping downstream codon." [http://www.ncbi.nlm.nih.gov/entrez/query.fcgi?cmd=Retrieve&db=PubMed&list_uids=8811194&dopt=Abstract]');
INSERT INTO soterms (id, name, definition) VALUES ('001265', 'mRNA_recoded_by_codon_redefinition', 'A recoded_mRNA that was modified by an alteration of codon meaning." [SO:ma]');
