INSERT INTO soterms (id, name, definition) VALUES ('001274', 'SECIS_element', 'The incorporation of selenocysteine into a protein sequence is directed by an in-frame UGA codon (usually a stop codon) within the coding region of the mRNA. Selenoprotein mRNAs contain a conserved secondary structure in the 3 UTR that is required for the distinction of UGA stop from UGA selenocysteine. The selenocysteine insertion sequence (SECIS) is around 60 nt in length and adopts a hairpin structure which is sufficiently well-defined and conserved to act as a computational screen for selenoprotein genes." [http://www.sanger.ac.uk/cgi-bin/Rfam/getacc?RF00031]');
INSERT INTO soterms (id, name, definition) VALUES ('001275', 'retron', 'Sequence coding for a short, single-stranded, DNA sequence via a retrotransposed RNA intermediate; characteristic of some microbial genomes." [SO:ma]');
INSERT INTO soterms (id, name, definition) VALUES ('001277', 'three_prime_recoding_site', 'The recoding stimulatory signal located downstream of the recoding site." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001279', 'three_prime_stem_loop_structure', 'A recoding stimulatory region, the stem-loop secondary structural element is downstream of the redefined region." [PMID:12519954, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001280', 'five_prime_recoding_site', 'The recoding stimulatory signal located upstream of the recoding site." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001281', 'flanking_three_prime_quadruplet_recoding_signal', 'Four base pair sequence immediately downstream of the redefined region. The redefined region is a frameshift site. The quadruplet is 2 overlapping codons." [PMID:12519954, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001282', 'UAG_stop_codon_signal', 'A stop codon signal for a UAG stop codon redefinition." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001283', 'UAA_stop_codon_signal', 'A stop codon signal for a UAA stop codon redefinition." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001284', 'regulon', 'A group of genes, whether linked as a cluster or not, that respond to a common regulatory signal." [ISBN:0198506732]');
INSERT INTO soterms (id, name, definition) VALUES ('001285', 'UGA_stop_codon_signal', 'A stop codon signal for a UGA stop codon redefinition." [SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001286', 'three_prime_repeat_recoding_signal', 'A recoding stimulatory signal, downstream sequence important for recoding that contains repetitive elements." [PMID:12519954, SO:ke]');
INSERT INTO soterms (id, name, definition) VALUES ('001287', 'distant_three_prime_recoding_signal', 'A recoding signal that is found many hundreds of nucleotides 3 of a redefined stop codon." [http://www.ncbi.nlm.nih.gov\:80/entrez/query.fcgi?cmd=Retrieve&db=PubMed&list_uids=8709208&dopt=Abstract]');
INSERT INTO soterms (id, name, definition) VALUES ('001288', 'stop_codon_signal', 'A recoding stimulatory signal that is a stop codon and has effect on efficiency of recoding." [PMID:12519954, SO:ke]');
