INSERT INTO soterms (id, name, definition) VALUES ('ant_of', 'variant_of', 'A is a variant (mutation) of A = definition every instance of A is either an immediate mutation of some instance of A, or there is a chain of immediate mutation processes linking A to some instance of A." [SO:immuno_workshop]');
